css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("protonmail.ch") {
/*header*/
  #main footer .wrap, body #mainHeader, footer .wrap {
    background: linear-gradient(#555, #000)!important;
}
  /*header fix*/
    .header {
        background: #000 !important;
    }
/*Footer*/
body #main footer .wrap, body footer .wrap {
    font-size: 0.7rem;
    color: #fff !important;
    background: linear-gradient(#000, #343434)!important;
}
    /*links*/
    .secondaryActions button, ul li a {
    color: #888 !important;
    }
    .secondaryActions button, ul li a:hover {
    color: #ddd !important;
    }
/*general BG*/
#page-draft, #page-inbox, #page-m #main, #page-outbox, #page-settings, #page-spam, #page-starred, #page-trash {
    background: none repeat scroll 0% 0% #121212 !important;
}
    /*search bar(box)*/
    #mainHeader .search.dark input {
    width: 100%;
    background: none repeat scroll 0% 0% #111;
    color: #FFF;
    box-shadow: 0px 1px 0px rgba(255, 255, 255, 0.1), 0px 1px 0px rgba(0, 0, 0, 0.1) inset;
    }
    /*logged in as...*/
    #mainHeader p {
    color: #0c7 !important;
        background: #121212 !important;
        border-radius: 20px !important;
        height: 32px !important;
        margin-top: 8px !important;
        border: 1px solid #343434 !important;
        line-height: 30px !important;
        padding-left: 9px !important;
        padding-right: 9px !important;
    }
/*msg list*/
#messages tr {
    background: linear-gradient(#121212,#343434,#121212)!important;
}
    #messages .fa.fa-star-o, #messages tr, #messages tr a, #messages tr a:hover, #messages tr strong .fa {
    color: #959595 !important;
    }
    table .fa.fa-lock.external {
    color: rgba(250, 250, 250, 0.25) !important;
    }
    /*color fix*/
    tr, tr a {
    color: rgba(230, 230, 230, 0.7) !important;
        text-shadow: 0px 4px 9px #000 !important;
    }/*
    #messages tr.unread td {
    background: #F00 none repeat scroll 0% 0%;
    }*/
    .pure-table td, .pure-table th {
    box-shadow: 0px -1px 0px #000 inset !important;
    }
    #messages tr.unread, #messages tr.unread a, #messages tr.unread td {
    color: #00ac70 !important;
    font-weight: 500;
        background: linear-gradient(#121212,#000,#121212) !important;
    }
    td.table_subject{
        background: rgba(10,10,10,0.2) !important;
    }
    iframe {
        background: #222222;
        color: #444 !important;
        text-shadow: 0px 0px 34px #f00 !important;
    }
/*actions bar*/
#headerListingActions.header {
    background: none repeat scroll 0% 0% #000 !important;
    right: 0px;
    clear: both;
    z-index: 1000;
}
/**/
.pure-button, body .ui-button {
    box-sizing: border-box;
    color: #999 !important;
    border: 1px solid #D8D8D8;
    background-color: transparent;
    background-image: linear-gradient(#555, #000)!important;
}
/*compose a new email*/
#page-compose {
    background: none repeat scroll 0% 0% #222 !important;
}  
  /*dropdown menu(from)*/
    .pure-form select {
    border: 1px solid #CCC;
    background-color: #000 !important;
        color: #ddd;
    }

    /*complete*/
    .pure-form input:not([type]), .pure-form input[type="color"], .pure-form input[type="date"], .pure-form input[type="datetime-local"], .pure-form input[type="datetime"], .pure-form input[type="email"], .pure-form input[type="month"], .pure-form input[type="number"], .pure-form input[type="password"], .pure-form input[type="search"], .pure-form input[type="tel"], .pure-form input[type="text"], .pure-form input[type="time"], .pure-form input[type="url"], .pure-form input[type="week"], .pure-form select, .pure-form textarea {
     color: #ddd;   
        background: #000 !important;
        border-radius: 20px !important;
        border: 1px solid rgba(120,120,120,0.2) !important;
    }
    
  /*right hand pane*/
    .panel.panel-bordered {
    border: 1px solid #CCC;
    background: rgba(120,120,120,0.2) !important;
        border-radius: 12px !important;
    }
    h1{
        color: #a2a2a2 !important;
    }
/*mail reading(open)*/
   /*info header(left side/legend)*/
    #primaryHeader .pure-table td strong, #subHeader .pure-table td strong {
    color: #0a0!important;
    }
    /*left left*/
    .email-content-subtitle span {
    display: inline-block;
    color: rgba(230, 230, 230, 0.33)!important;
    }
    /*right side*/
    .contentHeader em {
    color: rgba(230, 230, 230, 0.33)!important;
    }
/*sidebar*/
#sidebar{    
border-right: 1px solid #555;    
}
    /*ad nauseam*/
    input[type="color"]:focus, .pure-form input[type="date"]:focus, .pure-form input[type="datetime-local"]:focus, .pure-form input[type="datetime"]:focus, .pure-form input[type="email"]:focus, .pure-form input[type="month"]:focus, .pure-form input[type="number"]:focus, .pure-form input[type="password"]:focus, .pure-form input[type="search"]:focus, .pure-form input[type="tel"]:focus, .pure-form input[type="text"]:focus, .pure-form input[type="time"]:focus, .pure-form input[type="url"]:focus, .pure-form input[type="week"]:focus, .pure-form select:focus, .pure-form textarea:focus {
    box-shadow: 0px 0px 0.7rem rgba(250, 259, 254, 0.25) !important;
}
    /**/
    input:focus:not([type]), .pure-form input[type="color"]:focus, .pure-form input[type="date"]:focus, .pure-form input[type="datetime-local"]:focus, .pure-form input[type="datetime"]:focus, .pure-form input[type="email"]:focus, .pure-form input[type="month"]:focus, .pure-form input[type="number"]:focus, .pure-form input[type="password"]:focus, .pure-form input[type="search"]:focus, .pure-form input[type="tel"]:focus, .pure-form input[type="text"]:focus, .pure-form input[type="time"]:focus, .pure-form input[type="url"]:focus, .pure-form input[type="week"]:focus, .pure-form select:focus, .pure-form textarea:focus {
    outline: 0px none;
    border-color: #ccc !important;
}
    #message-metadata {
    box-shadow: 0px 1px 0px #C7C7C7;
    background: none repeat scroll 0% 0% #202020 !important;
}
#messages tr.active.official.active.starred{
    background: #f00 !important;
}
    #main #primaryHeader.starred #titleBar, body #messages tr.starred #titleBar {
    background: linear-gradient(#000 ,#333) !important;
    color: #a3a3a3 !important;
    box-shadow: 0px 1px rgba(0, 0, 0, 0.2) inset, 0px -1px rgba(0, 0, 0, 0.2) inset;
}
    #main #primaryHeader.starred #titleBar .fa, body #messages tr.starred #titleBar .fa {
    color: rgba(80, 80, 80, 0.5) !important;
}
    body table#messages tr.starred td {
    background: linear-gradient(#141414,#242424,#141414) !important;
        color: #d2d2d2 !important; /***/
}
a.action-trigger.pure-button {
    border-top-right-radius: 8px !important;
    border-top-left-radius: 8px !important;
}
    a.pure-button.disabled{
        border-top-right-radius: 8px !important;
        border-top-left-radius: 8px !important;
    }
    a.pure-button{
        border-top-left-radius: 8px !important;/*
        border-top-right-radius: 8px !important;*/
    }
/*search panel*/
    #searchPanel {
    color: #c5c5c5 !important;
    background: rgba(0,0,0,0.25) !important;
    border-radius: 2px;
    box-shadow: 0px 1px 2px 0px rgba(0, 0, 0, 0.5);
}
    #searchPanel
    #advSearchClose {
    color: #c00;
}
    #searchPanel label {
    color: #c7c7c7 !important;
    font-weight: bold !important;
    text-shadow: 0px 2px 9px #0f0 !important;
}
    #searchPanel input:focus, #searchPanel select:focus {
    color: #ddd !important;
}
    table {
    background: radial-gradient(#151515,#222222) !important;
    width: 100%;
}
    .ui-widget-content {
    border: 1px solid #AAA;
    background: #222 !important;
    color: #222 !important;
}
    .ui-widget-content .ui-state-default {
    background: none repeat scroll 0% 0% #666 !important;
    border: 1px solid rgba(0, 0, 0, 0.1);
        color: #d2d2d2 !important;
}
        .ui-widget-content .ui-state-default:hover {
            color: #222 !important;
        }
    table.ui-datepicker-calendar th {
    color: #555 !important;
}
    body {
    background: url('data:image/png;base64,') !important;
    }  
    body #navMain {
    background: linear-gradient(#121212,#000) !important;
        box-shadow: 0px 0px 12px #fff !important;
    }
    body #menuMain li a strong {
    font-weight: 700;
    color: #95ac95 !important;
    }
    .container.panel {
    box-shadow: 0px 1px 2px rgba(0, 0, 0, 0.25);
    background: rgba(120,120,120,0.2) !important;
    border-radius: 12px !important;
    }
    #primaryHeader #titleBar {
    color: #000;
    background: linear-gradient(#555,#232323,#101010) !important;
    transition: background-color 0.25s ease 0s;
    box-shadow: 0px 1px rgba(0, 0, 0, 0.2) inset, 0px -1px rgba(0, 0, 0, 0.2) inset;
    }
    html a{
        color: #959595 !important;
    }
    .alert.alert-success {
    background: rgba(120,30,30,0.2) !important;
    border: 1px solid rgba(120,120,120,0.5) !important;
    color: #535353 !important;
        border-radius: 12px !important;
    }
}
