css:
/* Dark Portal by siliconworm */
@-moz-document domain("portal.utoronto.ca") {

span {	color: #C0C0C0!important;}
h3 {color: #C4ECEE!important;}
.helphelp, .pageTitle p, .details.label {color: #3A85A2;}

#moduleBorder, #moduleBody, .portlet {
background-color: #3C3D44;
}

#pageHeader, #schemePreviewHead, .pageTitle {
background: -webkit-gradient(linear,0% 0,0% 100%,from(#30393D),to(#425161));
}

.filterBarHorizontal {
background: -webkit-gradient(linear,0% 0,0% 100%,from(#30393D),to(#425161));
}

.filterBarHorizontal .sorter label {
color: #B3B3B3;
}

.localViewToggle {
background: #30393D;
}

#pageHeader, #schemePreviewHead, div.pageTitle, div.pageTitle h1, div.pageTitleError h1, div#lightboxContent h2 {
background: -webkit-gradient(linear,0% 0,0% 100%,from(#30393D),to(#425161));
}

.inventory {
background: #272D30;
}
.inventory>thead tr th, .splashTable thead tr th {
background: -webkit-gradient(linear,0% 0,0% 100%,from(#2C3847),to(#152638));
}
.inventory>thead tr th.sorted, th.sorted, .splashTable thead tr th.sorted {
background: -webkit-gradient(linear,0% 0,0% 100%,from(#6F92AD),to(#ACC5E0));
}
table.inventory tbody tr.gray td, table.inventory tbody tr.gray th {
background-color: #3B4C55;
transition: background-color .2s;
}
#pageHeader, #schemePreviewHead, div.pageTitle, div.pageTitle h1, div.pageTitleError h1, div#lightboxContent h2 {
background: -webkit-gradient(linear,0% 0,0% 100%,from(#343B46),to(#232A2C));
}
div.localViewToggle+div.pageTitle {
background: -webkit-gradient(linear,0% 0,0% 100%,from(#343B46),to(#232A2C));
}
#pageHeader, #schemePreviewHead, div.pageTitle, div.pageTitle h1, div.pageTitleError h1, div#lightboxContent h2 {
background: transparent;
}
table.inventory tbody tr.gray td, table.inventory tbody tr.gray th {
background-color: #5F686B;
transition: background-color .2s;
}
.genericButton, .genericButtonImg, input[type="submit"].genericButton, .browse, .browseIcon, .rumble h3+ul>li>a, .rumble h3+ul>li>a, .rumble_top h3+ul>li>a, .rumble_top h3+ul>li>a, .paging a, .itemHeaderControl>a, .actionMenuButton, .button-3, .button-3-img, .button-4, .button-4-img, #toolsSearchBox+.button-4, .treeContainer .tree li a.button-4-img, .rumble a, .rumble_top a, .rumble .abutton, .rumble_top .abutton {
background: linear-gradient(#1F262C,#2E4353);
}
.rumble_top .nav .sub ul li a, .rumble .nav .sub ul li a {
background-color: #3F5769;
}
.nav .sub ul li a:hover {
background-color: #32424E;
}
.genericButton:hover, .genericButton:focus, .genericButtonImg:hover, .genericButtonImg:focus, .browse:hover, .browse:focus, .browseIcon:hover, .browseIcon:focus, input[type="submit"].genericButton:hover, input[type="submit"].genericButton:focus, .rumble h3+ul>li>a:hover, .rumble h3+ul>li>a:focus, .rumble_top h3+ul>li>a:hover, .rumble_top h3+ul>li>a:focus, .paging a:hover, .paging a:focus, .itemHeaderControl a:hover, .itemHeaderControl a:focus, .actionMenuButton:hover, .actionMenuButton:focus, .button-3:hover, .button-3:focus, .button-4:hover, .button-4:focus, .button-3-img:hover, .button-3-img:focus, .button-4-img:hover, .button-4-img:focus, .navPaletteContent ul li:first-child .paletteSearch .button-4:hover, .navPaletteContent ul li:first-child .paletteSearch .button-4:focus, h3#cs_search+ul div.none:first-child .button-4:hover, h3#cs_search+ul div.none:first-child .button-4:focus, #toolsSearchBox+.button-4:hover, #toolsSearchBox+.button-4:focus, .treeContainer .tree li a.button-4-img:hover, .treeContainer .tree li a.button-4-img:focus, .rumble a:hover, .rumble_top a:hover, .rumble a:focus, .rumble_top a:focus {
background: -webkit-gradient(linear,center top,right bottom,color-stop(0,#4191BE),color-stop(100%,#249FE4));
}
.nav .sub ul, .cmdiv ul, .menumini ul, .quickAddPal, .keyboardAccess, .nav .sub ul li a, .cmdiv a, .menumini a, .paneTabs .nav .sub a, span.currentTags, .liveArea, .liveArea-slim, .liveAreaTab, a.liveAreaTab, a.liveAreaTab:hover, a.liveAreaTab:focus, .previewArea, .jumptopage, .panelTabs li.active a.edit, .panelTabs li.active a.edit:hover, .panelTabs li.active a.edit:focus, .bcContent .flyout, ul.tree li a + img + a:focus, ul.tree li img + img + a:focus, div.treeContainer ul.tree li a.itemActive, #learningUnitToc ul.tree li a.itemActive, ul.tree li a.itemActive, .nav .sub ul, .cmdiv ul, .menumini ul, .quickAddPal, .keyboardAccess, .cmdiv, span.currentTags, .rumble ul li.jumptopage, .rumble ul li.inventory_page.jumptopage, .rumble_top ul li.jumptopage, .rumble_top ul li.inventory_page.jumptopage, .inventory_paging input, .inventory_paging .currentPage, .jumptopage, .jumptopage input, .cmdiv li.contextmenubar_top a, span.currentTags a.closeCloud, .cmdiv li.contextmenubar_top a:focus, .cmdiv li.contextmenubar_top a:hover, .currentTags a.closeCloud:hover, .currentTags a.closeCloud:focus {
background-color: rgba(52, 64, 87, 0.44);
}
.nav .sub ul li a:hover, .nav li.sub ul li a:focus, .cmdiv a:hover, .cmdiv a:focus, .menumini a:hover, .menumini a:focus, #loginLang ul li a:hover, #loginLang ul li a:focus {
background-color: #608B9C;
}
.cmdiv li.contextmenubar_top a, .cmdiv li.contextmenubar_top a:focus, .cmdiv li.contextmenubar_top a:hover, .currentTags a.closeCloud:hover, .currentTags a.closeCloud:focus {
background-color: rgba(50, 71, 88, 0.55)!important;
}
.reply-lvl-0 .message-wrapper-inner, .db-head-message .message-wrapper-inner {
background: #384952;
}
.containerOptions .searchbar, .containerOptions .liveArea, #addFolderForm {
background: #29303A;
}
.db-reply-block, .reply-lvl-0 .messageTemplate {
background: #273036;
}
.db-collapse-control a {
background: #3D4857;
}
.backLink a:hover {
background: #527285;
}
.defaultSkin .mceIframeContainer {
background-color: #323B44;
}

#courseMenuPalette {
background: #293134!important;
}

textarea {
background: #293134!important;
color: #C0C0C0!important;
}
input, button, select {
background: -webkit-gradient(linear,0% 0,0% 100%,from(#767777),to(#CCCCCC));
color: #683636;
text-shadow: #584848 0px 0px 2px;
}

.containerPortal, .portal .container {
background: #293134!important;color: #C0C0C0!important;
}
.contentPaneWide, .portalPane {
background: #293134!important;color: #C0C0C0!important;
}

#courseMenuPalette div.navPaletteContent {
background-color: #293134!important;color: #C0C0C0!important;
}

.paneTabs {
background: #293134!important;color: #C0C0C0!important;
}

#contentArea, #schemePreviewBody, .contentBox {
background: #293134!important;color: #C0C0C0!important;
}

#contentArea, #schemePreviewBody, .container, .contentPane div.bottomRound b {
background: #293134!important;color: #C0C0C0!important;
}

#contentFrame, #schemePreview, body, .topFrame, .paneTabs ul li.active, .paneTabs ul li, .wizardBody .containerWizard{
background: #292929!important;color: #C0C0C0!important;
}

.altLi {
background-color: #404244;
}

.vertical_filter .stream_full {
padding: 0;
background: #5A5A5A;
}

.vertical_filter .left_stream_wrapper {
background: #292929;
}

.vertical_filter .stream_header {
background: #555961;
}

div#dynamic_filters_alerts.stream_dynamic_filters {
background: #444;
}

div.stream_settings {
background-color: rgba(50, 50, 53, 0.91);
}

.accordion_content {
background-color: rgba(75, 76, 80, 1);
}
.flyout-menu .accordion_toggle:focus span, .flyout-menu .accordion_toggle:hover span {
background-color: #3F5464;
transition: background-color .2s;
}
.flyout-menu .accordion_toggle span {
-moz-user-select: none;
background-color: #63565A;
}
.bottom-buttons {
background-color: rgb(142, 143, 135);
}

#left_stream_alerts .stream_item:hover {
background-color: #343A3B;
transition: background-color .2s;
}
.gradeTableNew .row:hover {
background-color: #3B4442;
}
.grade-value:hover, .grade-value:focus {
background: inherit;
background: #525A5F;
box-shadow: 2px 0 9px rgba(231, 229, 224, 0.37);
transition: background-color .2s;
border-color: #bbb;
border-color: rgba(0, 0, 0, .2);
}
.active_stream_item .grade-value:hover, .active_stream_item .grade-value:focus {
background: inherit;
background: #525A5F;
box-shadow: 2px 0 9px rgba(231, 229, 224, 0.37);
transition: background-color .2s;
border-color: #bbb;
border-color: rgba(0, 0, 0, .2);
}

.gradeTableNew .grades_header {
background: #2B2B2B;
}

.stream_page_left {
background: rgba(63, 64, 68, 1);
}
.grade-value:after {
background-color: rgba(231, 229, 224, 0);
box-shadow: -3px 0 2px rgba(231, 229, 224, 0);
}
.active_stream_item .grade-value:after {
background-color: rgba(231, 229, 224, 0);
box-shadow: -3px 0 2px rgba(231, 229, 224, 0);
}

/*new updates*/
.stream_new_entry, #left_stream_alerts .stream_item.stream_new_entry:hover {
background-color: #386080;
}

.reorderableModule:hover {
border-color: #555;
background: #3C505F;
}

div.lb-wrapper {
background-color: rgba(255, 255, 255, 0.05);
}
div.lb-wrapper div.lb-header {
background: rgba(54, 62, 71, 0.26);
}
div.lb-wrapper div.lb-content {
background-color: rgba(67, 68, 85, 0.25);
}

.pageTitle {
background: -webkit-gradient(linear,0% 0,0% 100%,from(#30393D),to(#868D92));
}

.breadcrumbs {
background: -webkit-gradient(linear,0% 0,0% 100%,from(#30393D),to(#8A8F96));
}


element.style {
	background: rgba(244, 244, 244, 0) !important;
}

/* defaults for all links */
a:link, .link, a font[color], .mn-hd-txt, 
    a.fl:link, .fl a, .flt, a.flt, .gl a:link, a.mblink, .mblink b, ._Hg ._qA, ._Hg ._Pn, ._Hg ._Uq, #gt-feedback, #gb a  { 
        color: #7C9EDC !important; 
        transition: color .4s, text-shadow .4s !important;
    }
                    
a:visited { 
    color: #F92672 !important; 
}

/* hovers */
.menuitem:hover, a:hover, a span:hover, .actbar-btn:hover, #gt-feedback:hover, #gb a:hover {
color: #E5E5E5 !important; text-shadow: #ffffff 0px 0px 2px !important;
transition: color .4s, text-shadow .4s !important;
}

/* big link visited */
#res .g a:visited { opacity: 0.5 !important; }

/* URL text */
.a, cite, .cite, .cite:link, cite b, #resultStats { color: #999 !important; }

/* dashes between Cached and Similar links */
.gl { color: #999 !important; }

/* disable dashed outline on focused elements */
:focus { outline: none; }

/* image thumbnails */
#res .g img, #ImgContent td img {
  border: none !important;
box-shadow: 4px 4px 10px rgba(0,0,0,0.8) !important;
}

/* Main text color */
div, td, font[color="#000000"], .qbtbha .qbtbtxt.qbclr, .gt-rw-span, .gt-syn-span, .gt-card-ttl-txt, #contribute-target, #source { color: #999999 !important; }

/* fix for google suggest */
.aAutoComplete .cAutoComplete, .google-ac-a .google-ac-c, .gac_a > .gac_c { color: #CCCCCC !important; }
.aAutoComplete .dAutoComplete, .google-ac-a .google-ac-d, .gac_a > .gac_d { color: white !important; }
.google-ac-e td, .gac_e { color: #380000 !important; }

/* make sure web forms (and google suggest) don't get their colors changed */
input *, textarea *, .aAutoComplete {
    background: -moz-Field !important;
    color: #999999 !important;
}

}
/* Dark Portal by siliconworm */
