css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("heise.de") {
  #container {
    width: 85% !important;
  }
  #container_content {
    width: 100% !important;
    position: relative !important;
    top: 10px !important;
    min-width: 980px !important;
    margin: 0 auto !important;
  }
  #extra_spalte_links {
    width: 85% !important;
  }
  #mitte_news article .aufmacherbild {
    width: 40%;
    float: right;
    margin: 10px;
  }
  #mitte_news p, #artikel p {
    font-family: serif;
  }
  .indexlist_text.masterbild figure {
    width: 100px !important;
  }
  .indexlist_item h3 span {
    display: inline-flex;
  }
  #mitte_rechts .teaser_left img {
    max-width: 100px;
  }
  #mitte_rechts .teaser_right img {
    max-width: 100px;
  }
  table.content_bereich {
    margin: 0 auto !important;
  }
  div#login_suche input#q.textfield  {
    width: 15em !important;
  }
  div#navi_login noscript p {
    margin: 0 !important;
  }
  div#bannerzone, div#bannerzone_top, div.cadv, div.cadv_start, div.bcadv, div.bcadv_start, div.adbottom, .banner, .sky, .skyscraper, .heise_advert, table.druck, .leaderboard, .ISI_IGNORE table, p.us_ad {
    display: none !important;
  }
  div.meldung_wrapper p .ISI_IGNORE {
    display: inline !important;
  }
  span.messespecial {
    display: inline-block;
    padding: 0 .2em .05em .2em;
    background: rgba(50,50,150,0.2);
    text-transform: lowercase;
    font-variant: small-caps;
    color: #fefefe !important;
    float: right;
    margin-left: .7em;
    border-radius: 3px;
  }
  div#mitte_news div.anrissliste h3 {
    display: inline-block !important;
    margin-top: .5em !important;
  }
  div#mitte_news .anrissliste p {
  }
}

@-moz-document url("http://www.heise.de/"),url-prefix("http://www.heise.de/?seite="),url-prefix("http://www.heise.de/newsticker"),url-prefix("http://www.heise.de/developer"),url-prefix("http://www.heise.de/security"),url("http://www.six.heise.de/"),url-prefix("http://www.six.heise.de/?seite="),url-prefix("http://www.six.heise.de/newsticker"),url-prefix("http://www.six.heise.de/developer") {
  body {
    background: #f5f5f5 !important;
  }
  div#container_content {
    width: 100% !important;
    margin: 0;
    border: 1px solid #CCC;
  }
  div.news_logo, h4#breadcrumb, div.indexlist_item i.figure, div.news_topteaser ul, div#mitte_news div.img_clip, div.news_topteaser .trennlinie_1px {
    display: none !important;
  }
  div.readspeaker {
    position: absolute;
    top: 0.25em;
    left: 6em;
  }
  div#mitte {
    width: 100% !important;
    margin-left: 0 !important;
    background: none !important;
  }
  div#mitte_links {
    width: 80% !important;
    background: white !important;
    padding-right: 5% !important;
  }
  div#mitte_news {
    overflow: hidden !important;
    position: relative;
  }
  div#mitte_rechts {
    width: 20% !important;
    min-width: 150px !important;
    background: #EEE;
    float: right !important;
  }
  div#mitte_rechts * {
    opacity: 1;
  }
  div#mitte_rechts:hover * {
	opacity: 1;
  }
  div#mitte_rechts p > a {
    display: block;
  }
  div#mitte_rechts p > a > img {
    width: 100% !important;
    height: auto !important;
    max-height: none !important;
  }
  div.teaser_liste_foren, div.teaser_liste_software, div.naviad, div.teaser_adliste, div#mitte_rechts div.clear, div.online-markt, div.col100preisvergleich, div.col100weiss, div.col50anschnitt, div.teaser_abdeckung {
    display: none !important;
  }
  div#mitte_news .indexlist *, div.teaser_linker {
    font-size: 1em !important;
    line-height: 1.35em !important;
  }
  div#mitte_news a.indexlist_text {
    clear: both;
    float: left;
    display: inline-block;
  }
  div#mitte_news a.indexlist_text p {
    margin-left: 0 !important;
  }
  div#mitte_news ul.indexlist_info {
    padding: 0 !important;
    display: inline-block;
    font-style: italic;
    font-size: .85em !important;
    margin: 0 !important;
    line-height: 1.25rem !important;
  }
  div#mitte_news ul.indexlist_info::before {
    content: "\00a0\00a0\00a0";
    display: inline;
  }
  div#mitte_news ul.indexlist_info li {
    margin: 0 !important;
    vertical-align: bottom !important;
  }
  div#mitte_news ul.indexlist_links {
    margin-left: 1.5em !important;
  }
  div#mitte_news ul.indexlist_links li a {
    line-height: 1.5 !important;
  }
  div#mitte_news h1 {
    font-size: 1.5em !important;
    line-height: 1.1 !important;
  }
  div#mitte_news h5 {
    font-size: 11.75pt;
    line-height: 1.3;
    color: #447799;
  }
  div.indexlist_item {
    vertical-align: bottom !important;
    margin-bottom: 1.5rem !important;
  }
  div.indexlist header {
    vertical-align: bottom !important;
  }
  div.indexlist h3 {
    margin-bottom: 0 !important;
    float: left;
    font-size: 1.2em !important;
    line-height: 1.25rem !important;
  }
  div.indexlist h3 .alert {
    height: 1em;
    width: 1em;
  }
  .indexlist .indexlist_item .indexlist_info .date {
    margin-left: 1em;
  }
  div#oben, div#unten {
    margin: 0;
  }
  div#oben:before {
    content: "Top News * * * ";
    color: rgba(0,0,0,0.10);
    font-size: 1.23em;
    text-transform: uppercase;
    font-weight: bold;
    display: block;
    width: 100%;
    border-bottom: none;
    padding-top: .25em;
    padding-bottom: .5em;
    -moz-transform-origin: top left;
    top: .2em;
    z-index: 0;
  }
  div#oben div, div#unten div {
    margin-top: 0 !important;
    margin-bottom: .75em !important;
    width: auto !important;
  }
  div#unten {
    padding-bottom: .25em;
    border-bottom: 4.25pt dotted rgba(0,0,0,0.10);
  }
  div#oben h1 *, div#oben h2 *, div#unten h1 *, div#unten h2 * {
    font-size: 12pt !important;
  }
  div#oben p, div#unten p {
    font-size: 1em !important;
  }
  div.news_topteaser .position_1, div.news_topteaser .position_2, div.news_topteaser .position_3 {
    width: 100% !important;
    padding: 0 !important;
    border: none !important;
  }
  div.trennlinie_6px {
    border-color: #CCC !important;
  }
  div.anrissliste p {
    margin-bottom: 1em !important;
  }
  div.meldung_wrapper p {
    font-size: 1em !important;
    line-height: 1.35em !important;
  }
  span.bild_links, span.bild_rechts {
    margin-bottom: 0.5em !important;
    position: relative;
    top: -0.5em;
  }
  div#mitte_news > a:visited {
    color: #BBB !important;
  }
  div#sitemap {
    display: none !important;
  }
  p.news_option {
    position: relative !important;
    top: 6em !important;
  }
  p.news_option a {
    font-size: 0.9em !important;
  }
  div#linie_rechts {
    background: none !important;
  }
  #social_bookmarks {
    margin-top: 4em !important;
  }
  div.related_items {
    margin-top: 5em !important;
  }
  div.related_items h4 {
    line-height: 2em !important;
    color: #666666 !important;
  }
  div.related_items ul {
    margin-left: 0 !important;
    padding-left: 0 !important;
  }
  div.related_items {
    opacity: 0.6;
  }
  div.related_items:hover {
    opacity: 1;
  }
  ul.forum_navi li {
    margin-left: 0.5em !important;
  }
  ul.forum_navi li a {
    text-decoration: none !important;
  }
  ul.forum_navi li a:hover {
    text-decoration: underline !important;
  }
  p.translation {
    margin-top: 6em !important;
  }
  .news_topteaser {
    float: left;
    background: #fefefe;
    box-shadow: 0px 2px 9px rgba(0,0,0,0.15);
    padding: 0 18px;
    margin-bottom: 20px;
  }
  .trennlinie_6px {
    display: none;
  }
  div#mitte_news {
    margin: 1em 0 !important;
  }
  div.anrissliste {
    padding: 0.2em 0;
  }
  div.channel_balken {
    display: none;
  }
  #container_content {
    border: none !important;
    box-shadow: 0px 2px 7px rgba(0,0,0,0.1);
    background: #fdfdfd;
  }
  #mitte_links {
    padding-left: 25px !important;
    padding-right: 25px !important;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
  }
}

@-moz-document url-prefix("http://www.heise.de/tp/"),url-prefix("http://www.six.heise.de/tp/") {
  table.table {
    background: #E6E6E6;
  }
  td.f-content {
    width: 70% !important;
  }
  .banner {
    display: table-cell !important;
    background: #C6D6DE;
  }
}

@-moz-document url-prefix("http://www.heise.de/events/"),url-prefix("http://www.six.heise.de/events/") {
  div#container {
    top: 0 !important;
  }
  div#mitte {
    position: relative !important;
    top: 20px;
    width: 47em !important;
  }
}

@-moz-document url-prefix("https://www.heise.de/abo/"),url-prefix("http://www.six.heise.de/abo/") {
  div#container {
    top: 0px !important;
    width: 87em !important;
  }
}
