css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("8ch.net") {

  /* Solarized Light for Infinity (and possibly Vichan)
   By notlily, v0.1
   Use Yotsuba B as base style for optimum results.
  */

  body { 
   background: #fdf6e3;
   color: #657b83;
   font-family: Arimo,Helvetica,"Nimbus Sans","Liberation Sans",Arial;
  }

  a:not(:hover) {
   color: #268bd2;
   text-decoration: none;
  }
  a:hover {
    color: #2aa198;
    text-decoration: none;
  }
  
  div.post.reply, div.pages {
   background: #eee8d5;
   border: none;
  }
  
  div.post.reply.highlighted {
    background: #eee8d5;
    border-bottom: 1px solid;
    border-right: 1px solid;
  }
  
  div.boardlist:not(.bottom) {
    background: #eee8d5 !important;
  }
  
  p.intro span.name {
    color:  #586e75;
  }
 
  p.intro span.subject {
    color: #cb4b16;
  }
  
  span.heading, p.intro span.capcode, div.blotter{
    color: #dc322f;
  }
  
  th {
    background: #93a1a1 !important;
    color: #073642;
  }
  
}
