css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.ohmydollz.com/forum") {

html {
  background : url("http://www.zupimages.net/up/15/13/6sh6.png") top fixed no-repeat, url("http://www.zupimages.net/up/15/13/1m6k.png") bottom fixed no-repeat, linear-gradient(to bottom, #A4C73C, #579603, #357101) fixed no-repeat !important ;
  background-size : 100% auto, 100% auto, cover !important ;
}  

body {
  background-color : transparent !important ;
  background-image : url("http://www.zupimages.net/up/15/14/k3f8.gif") !important ;
  background-attachment : fixed ;
  background-repeat : no-repeat ;
  -webkit-animation : oiseaupasse 15s infinite ;
  animation : oiseaupasse 15s linear infinite ;
} 
  
#contenu {background : none !important ;}  
  
body > center {
  background-image : url("http://www.zupimages.net/up/15/14/k58j.png"), url("http://www.zupimages.net/up/15/14/57o9.png"), url("http://www.zupimages.net/up/15/14/gdfa.png") !important ;
  background-attachment : fixed ;
  background-repeat : no-repeat ;
  -webkit-animation : pluiepetales 25s infinite ;
  animation : pluiepetales 25s linear infinite ;
}    
  
@-webkit-keyframes pluiepetales {
  0% {background-position : 20px 0, 85px -120px, calc(100% - 60px) -100px ;}
  20% {background-position : 45px 33%, 55px 15%, calc(100% - 90px) 5% ;}
  40% {background-position : 25px 70%, 115px 65%, calc(100% - 65px) 25% ;}
  60% {background-position : 0 calc(100% + 500px), 80px 85%, calc(100% - 25px) 45% ;}
  80% {background-position : 0 calc(100% + 500px), 80px calc(100% + 120px), calc(100% - 55px) 85% ;}
  100% {background-position : 0 calc(100% + 500px), 80px calc(100% + 120px), calc(100% - 75px) 105% ;}
}  
@keyframes pluiepetales {
  0% {background-position : 20px 0, 85px -120px, calc(100% - 60px) -100px ;}
  20% {background-position : 45px 33%, 55px 15%, calc(100% - 90px) 5% ;}
  40% {background-position : 25px 70%, 115px 65%, calc(100% - 65px) 25% ;}
  60% {background-position : 0 calc(100% + 500px), 80px 85%, calc(100% - 25px) 45% ;}
  80% {background-position : 0 calc(100% + 500px), 80px calc(100% + 120px), calc(100% - 55px) 85% ;}
  100% {background-position : 0 calc(100% + 500px), 80px calc(100% + 120px), calc(100% - 75px) 105% ;}
}  

  
@-webkit-keyframes oiseaupasse {
  0% {background-position : 140% 200px ;}
  100% {background-position : -150% 700px ;}
}
@keyframes oiseaupasse {
  0% {background-position : 140% 200px ;}
  100% {background-position : -150% 700px ;}
}  
  
.pun a:link, .pun a:visited {color : #EBE0F4 !important ;}  
  
/*Bannière*/
  
  #head_forum {
    background : url("http://www.zupimages.net/up/15/13/1bgj.png") no-repeat !important ;
  }

  #head_links {bottom : 20px !important ;}  
  
  #head_links a:hover {color : #562986 !important ;}

/*Sous-bannière*/
  #brdmenu {
    background : url("http://www.zupimages.net/up/15/13/qg0t.png") no-repeat !important ;
  }

  #brdwelcome {
    background : url("http://www.zupimages.net/up/15/13/e2o8.png") no-repeat !important ;
    color : white !important ;
  }
  
  #brdmenu a {
    color : white !important ;
    text-shadow : 4px 4px 6px black ;
  }
  
  #brdmenu a:hover {
    border-bottom : 1px dotted #EBE0F4 !important ;
  }
  
/*Footer*/
  
  #footer_box {
    background : url("http://www.zupimages.net/up/15/13/n27z.png") !important ;
  }

  #footer_box p, #footer_box a {
    color : white !important ;
    margin-top : 50px !important ;
  }
  
/*Boards principaux*/
  
.pun H2 {
    background : url("http://www.zupimages.net/up/15/13/odgu.png") !important ;
  border-radius : 10px 10px 0 0 ;
  text-shadow : 2px 2px 4px black ;
}

.blocktable, #announce, #punredirect > .block {
  border : 4px solid #562986 !important ;
  border-top : none !important ;
  border-radius : 10px ;
}  

  .blocktable > .box {
    border : none !important ;
  }
  
  #announce > .box {
    background : #C9A6E1 !important ;
    border : none !important ;
  }  
  
.pun .tcl, .pun .tc2, .pun .tc3, .pun .tcr {background : none !important ;}  
.pun thead {
    background : url("http://www.zupimages.net/up/15/13/ycr6.png") no-repeat !important ;
    background-size : cover !important ;
}

.pun th {color : #562986 !important ;}  
  
.pun TD.tcl, .pun TD.tc2, .pun TD.tc3, .pun TD.tcr {background-color : #74B01E !important ;}
 
.pun .tcl, .pun .tc2, .pun .tc3, .pun .tcr {border-color : #5B9613 -moz-use-text-color #5B9613 #5B9613} 
  
   DIV.icon {
     background : url("http://www.zupimages.net/up/15/13/k3fm.png") !important ;
     -webkit-transition : opacity 0.5s !important ;
     transition : opacity 0.5s !important ;
   }
  
  DIV.inew {
    background : url("http://www.zupimages.net/up/15/13/x46k.png") !important ;
  }
  
.blocktable tr:hover DIV.inew, .blocktable tr:hover DIV.icon {
  opacity : 0 !important ;
}
  
  
  
/*Topics*/
  .blockpost > h2 a[style="float:left;margin-left:5px;"] {
    float : none !important ;
    margin-left : calc(50% - 125px) !important ;
  }
  
.blockpost {
  border : 3px solid #562986 !important ;
  border-top : none !important ;
  border-radius : 10px ;
}  
  
.pun BLOCKQUOTE, .rowodd, .roweven, .postright, .postfootright, div.postfootleft {background : none !important ;}
  
div.box {background : #E4D8EE !important ; border : none !important ;}  
 
.postleft {background : #C9A6E1 !important ;} 
.postright, .postfootright {border-color : #C9A6E1 !important ;}  
}
