css:
@-moz-document domain("vk.com") {
.wide_column {
    width: 82% !important;
}
#stl_left {
    width: 5px !important;
}
#page_layout {
    width: 1720px !important;
}
.profile_info .labeled {
    width: 1000px !important;
}
body {
    font-family: Inconsolata-g for Powerline, Tahoma !important;
    font-size: 14px !important;
}
h4 {
    font-size: 14px !important;
    padding: 0 0 8px !important;
}
#pageLayout,
#page_layout {
    width: 1480px !important;
    margin-left: 0 !important;
    margin-right: 0 !important;
}
#page_body,
#pageBody {
    width: 1320px !important;
    margin-left: 0 !important;
    margin-right: 0 !important;
}
.narrow_column {
    float: left;
}
#side_panel {
    float: left;
}
#bFooter {
    display: none !important;
}
#fl_r {
    float: left;
}
#side_bar {
    float: left;
}
.pedit_interests_details {
    display: none !important;
}
.wall_post_text {
    width: 100%;
}
.wall_text {
    width: 98%;
}
.wall_module .post_info {
    width: 95%;
}
.group_share .info,
.event_share .info {
    width: 100%;
}
.page_media_poll {
    max-width: 9999px;
}
.wide_wall_module .wall_text,
.wide_wall_module .wall_post_text,
.wide_wall_module .post_media {
    width: 100%;
}
.wide_wall_module .reply_info {
    width: 90%;
}
.wide_wall_module .reply_text {
    width: 90%;
}
.wall_module .post_media {
    width: 98%;
}
.wall_module .reply_text {
    word-wrap: break-word;
    overflow: visible;
    width: 95%;
    margin-left: 50px;
}
.wide_wall_module .like_wrap,
.wide_wall_module .reply_link_wrap,
.wide_wall_module .reply_link_wrap small,
.wide_wall_module .info_footer {
    font-size: 11px;
    margin-left: 50px;
}
#info_module_wrap {
    overflow: visible;
    position: relative;
    width: 100%;
    margin-bottom: 10px;
}
.info_table {
    overflow: visible;
    position: relative;
    width: 100%;
    margin-bottom: 10px;
}
#info_module_wrap .info_tabs {
    position: relative;
    width: 100%;
}
#info_module_wrap .info_tab {
    width: 100%;
}
.info_table .info_tab .labeled_text {
    width: 90%;
}
.help_step_info {
    width: 90%;
    padding: 0 10px;
}
.page_wide_no_narrow #info_module_wrap,
.page_wide_no_narrow #info_module_wrap .info_tabs.no_edit,
.page_wide_no_narrow #info_module_wrap .info_tab {
    width: 100%;
}
#info_module_wrap .info_tabs.no_edit {
    width: 100%;
}
.wall_module .reply_info {
    margin-left: 39px;
    width: 95%;
    overflow: hidden;
    -o-text-overflow: ellipsis;
    text-overflow: ellipsis;
}
.wall_module .reply_text {
    word-wrap: break-word;
    overflow: visible;
    width: 95%;
}
.wall_fixed .wall_module .post_info {
    margin-left: 6px;
    width: 98%;
}
.im_fixed_nav #im_controls_wrap {
    width: 80%;
}
#im_top_sh,
#im_bottom_sh {
    width: 98%;
}
#im_smile {
    position: relative;
    margin: 1px 0px 0px 94%;
}
.im_log_body div.wrapped,
.im_log_body .wall_module .post_media,
.im_single_log .im_log_body div.im_fwd_log_wrapped {
    width: 100%;
    overflow: visible;
}
.im_fwd_log_t {
    width: 100%;
}
#im_rows .im_wall_log_wrap .wall_post_text,
#im_rows .im_wall_log_wrap .wall_text {
    width: 100%;
}
.im_fixed_nav #im_nav_wrap {
    top: 40px;
    width: 80%;
}
#friends .main_panel {
    width: 90%;
}
#friends .user_block .info,
#friends .user_block .info_no_actions {
    width: 70%;
    padding-top: 4px;
    padding-left: 14px;
    overflow: visible;
}
.video_items_list {
    width: 100%;
}
.video_item,
.video_item_thumb {
    width: 244px !important;
    height: 138px !important;/*177*/
    border-radius: 10px !important;
}
.video_item{
    margin: 0 1px 68px 0 !important;
}
.video_item .video_item_thumb_wrap {
    background-color: transparent !important;
}
}
