@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("myzuka.org") {
  
   body, html {background: rgba(0,0,0,.05)url(/*[[URL]]*/)!important; 
  background-color: #111 !important;
	background-attachment: fixed !important;
	background-clip: border-box !important;
	background-origin: padding-box !important;
	background-position: center center !important;
	background-repeat: no-repeat !important;
	background-size: cover !important;
  font-family:Constantia!important}
  
	.header .logo {width: 210px;margin: -2px 31px 0px 0px;background: url(https://leto36g.storage.yandex.net/rdisk/c634c80bb82eb4a089c5924232f16eb80157b18c84fcd31179d5f36319142e39/inf/oyzXLtOAcCiajnfVfEX0gAATFwEIwwsNT_TkNTCEFOCAweSOCNgTs9GDc8FNatiFAptP1twlRuQOl-9XD44H2Q==?uid=0&filename=myzuka.png&disposition=inline&hash=&limit=0&content_type=image%2Fpng&tknv=v2&rtoken=b7f5693d8540cd54e5c6bdb8d4c73d03&force_default=no) no-repeat scroll 1px 0px transparent;}
	  
  .all, .main, .pagin-letters li, .tbl tr:nth-child(2n+1), .main-details .side .iwrap, .player-inline, .main-tabs li a, .pager li a  {background: transparent !important;border: 0px !important;}
	
	.side-block, .profile-bar, .profile-bar .foot {background: rgba(0, 0, 0, 0.6)!important; box-shadow: 0px 0px 9px rgba(0, 0, 0, .9 ) !important}
  
  .footer, .main .content .inner, .player-inline:nth-child(2n+1), input.form-text, input[type="text"], input[type="password"], .main-tabs li.active a, .login-block, .login-block .form-input,.table-striped > tbody > tr:nth-child(2n+1) > td, .table-striped > tbody > tr:nth-child(2n+1) > th, .sorters li a.active {background: rgba(0, 0, 0, 0.5)!important; box-shadow: 0px 0px 5px rgba(0, 0, 0, .9 ) inset!important;border: 0px !important}
	
	.main-nav {background: rgba(0, 0, 0, 0.35)!important; box-shadow: 0px 0px 5px rgba(200, 0, 0, .9 ) inset!important;}
	.main-nav li a {color: #33ccff !important; margin-left: 50px !important; background: transparent!important; }
	.main-nav li a:hover, .pagin-letters a:hover {color: #ff0000 !important;background: transparent!important;}
	.header .search-box .form-button, input.form-text, input[type="text"], input[type="password"]{color: #33ccff !important;background: rgba(0, 0, 0, 0.6)!important;}
	.header .search-box .form-button:hover, input.form-text:hover, input[type="text"]:hover, input[type="password"]:hover{color: #3399ff !important;background: rgba(0, 0, 0, 0.6)!important;box-shadow: 0px 0px 5px rgba(0, 191, 255, .9 ) inset!important;}
	.player-controls .timeline, .player-controls .volume {background: rgba(200, 0, 0, 0.6)!important;}
		
	a{color: #33ccff !important;}
	a:hover{color: #ff0000 !important;}
	body{color: #0099ff !important;}
 .header .logo .text{color: #20B2AA !important;}
	
	.pagin-letters {margin-left: 180px !important}
	
	.button-a, .button-b, .sorters li a{background: rgba(0, 0, 0, 0.6)!important; box-shadow: 0px 0px 5px rgba(0, 191, 255, .5 ) inset!important;}
	.button-a:hover, .button-b:hover, .sorters li a:hover{background: rgba(0, 0, 0, 0.6)!important; box-shadow: 0px 0px 5px rgba(200, 0, 0, .5 ) inset!important;opacity:1 !important}
	
  #ad_ph_1>embed, .aside-col>noindex>center>a, #MarketGidComposite577725, .side-col>noindex>center>a, #\37 8c593cbd2fb7d1c7a9e51ef9e0808a9, #ad_ph_1 {display: none !important}}
