css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

image[src*="chrome://speeddial/skin/icon16.png"] {
  background: url("data:image/png;base64,") !important;
  padding-left: 16px !important;
}

@-moz-document url("chrome://speeddial/content/speeddial.xul") {
  
  /* Thumbnail box - with title, icons etc. */
  
  .thumbnail {
    cursor: pointer;
  }

  .speeddialThumb {
    border-radius: 8px;
    border-style: solid;
    border-color: white;
    
    background-color: white;
    box-shadow: 0 1px 3px #c1c1c1;
    
    margin: 0px 0px 20px 20px;
    padding: 0px 0px 0px 0px;
    width: 0px;
    height: 0px;
    min-width: 0px;
    min-height: 0px;
  }

  .speeddialThumb:hover {
    box-shadow: 0 0 6px 2px #4cb1ff;
    transition: all 150ms ease-out;
  }
    
  
  /* Inside of the thumbnail box */
  .speeddial-container {
    border-radius: 8px;
    border-width: 0px;
    border-style: solid;
    border-color: transparent;
    
    margin-top: 5px;
    min-width: 0px;
    min-height: 0px;
    
    background-color: transparent;
    background-repeat: no-repeat;
    background-position: center center;
  }
    
  .thumbnail-title,
    .speeddial-top-middle,
    .speeddial-bottom-middle,
    .speeddial-top-middle,
    .speeddial-middle-left,
    .speeddial-middle-right,
    .speeddial-bottom-middle,
    .speeddial-top-left,
    .speeddial-top-right,
    .speeddial-bottom-left,
    .speeddial-bottom-right {
    display: none;
  }
}
