css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("muzabaza.com") {


*{padding:0;margin:0;border:none;outline:none;font-size:100%;text-decoration:none;}
body{ font-family:Tahoma, Helvetica, sans-serif; font-size:13px;color:#bbb; background-color:#202020;}


.search_warpen {
top:50%;
position:absolute;
width:100%;

}

.warp {
width:100%;

margin-left:auto;
margin-right:auto;
display:block;
text-align:center;
}

warp_index {
width:100%;
height:100%;

margin-left:auto;
margin-right:auto;
display:block;
text-align:center;
}

.download_tracks_warp {
margin: 5px 15px 15px 15px;
text-align:center;
}
.download_button {
height:60px;
width:120px;
background:none;
display:block;
margin-left:auto;
margin-right:auto;
line-height:60px;
color:#FFF;
font-size:32px;
padding-left:50px;
margin-top:20px;
}


.search_word {
height:28px;
	background-color: #101010;
	color: #c0c0c0;
  border:1px solid #505050;
  width:100%;
  display:block;
  padding-left:10px;
	padding-right: 16px;
}

.search_area {
text-align:center;
margin-left:auto;
margin-right:auto;
display:block;
}

.search_button {
background-color:#202020;
background-image:none;
border:1px solid #606060;
height:30px;
width:70px;
color:#FFFFFF;
font-size:14px;
cursor:pointer;
margin-left: 10px;
float:right;
}

.track_icon {
width:0px;
height:20px;
display:block;
background-color:#202020;
}

.singer {
width:100%;
padding-bottom:5px;
}

.time {
width:35px;
color:#c0c0c0;
font-weight:bold;
}

.composition {
width:100%;
	background: none;
background-color:#202020;
border:1px solid #404040;
}

li{list-style:none;}
a{color:#ccc;}
a:hover{text-decoration:none; border:none;}
#wrap{margin:0 auto; border:1px solid #202020; padding:30px; }

.content {background: #999; /* for non-css3 browsers */
text-align:left;

margin-top:5px;
width:100%;

background-color: #202020;


border-radius: 3px 3px 3px 3px;
  border:2px solid #202020;
  float:left;
  display:block;
 
}

.inner_tracks_warp {
margin: 0px 4px 4px 4px;
}

.page_navigation {
margin-top:10px;
}

.pages {
			width:100%;
			min-height:30px;
			background-color:#303030;
			border-bottom: 1px solid #505050;
			border-top: 1px solid #505050;
			
		}
		
		.next_prev {
			text-align:right;
			color:#a0a0a0;
			padding-bottom:10px;
			
		}
		
		 .next_prev span {
			 float:left;
		 }
		
		.next_prev a {
			text-decoration:none;
			color:#aaa;
			margin-left:20px;
		}
		
		.next_prev a:hover {
			text-decoration:none;
			color:#fff;
		}
		.pages a {
		color:#aaa;
		font-size:14px;
		line-height:30px;
		text-decoration:none;
		display:block;
		height:30px;
		min-width:20px;
		padding-left:10px;
		float:left;
		}
		
		.pages a:hover {
		color:#fff;
		}
		
		.pages .whis_page {
		font-weight:bolder;
		color:#aaa;
		}
		
		.search_index {
		top:50%;
		}

		.banner_bottom {
		width:100%;
        position:fixed;
        bottom:0px;
        }

}
