  #page-head .box , .search-form-box form.search .input-field, .search-form-box form.search .submit, input, select, textarea {background: transparent!important; color: /*[[4]]*/ !important}
  
.audiotrack-player-progress__buffered, .musicset-track-list_new .musicset-progress__buffered, .ui-widget-content, .ui-state-default, .ui-widget-content{background-color: /*[[progress]]*/ !important}

/*[[ico]]*/
	
