@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("wiki.manjaro.org") {
  
    html, body, div#mw-head {background-color: 
#1e1e1f;background-image:url('data:image/svg+xml;base64,');background-attachment: 
fixed;}
  div#content {color: #959595!important;}
  div#mw-head {height: 80px !important;}
  
  
  /* Link */
  a {background: transparent;text-decoration: none;transition: all 0.3s;padding-left: 3px; padding-right: 3px;}
  a:link, a:visited {color: #5A885F !important;} 
  a:hover {color: #dadada !important;cursor: pointer;background: #4F8567;text-decoration: none;}
  
  
  /* Content */
  body.vector-animateLayout div#content, 
  body.vector-animateLayout div#footer, 
  body.vector-animateLayout  #left-navigation {background: rgba(39, 39,39, 0.5) !important;color: #959595!important;}
  div#p-views.vectorTabs, div#p-namespaces.vectorTabs {padding-left: 0px !important;padding-right: 0px !important;}
  div.vectorTabs ul li {background: rgba(39, 39,39, 0.5) !important;color: #959595!important;background-image: none!important;}
  div.vectorTabs li.selected {background:#000 !important;color: #fff !important;border:1px solid #4F8567!important;}
  div.vectorTabs, div.vectorTabs span {background-image: none!important;}
  .center, #toc, .toc, .mw-warning {background-color: #2d2d2d !important;border: none;}
  body, #footer, div#content {border: none !important;}
  
  
    /* Title */
  h1 {
    background: none repeat scroll 0 0 rgba(77, 147, 95, 0.3);
    color: #e0e0e0;
    font-size: /*188%*/ 130%;
    text-align: center;
    font-weight: bold;
    border-radius: 1px;border-bottom: 0px solid rgba(183, 183, 183, 0.3);
    margin-top:10px;
    margin-bottom: 10px;
    width: 50%;}
  h2, h3, h4, h5, h6 {color: #5A885F !important;border-bottom: 0px solid #5A885F;}
  .center, html.client-js body.mediawiki.ltr.sitedir-ltr.ns-0.ns-subject.page-Main_Page.skin-vector.action-view.vector-animateLayout div#content.mw-body div#bodyContent 
div#mw-content-text.mw-content-ltr center,contentSub {display: none!important;}
  
  
  /* News */
  div#bodyContent div[style*="width: 70%; padding: 20px; border: medium solid #4EA64E; border-radius: 30px 0 30px 0; background: honeydew;"]
  {background: #333333!important;border:1px solid green!important;margin: auto !important;border-radius:0px!important;}
  
  
  /* code */
  code, pre, .mw-code {background: #2d2d2d;color:#627562;border: none;border-radius: 2px;border:1px solid rgba(89, 89, 89, 0.4);}
  
  
  /* catlink */
  .catlinks {background-color:#111;border: none!important;}
  
  
  /* Search Input */
  div#simpleSearch input#searchInput {background: none !important;border: 1px solid #627562;}
  div#simpleSearch input {color: #627562 ;background: none ;}
  div#simpleSearch {background: transparent!important;border: 0px solid #627562;color: black;}
  div#simpleSearch button#searchButton {padding: 0 !important;}
  select, sitedir-ltr textarea, .sitedir-ltr input , #mw-whatlinkshere-target, #namespace, input[type="submit"], input[type="button"], input[type="reset"], input[type="file"] {background-color: #272727 
!important;background-image: none !important;border: 1px solid green;color: #627562 /*green*/;}
  #pagehistory li.selected {background-color: #373737!important;background-image: none !important;border: 1px solid #777777;color: #627562 ;}
  #pagehistory li {background-color: #373737!important;background-image: none !important;border: 1px solid #373737;color: #627562 ;}
    
 
  /* Note */
  div#bodyContent div[style*="padding: 5px; margin: 0.50em 0; background-color: #ddddff; border: thin solid #bbbbdd; overflow: hidden;"]  
  {background: #151515 !important;color: #317EC8; border: 0px solid rgba(86, 143, 160, 0.3)!important;}
  
  
  /* Tip */
  div#bodyContent div[style*="padding: 5px; margin: 0.50em 0; background-color: #ddffdd; border: thin solid #bbddbb; overflow: hidden;"]  {background: #151515 
!important;color:#88DB9C!important;border:0px solid green!important;}
  
  
 /* Warm */   
  div#bodyContent div[style*="padding: 5px; margin: 0.50em 0; background-color: #FFDDDD; border: thin solid #DDBBBB; overflow: hidden;"] 
 {background: #151515 !important;
  color:/*rgba(179, 63, 63, 0.6) !important;*/#D44A38;
  border: 0px solid rgba(179, 63, 63, 0.3)!important;}
  
  
  /* left panel */
  div#mw-panel.collapsible-nav {background: #272727!important;border:0px solid orange !important;}
  
  
  /* fieldset*/
  fieldset {border: 1px solid #898989;}
  ul {list-style-image: 
url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAUAAAANAQMAAABb8jbLAAAABlBMVEUAAAB7kmGEAYcoAAAAAXRSTlMAQObYZgAAAAFiS0dEAIgFHUgAAAAJcEhZcwAACxMAAAsTAQCanBgAAAAHdElNRQffAwMSDhd4Ww4zAAAAE0lEQVQI12NgQAEFDD/AsICBAQAakgPJO2sGpQAAAABJRU5ErkJggg==");}

  
  /* disclaimer */
  div.mw-warning-with-logexcerpt {border: 1px solid #959595;}
  
}
