*/@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document 
url-prefix("https://www.facebook.com/"), url("https://www.facebook.com/"), url-prefix("https://www.facebook.com/index.php?stype=lo"), url-prefix("https://www.facebook.com/index.php") 
{
body.fbIndex 
{
  background: url('http://www.csgo4coins.com/v2/styles/fragzone/xenforo/sources/bgcod.jpg')right center fixed !important;
  background-repeat: no-repeat !important;
}
.loggedout_menubar_container 
{
  background: url('http://www.csgo4coins.com/v2/styles/fragzone/xenforo/sources/bgcod.jpg') !important; 
}
.fbIndex #globalContainer #dropmenu_container,.fbIndex #globalContainer #content,.fbIndex #globalContainer #pageFooter
{
display: none!important
}
.fbIndex .loggedout_menubar_container 
{
  position: fixed !important;
  top: 50% !important;
  left: 50% !important;
  margin-top: -150px !important;
  margin-left: -210px !important;
}
.fbIndex .loggedout_menubar_container .lfloat,.fbIndex .loggedout_menubar_container .rfloat 
{ 
  float: none !important 
}
}
