css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("www.tumblr.com") {
body.logged_in{
	background: url(http://static.tumblr.com/ek9ly4s/i6Fm12595/pagebg.png) fixed !important;
}
#dashboard_index #container {
	background: none !important;
}
.as_links a {
	text-shadow: none !important;
}
.source_url .source_url_gradient,
div.l-content,
#dashboard_index #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
	background: none !important;
}
#posts .post {
	background: url(https://github.com/TumblrLove/images/raw/master/back.jpg) !important;
	color: #ddd;
	border-bottom: 1px solid #666 !important;
	border-top: 1px solid #888 !important;
	border-left: 1px solid #666 !important;
	border-right: 1px solid #666 !important;
}
#posts .post img {
	border-radius: 10px;
}
#posts .post .footer_links .source_url a,
#posts .post * {
	color: #ddd !important;
}

#posts .post .permalink,
.arrow ,
#right_column .radar .content.is_media,
#right_column .radar .content .mask,
#right_column .controls_section .recessed {
	background: none !important;
}
.post_question_nipple,
.autopagerize_page_separator,
div.l-header .selection_nipple,
div.l-header .selected .selection_nipple
.autopagerize_page_separator_blocks,
.autopagerize_page_info {
	display: none !important;
}

#posts .post .post_question {
background: rgba(0, 0, 0, 0.2) !important;
}
.post_question_nipple +div+div {
	border-top: 1px solid #777 !important;
}
#posts ol.notes li.like ,
#posts ol.notes li.reblog {
background-color: rgba(0, 0, 0, 0.2)  !important;
}

#posts .notification {
background-color: rgba(0, 0, 0, 0.4)  !important;
border-bottom: 1px solid #333 !important;
border-right: 1px solid #333 !important;
border-left: 1px solid #333 !important;
}

#posts .notification.alt .nipple {
border-right: 6px solid #333 !important;
}
#posts .notification .nipple {
width: 0;
height: 0;
border-top: 6px solid transparent;
border-bottom: 6px solid transparent;
border-right: 6px solid #333 !important;
position: absolute;
left: -5px;
top: 10px;
}

#posts .notification a:not(.avatar_frame) {
	border-bottom: none !important;
}
#posts .post .conversation_lines .chat_line:first-child{
border-top: 1px solid #333 !important;
}
#posts .post .conversation_lines .chat_line,
#posts .post .conversation_lines .chat_line:nth-child(even) {
border-right: 1px solid #333 !important;
border-bottom: 1px solid #333 !important;
border-left: 1px solid #333 !important;
}
#posts .post .conversation_lines .chat_line:nth-child(even) {
background-color: rgba(0, 0, 0, 0.2) !important;
}

#posts .post .conversation_lines .chat_line:nth-child(odd) {
background-color: rgba(0, 0, 0, 0.4) !important;
box-shadow: 0 0 10px #000;
}
.answer_container {
background: rgba(0, 0, 0, 0.16) !important;
border-left: 1px solid #51535A !important;
border-right: 1px solid #51535A !important;
border-bottom: 1px solid #777 !important;
}
#main_tag #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
	background: none !important;
}
#dashboard_controls div[style="float:right;"],
#pagination a {
background: rgba(0, 0, 0, 0.16) !important;
border: 1px solid #444 !important;
border-radius: 5px;
box-shadow: inset 0 0 10px #000;
padding: 5px 20px !important;
margin-right: 10px;
}
#dashboard_controls a[href="/dashboard"] {
background: rgba(0, 0, 0, 0.16) !important;
border: 1px solid #444 !important;
box-shadow: inset 0 0 10px #000;
border-radius: 5px;
padding: 10px;
}
#dashboard_controls a[href="/dashboard"]:hover,
#dashboard_controls div[style="float:right;"]:hover,
#pagination a:hover {
box-shadow: 0 0 10px #000;
border: 1px solid #222 !important;

}

div.l-header {
background: rgba(0, 0, 0, 0.3) !important;
margin-bottom: 0!important;
border-radius:  0 0 10px 10px;
border: 1px solid #222 !important;
}
#footer {
background: rgba(0, 0, 0, 0.3) !important;
padding-bottom: 15px;
margin-bottom: 0!important;
border-radius: 10px 10px 0 0;
border: 1px solid #666 !important;
}
#right_column .controls_section li *,
div.l-header .tab > a {
color: #fff !important;
text-decoration: none !important;
text-shadow: -1px 1px 3px black,
 1px	1px 3px black !important;
 font-weight: normal!important;

}
#right_column .radar .attribution * ,
#dashboard_controls a[href="/dashboard"],
#dashboard_controls div[style="float:right;"] span,
#pagination a,
#footer a ,
#footer strong {
color: #fff !important;
text-decoration: none !important;
text-shadow: -1px 1px 3px black,
 0px	1px 3px black,
 1px	1px 3px black !important;
}
#right_column::after {
	background: none !important;
}
div.l-content #search_query,
#right_column .controls_section li:last-child{
background-color: rgba(255, 255, 255, .05) !important;
box-shadow: inset 0 1px 0 rgba(255,255,255,.09) !important;
border-top: 1px solid rgba(0, 0, 0, .09);
}
#right_column .radar .attribution{
background-color: rgba(255, 255, 255, .05) !important;
box-shadow: inset 0 1px 0 rgba(255,255,255,.09) !important;
border: 1px solid rgba(0, 0, 0, .09);
margin: 0!important;
border-radius: 0 0 10px 10px !important;
}
#right_column .controls_section .selected {
background: #333 !important;
background: -moz-linear-gradient(top,#444 0,#333 50%,#222 50%,#222 100%) !important;
background: -webkit-gradient(linear,left top,left bottom,color-stop(0%,#444),color-stop(50%,#333),color-stop(50%,#222),color-stop(100%,#222)) !important;
}
#posts .notification .avatar_frame {
	background: none !important;
}
#posts .notification.first_notification {
border-top: solid 1px #444 !important;
}
#posts .post pre {
	background: rgba(0, 0, 0, 0.3) !important;
	border-radius: 10px;
}
div.l-content #left_column {
	width: 630px !important;
}

/* http://www.tumblr.com/help 
   --------------------------------------------- */

#main_help #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
	margin-top: 20px !important;
	border-bottom: 1px solid #666 !important;
	border-top: 1px solid #888 !important;
	border-left: 1px solid #666 !important;
	border-right: 1px solid #666 !important;
	background: url(https://github.com/TumblrLove/images/raw/master/back.jpg) !important;
}
#main_help div.l-content p.result .title {
	color: #fff !important;	
	text-shadow: black -1px 1px 3px, black 1px 1px 3px;	
}
#main_help div.l-content p {
	color: #ddd !important;	
	text-shadow: black -1px 1px 3px, black 1px 1px 3px;
}


#main_help div.l-content p.result a {
	color: #349dff !important;	
	text-shadow: none;
	text-decoration: none !important;	
}

#main_help #help_footer {
	box-shadow: inset 0 0 10px #111 !important;
	background: none !important;
}
#main_help #help_footer * {
	color: #ddd !important;	
	text-shadow: black -1px 1px 3px, black 1px 1px 3px;	
	background:transparent  !important;
}
#main_help .help_search {
	border: none !important;
	background: rgba(0, 0, 0, .2) !important;
}

/* https://www.tumblr.com/settings
   --------------------------------------------- */
#dashboard_tumblelog_settings div.l-content ,
#dashboard_settings #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
	box-shadow: none !important;
	margin-top: 20px !important;
	background: url(https://github.com/TumblrLove/images/raw/master/back.jpg) !important;
}
#dashboard_tumblelog_settings .right_column ,
#dashboard_settings .right_column {
	background: none !important;
	background-color: transparent !important;
}
#dashboard_tumblelog_settings .left_column,
#dashboard_tumblelog_settings .vertical_tab > a,
#dashboard_tumblelog_settings h1 ,
#dashboard_tumblelog_settings .right_column * ,
#dashboard_tumblelog_settings .with_two_parts ,
body#dashboard_settings label[for="user_permit_marketing_emails"] ,
body#dashboard_settings label[for="user_dashboard_show_large_photos"] span ,
body#dashboard_settings .settings_container.first ,
body#dashboard_settings .right_column ,
body#dashboard_settings .help_text,
body#dashboard_settings .block_label,
body#dashboard_settings .left_column,
body#dashboard_settings  .vertical_tab > a ,
body#dashboard_settings h1 {
	color: #ddd !important;	
	text-shadow: black -1px 1px 3px, black 1px 1px 3px;		
}
#dashboard_tumblelog_settings .right_column .section input.text_field,
#dashboard_tumblelog_settings .right_column .section textarea.text_field,
#dashboard_tumblelog_settings .right_column .section input.text_field.username {
	color: #000 !important;
	text-shadow: none !important;
}
#dashboard_tumblelog_settings .right_column .chrome.white {
	color: #000 !important;
}
#page_nav .vertical_tab.selected {
	background: -webkit-gradient(linear, 0% 0%, 0% 100%, from(#444), color-stop(0.5, #333), color-stop(0.5, #222), to(#222)) !important;
}

.notification_list_table td,
.settings_container,
.settings_container.first {
	border-color: #000 !important;
}

/* http://www.tumblr.com/new/***
   --------------------------------------------- */
#dashboard_edit_post img#content_bottom,
#dashboard_edit_post img#content_top{
	   display: none !important;
}
#dashboard_edit_post #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
	padding-top: 30px !important;
	border-radius: 20px;
	margin-top: 20px !important;
	border-bottom: 1px solid #666 !important;
	border-top: 1px solid #888 !important;
	border-left: 1px solid #666 !important;
	border-right: 1px solid #666 !important;
	background: url(https://github.com/TumblrLove/images/raw/master/back.jpg) !important;
}


#audio_type_bar * ,
.audio_input_info .small_link ,
#dashboard_edit_post h1 ,
#dashboard_edit_post h2 ,
#dashboard_edit_post h3 ,
#edit_post_controls .advanced_option label ,
#dashboard_edit_post .promote_post_options_inline #promote_post_options_anchor label ,
#dashboard_edit_post #photo_type_bar * ,
#dashboard_edit_post #add_photo_link a {
	color: #ddd !important;	
	text-shadow: black -1px 1px 3px, black 1px 1px 3px;			
}

#edit_post_controls .advanced_option {
	border: none !important;
}
/* http://www.tumblr.com/dashboard  Rescued Post
   --------------------------------------------- */

.flash_notification.informational .flash_notification_wrapper {
	background: rgba(0, 0, 0, 0.1) !important;
	border: none !important;
	box-shadow: inset 0 0 4px #000;
}
.flash_notification.informational * {
	color: #ddd !important;
}


/* http://www.tumblr.com/docs/__/pages
   --------------------------------------------- */
#main_doc_page.corp_page #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
	margin-top: 30px !important;
	border-radius: 20px;
	border-bottom: 1px solid #666 !important;
	border-top: 1px solid #888 !important;
	border-left: 1px solid #666 !important;
	border-right: 1px solid #666 !important;
	background: url(https://github.com/TumblrLove/images/raw/master/back.jpg) !important;	
}

#main_doc_page.corp_page div.l-content * {
	background-color: transparent !important;
	color: #ddd !important;	
}
.doc_footer .separator,
.doc_footer {
	background: none !important;
	border: none !important;
}
/* http://www.tumblr.com/spotlight/
   --------------------------------------------- */
#dashboard_directory #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
	margin-top: 30px !important;
	border-radius: 20px;
	border-bottom: 1px solid #666 !important;
	border-top: 1px solid #888 !important;
	border-left: 1px solid #666 !important;
	border-right: 1px solid #666 !important;
	background: url(https://github.com/TumblrLove/images/raw/master/back.jpg) !important;
	padding-bottom: 30px !important;
	border-radius: 20px !important;
}
#dashboard_directory .chrome_nav .chrome.sidebar,
#dashboard_directory .chrome_nav .chrome.sidebar:hover,
#dashboard_directory .chrome_nav .chrome.sidebar:focus {
	background-color: rgba(255, 255, 255, .1) !important;
	color: #fff !important;	
	text-shadow: black -1px 1px 3px, black 1px 1px 3px !important;
}
#main_doc_page.corp_page div.l-content * {
	background-color: transparent !important;
	color: #ddd !important;	
}
img.content_top, img#content_top, img#content_bottom {
	display: none !important;
}
/* http://www.tumblr.com/lookup
   --------------------------------------------- */
#dashboard_lookup_contacts  #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
	margin-top: 30px !important;
	border-radius: 20px;
	border-bottom: 1px solid #666 !important;
	border-top: 1px solid #888 !important;
	border-left: 1px solid #666 !important;
	border-right: 1px solid #666 !important;
	background: url(https://github.com/TumblrLove/images/raw/master/back.jpg) !important;
	padding-bottom: 30px !important;
	border-radius: 20px !important;	
}

/* http://www.tumblr.com/following
   --------------------------------------------- */
#dashboard_following #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
	margin-top: 30px !important;
	border-radius: 20px;
	border-bottom: 1px solid #666 !important;
	border-top: 1px solid #888 !important;
	background: url(https://github.com/TumblrLove/images/raw/master/back.jpg) !important;
	padding-bottom: 30px !important;
	border-radius: 20px !important;
}
#dashboard_following div.l-content .chrome.clear {
	background: #ccc !important;
}

.follower .info .name a {
	color: #fff !important;	
	text-shadow: black -1px 1px 3px, black 1px 1px 3px !important;	
}

.info .description  span{
	color: #ddd !important;
}
/* http://www.tumblr.com/blog/nyuumenn/followers
   --------------------------------------------- */
#dashboard_followers #left_column .white_pane .info .name .hide_overflow {
	color: #333 !important;
	text-shadow: none !important;
}
/*「 High-Res Photos 改」に対応ここから
#container,
#left_column {
	width: auto !important;
}
div.l-header {
	width: auto !important;
}

div.l-header #logo {
	width: 86px!important;
	height: 23px !important;
	margin-top: 25px !important;
}
#right_column {
	position: absolute;
	top:0px;
	left:135px;
	z-index: 200;
	background: url(https://github.com/ThetaApple/Tumblr_iOS/raw/master/back.jpg) !important;
	box-shadow: 0 0 10px #000;	
	width: 150px !important;
	border-radius: 10px;
	height: 15px !important;
	margin-top: 20px !important;
	overflow: hidden;
		transition: all 0.3s ;
		-o-transition: all 0.3s ;
		-moz-transition: all 0.3s ;
		-webkit-transition: width 0.3s,height 3s;
}

#right_column:hover {
	height: 100% !important;
	width: 215px !important;	
}


#posts .post.new_post {
	width: 545px !important;
	padding-right: 0px !important;
	padding-left: 0px !important;
	background: none !important;
	box-shadow: inset 0 0 10px #000 !important;
}


#left_column #posts .post.new_post ,
#left_column #posts #new_post {
	width: 630px !important;
	padding-left: 0px !important;
}

#posts .post.quote .post_content{
	width: 525px !important;
	float: left;
}

#posts .post.audio,
#posts .post.regular,
#posts .post.note,
#posts .post.quote  {
	width: 525px !important;
}
