css:
@-moz-document domain("www.serebiiforums.com") {
.postbit .postbody, .blockbody {
  background: #000;
  color: #ccc;
  padding: 2em 2%;
}
    
.postbit {
  border-top: 1px solid darkseagreen;
  margin-bottom:1.5em;
}
#postlist .header {
  border-bottom: 1px solid #333;
}
    
.body_wrapper {
  max-width: 1200px;
  margin: 0 auto;
}
    
.postbit .postrow, #postlist .postbit .content {
  font: 12pt 'Open Sans','Arial','Helvetica','Verdana';
  line-height: 1.4em;
}
.bbcode_container div.bbcode_quote {
  font: italic normal 11pt Tahoma,Calibri,Verdana,Geneva,sans-serif;
}
.body_wrapper, html {
  background: #1d1d1d;
  }
    
.postbit .userinfo, .postbit .userinfo_noavatar {
  background: #111;
}
 
.postbit .posthead, .postbitlegacy .posthead, .eventbit .eventhead {
  background: #222;
}

.postbit .posttitle, .postbitlegacy .title {
  display: none;
}
.above_body {
  display: none;
}

.notices {
  display: none;
}

.pagination span a.popupctrl {
  background: transparent;
  border: 0;
}
    
.pagination span a {
  background: transparent;
  border: 0;
}  
.pagination span a:hover {
  border: 0;
  color: forestgreen;
}
  
.pagination span.selected a {
  background: transparent;
  border: 0;
  color: forestgreen;
}

.pagination span.prev_next a img, .pagination span.first_last a img {
  display: none;
}

.pagination span.prev_next a, .pagination span.first_last a {
  top: 0;
}
  
.pagination span.prev_next a[rel='next']:before {
  content: "Next";
}
.pagination span.prev_next a[rel='prev']:before {
  content: "Prev";
}
        
.popupbody {
  background: transparent;
  top: 2em;
  left: 2em;
  list-style-type: none;
  border: 0;
}
.popupbody li.formsubmit {
  background: transparent;
}
.popupbody li a, .popupbody li label {
  color: #fff;
}

#spoiler {
  padding: 0 2%;
}
}
