css:
/* ==UserCSS==
@name osu!dark transparent
@url 
@idUrl 
@md5Url 
@md5 
@updateUrl 
@enabled true
==/UserCSS== */
@-moz-document domain("osu.ppy.sh"){
body, textarea, input, textarea:focus, input:focus {
	color: #ddd;
}

textarea {
	background-color: rgba(0,0,0,0.3);
}

a {
	color: #15DFDF!important;
	text-shadow: #10CFEF 0px 0px 10px;
}

a:hover {
	color: #0df;
}

a:visited {
	color: #c35;
}

input, select {
	border: 1px solid #15BFDF;
	background-color: rgba(0,0,0,0.3);
	color: #ddd;
}

input[type="submit"], input[type="button"] {
	background-color: rgba(0,0,0,0.3);
	color: #ddd;
}

input[type="submit"]:hover, input[type="button"]:hover {
	background-color: rgba(224,240,48,0.7);
	color: #444;
}

h1,h2,h3,h4,h5 {
	color: #ce3!important;
	text-shadow: #8e3 0px 0px 10px!important;
}

h2, h2 a {
	color: #ce3;
	text-shadow: #ce3 0px 0px 10px;
}

.sfHover {
	background-color: #ce3!important;
}

.sf-menu, .sf-menu a, .sf-menu li {
	background-color: rgba(0,0,0,0.3)!important;
} 

a.hovering {
	color: #444!important;
	background-color: rgba(224,240,48,0.7)!important;
}

div#coolheader, .mainbody2, .footer, .final_footer {
	background-image: none!important;
}

.footer {
	background-color: rgba(0,0,0,0.5);
	border-bottom-left-radius: 10px;
	border-bottom-right-radius: 10px;
}

#playstats {
	margin-right: 8px;
	padding-left: 5px;
	background-color: rgba(0,0,0,0.5);
}

.news-heading {
	border-bottom: 1px solid #0ef;
}

.news-text {
	background-color: rgba(0,0,0,0.3);
	/*border-top: 1px solid #0ef;*/
}

.content-infoline {
	background-color: rgba(0,0,0,0.7);
	border-top-left-radius: 10px;
	border-top-right-radius: 10px;
	height: 24px;
}

.content-with-bg {
	background-image: none!important;
	background-color: rgba(0,0,0,0.5);
	margin-top: 0px;
}

.row1 {
	background-color: rgba(0,0,0,0.3);
}

.row2 {
	background-color: rgba(0,0,0,0.2);
}

.row3 {
	color: #aaa;
}

td.chattime {
	color: #aaa;
}

/** userpage **/
.userpage, .posttext, .quickreply, .changelog-text {
	border: 1px solid #0bd;
	background-color: rgba(0,0,0,0.3)!important;
}

.userpage-expand {
	background-color: rgba(0,0,0,0.5);
}

.userpage-expand:hover, .spoiler:hover .spoiler_head {
	background-color: rgba(224,240,48,0.5);
}

.frozen-header, .playstyle-container {
	background-color: rgba(0,0,0,0.3);
	border-radius: 5px;
}

.sectionHeading {
	background-color: rgba(0,0,0,0.5);
}

.sectionHeading, .sectionHeading:hover, #tablist a.active  {
	background-image: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAQAAAAECAYAAACp8Z5+AAAAIklEQVQIW2NkQAL8K1b8Z4TxQZyPERGMYAEYB8RmROaABAC0og5jYLfsBQAAAABJRU5ErkJggg==")!important;
}

.sectionHeading, .sectionHeading:hover {
	color: #ce3;
	text-shadow: 0px 0px 3px #0bd;
}

svg text {
	fill: #ddd;
}

.profile-tabs td {
	background-color: rgba(0,0,0,0.3);
	border-color: #0bd;
}

.profile-tabs td:hover {
	background-color: rgba(0,176,208,0.3);
}

.profile-username {
	color: #ddd;
	text-shadow: #333 0px 0px 10px;
}

.avatar-holder {
	background-color: transparent;
}

.btn {
	background-color: transparent!important;
	border: 2px solid;
}

.profileGameModes a {
	background-color: rgba(0,0,0,0.3);
	border-color: #ce3;
}

.profileGameModes .active {
	background-color: rgba(224,240,48,0.7);
	color: rgba(0,0,0,0.7)!important;
}

.spoiler_head {
	border-color: #ce3;
}

.sectionContents, .spoiler_body {
	background-color: rgba(0,0,0,0.3);
}

.profileStatLine, .profileStatHeader {
	background-color: rgba(0,0,0,0.3);
}

.profileStatHeader {
	color: #7c7;
}

.profileBeatmapHeader:hover {
	background-color: rgba(64,168,255,0.2);
	color: #ddd;
}

.levelPercent {
	color: #555;
}

.prof-beatmap {
	background-color: rgba(0,0,0,0.3);
	border: 1px solid #0ef;
	border-radius: 3px;
}

.prof-beatmap .pp-display {
	color: #ce3;
	text-shadow: #8e3 0px 0px 10px!important;
}

.prof-beatmap .pp-display-weight {
	color: #aaa;
}
/** settings **/
.mutual {
	background-color: rgba(224,240,48,0.7);
	border-color: #ce3;
}

.mutual a {
	color: rgba(0,0,0,0.7)!important;
}

.row1:hover, .row2:hover, tr.row1p:hover, tr.row2p:hover, tr.row3p:hover, tr.row4p:hover, .tablebg .row1:hover {
	background-color: rgba(0,0,0,0.4)!important;
}

.forum_table tr:hover {
	color: #ddd;
}

.postcount {
	color: #aaa;
}

.top-maps-beatmap .header {
	color: #ec3!important;
}

.btn.red {
	background-image: none;
	color: red!important;
	text-shadow: 0px 0px 10px red;
}

.stream {
	border-color: #ce3;
}

.stream:hover {
	background-color:  rgba(0,0,0,0.3);
}

.stream>.title {
	color: #ddd;
}

/** beatmap **/
#tablist, #tablist a, #songinfo td.colour {
	background-color: rgba(0,0,0,0.3);
}

#tablist a.active {
	background-color: rgba(0,0,0,0.3)!important;
}

#songinfo {
	background-color: transparent;
	color: #ddd;
}

#songinfo td {
	background-color: rgba(0,0,0,0.5);
}

#songinfo td.colour {
	color: #aaa;
}

.row1p {
	background-color: rgba(0,0,0,0.3);
}

.row2p {
	background-color: rgba(0,0,0,0.5);
}

.row3p {
	background-color: rgba(200,240,100,0.5);
}

.row4p {
	background-color: rgba(240,200,100,0.5);
}

.row3p a, .row4p a {
	color: #444!important;
	text-shadow: 0px 0px 10px #333!important;
}

.row3p:hover a, .row4p:hover a {
	color: #15DFDF!important;
	text-shadow: #10CFEF 0px 0px 10px!important;
}

/** forum **/
.tablebgsideNoSig, .pack-msg {
	background-color: rgba(0,0,0,0.3);
}

.postLeft, .postTime, .postbody {
	background-color: transparent;
}

.postauthor {
	color: #ce3!important;
	text-shadow: #ce3 0px 0px 10px;
}

.codecontent {
	color: #ddd;
}

.osu-modheader {
	color: #ce3;
}

.osu-timecode {
	background-color: rgba(0,0,0,0.3);
	color: #d57;
}

.osu-timecode:hover {
	background-color: rgba(0,0,0,0.1);
}

.postKudosu, .postButtons, .codecontent {
	background-color: rgba(0,0,0,0.3);
}

.btn {
	background-image: none;
}

.quotecontent {
	background-color: rgba(0,0,0,0.3);
	border-width: 0px 0px 0px 5px;
	border-color: #ce3;
}

.post {
	background-color: transparent;
}

.tablebgside {
	background-color: transparent;
}

.bar {
	background-color: rgba(200,240,100,0.5);
}

/** search **/
#searchArea a:hover, #searchArea a.selected {
	background-color: rgba(0,0,0,0.3);
	border: 1px solid #ce3;
}

#searchArea dl {
	border-bottom: 1px dotted #ce3;
}

.beatmapListing th {
	background-color: rgba(0,0,0,0.3);
	border-bottom: 1px solid #ce3;
}
.beatmapListing th:hover {
	background-color: #ce3;
	border-bottom: 1px solid #ce3;
	color: #444;
}

.beatmap, .bmlistt {
	height: 64px;
}

.beatmap .artist {
	color: #aaa;
}

.beatmap, .changelog_area, .beatmap .tags a {
	background-color: rgba(0,0,0,0.3);
	border-color: #ce3;
}

.bmlist-options {
	background-color: rgba(0,0,0,0.3);
}

.beatmap:hover, .beatmap .tags a:hover {
	background-color: rgba(0,0,0,0.5);
	border-color: #ce3;
}

.beatmap .rating {
	border-color: #555;
}

.posthilit {
	color: #ce3;
}

/** changelog **/
.cat h4 {
	background-image: none;
	background-color: rgba(224,240,48,0.7);
	color: #444!important;
}

.blog-post {
	background-color: transparent;
}

/** support **/
.dialog-area, .peppynote {
	background-color: rgba(0,0,0,0.3)!important;
}

.peppynote {
	border-color: #ce3!important;
}

.reasonpanels>li {
	background-color: rgba(224,240,48,0.5)!important;
	color: #444!important;
}

.slider, .ui-progressbar {
	border-width: 1px;
	border-color: #0bd;
	background-color: rgba(0,0,0,0.3);
	background-image: none;
}

.ui-slider-range, .ui-progressbar-value {
	background-color: rgba(224,240,48,0.5);
	background-image: none;
}

}

@-moz-document regexp("http[s]?://osu\\.ppy\\.sh/wiki/.*"){
/* wiki */

body,p,li {
	color: #ddd!important;
}

div.mw-panel {
	background-color: rgba(0,0,0,0.5)!important;
	border-color: #ce3!important;
}

.toc {
	background-color: rgba(0,0,0,0.3)!important;
	box-shadow: 0px 0px 10px inset;
}


h1,h2,h3,h4,h5 {
	color: #ce3!important;
	text-shadow: #8e3 0px 0px 10px!important;
}

a {
	color: #0bd!important;
	text-shadow: #10CFEF 0px 0px 10px;
}

a:hover {
	color: #0df;
}

a:visited {
	color: #c35;
}

tr {
	color: #ddd!important;
	background-color: transparent!important
}
}

@-moz-document domain("osu.ppy.sh"){
.bodytopbg {
	background-image: url("/*[[bgImage]]*/") !important;
	background-size: cover!important;
	background-position: 50%!important;
	background-repeat:no-repeat!important;
	background-attachment:fixed!important;
}
}
