css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.yafud.pl") {
   div#strona > * {
     display: none;
   }
   div#strona > div#login, div#strona > div#top, div#strona > div#srodek {
     display: block;
   }
   div#adsenseLeft, div#adsenseRight, div#adsenseTop, .adsbygoogle, #fbBOX, #statsAdd, .wpis:not([id]) , div#adsense_banner_top {
     display: none;
   }
   div#lightbox, div#srodek {
     max-width: 100%;
     width: 100%;
   }
   div#srodek {
     padding-left: 15px;
     padding-right: 15px;
   }
}
