css:
@-moz-document domain("nimbus.psim.us"),domain("play.pokemonshowdown.com"){
body {
    background: url("http://i.imgur.com/mb9UumB.jpg");
    background-color: #344B6C;
    color: #FFFFFF;
}
#header {
    font-family: Verdana, Helvetica, Arial, sans-serif;
    background: url("http://i.imgur.com/kWY9I5O.png");
}
.ps-room.ps-room-light.scrollable {
    font-family: verdana, helvetica, arial, sans-serif;
    background: rgba(230, 240, 255, 0.7);
    border-right: 1px solid #575757;
}
.pad {
    border-right: 1px solid #575757;
}
.mainmenuwrapper {
    border-right: 1px solid #575757;
}
.battle-log {
    background: url("http://i.imgur.com/JwNgfjk.png") repeat;
    border-right: 1px solid #575757;
}
.pm-log {
    background: url("http://i.imgur.com/jxOe6XC.png") repeat;
    border: 1px solid #575757;
}
.chat-log.hasuserlist {
    background: url("http://i.imgur.com/pX0Lhyc.png");
    background-repeat: no-repeat;
    background-size: auto auto;
    background-position: top right;
    background-color: #E9F3F3;
    opacity: 1;
}
.chat-log-add {
    background:url("http://i.imgur.com/2dyqgwD.png");
    border-top: 1px solid #575757;
}
.pm-log-add {
    background: #BBB;
    border-top: 0px solid #575757;
    border-right: 1px solid #575757;
    border-bottom: 1px solid #575757;
    border-left: 1px solid #575757;
}
.ps-overlay {
    background: rgba(230, 240, 255, 0.5)
}
.button.button {
    text-shadow: none;
}
.logo {
    opacity: 0;
}
.battleform label {
    background: url("http://i.imgur.com/yFWeHx0.png") no-repeat scroll left center transparent;
    padding: 4px;
    opacity: 0.9;
    color: #FFF;
}
.menugroup {
    background: rgba(0, 0, 0, 0);
}
.menugroup .button {
    background: none repeat scroll 0% 0% #474747;
    color: #FFFFFF;
    box-shadow: 0px 1px 0px 0px #5C5C5C inset, 0px 2px 3px rgba(0, 0, 0, 0.2);
    border-color: #212121;
    width: 230px;
}
.menugroup .button:hover {
    background: none repeat scroll 0% 0% #505050;
    color: #FFFFFF;
    box-shadow: 0px 1px 0px 0px #5F5F5F inset, 0px 2px 3px rgba(0, 0, 0, 0.2);
    border-color: #212121;
}
div.roomlist {
    font-family: Verdana, Helvetica, Arial, sans-serif;
}
.roomlist a.ilink {
    font-family: Verdana, Helvetica, Arial, sans-serif;
    border: 1px solid #2E2E2E;
    color: #001433;
    background: #EEEEEE;
    text-shadow: 1px 1px #EAEAEA;
}
.roomlist a.ilink:hover {
    font-family: Verdana, Helvetica, Arial, sans-serif;
    border: 1px solid #2E2E2E;
    color: #001433;
    background: #DFDFDF;
}
.pm-window.focused h3,
.pm-window.focused h3:hover {
    border: 1px solid #212121;
    color: #FFF;
    background:rgba(0, 0, 0, 0.8);
    font-family: verdana,helvetica, arial, sans-serif;
}
.pm-window h3 {
    border: 1px solid #212121;
    color: #FFF;
    background: rgba(0, 0, 0, 0.7);
    font-family: verdana,helvetica, arial, sans-serif;
}
.pm-window h3:hover {
    border: 1px solid #212121;
    color: #FFF;
    background: rgba(0, 0, 0, 0.8);
    font-family: verdana,helvetica, arial, sans-serif;
}
button.astext {
    background-color: transparent;
    border: 0;
    padding: 0;
    font-family: Verdana, Helvetica, Arial, sans-serif;
}
button.astext:hover {
    background-color: transparent;
    border: 0;
    padding: 0;
    font-family: Verdana, Helvetica, Arial, sans-serif;
}
.maintabbarbottom {
    background-color: rgb(190,190,190);
    border-top: 1px solid #575757;
    border-right: 0px solid #000000;
    border-bottom: 1px solid #575757;
    border-left: 0px solid #000000;
}
.tabbar a.button.cur,
.tabbar a.button.cur:hover {
    text-shadow: none;
    font: 11px Verdana, Helvetica, Arial, sans-serif;
    color: #000;
    background:url("http://i.imgur.com/SRdPIN3.png");
    border: 1px solid #575757;
}
.tabbar a.button {
    text-shadow: none;
    font: 11px Verdana, Helvetica, Arial, sans-serif;
    color: #000;
    background-color: #F2F2F2;
    border: 1px solid #575757;
    text-shadow: none;
}
a.button.notifying.closable, .tabbar a.button.notifying {
    background: url("http://i.imgur.com/tehpryL.png");
    border: 1px solid #575757;
}
a.button.notifying.closable:hover, .tabbar a.button.notifying:hover {
    background: url("http://i.imgur.com/MkX5drf.png");
    border: 1px solid #575757;
}
a.button.notifying.closable:active, .tabbar a.button.notifying:active {
    background: url("http://i.imgur.com/MkX5drf.png?1");
    border: 1px solid #575757;
}
.ps-room.ps-room-opaque {
    background: rgba(240, 240, 240, 0.8);
    color: #FFF;
    border-right: 1px solid #575757;  
}
div.trainer > strong {
    color: #323841;
}
.battle-log h2 {
    background: #808080;
    color: #FFF;
    border-top: 1px solid #000000;
    border-bottom: 1px solid #000000;
}
.userlist li button {
    text-shadow: 0px 1px 0px 0px #000000;
}
ul.userlist {
    border-right: 1px solid #575757;
}
span.username {
    text-shadow: 1px 1px #CFCFCF;
}
div.pm-log > div {
    border-right: 0px solid #000000;
}
.icon-user {
    display: none;
}
button.select.formatselect:hover,
button.select.teamselect:hover {
    background: #E3E3E3;
}
button.select.teamselect.preselected:hover {
    opacity: 0.85;
}
.infobox {
    background: rgba(102, 136, 170, .075);
}
span.message-pm {
    color: #00A0A0;
}
div.chat small {
    color: #001F2E;
    text-shadow: 1px 1px #D4D4D5;
}
div.tournament-bracket.tournament-bracket-overflowing {
    background: url("http://i.imgur.com/WCEwpwL.png");
    background-position: center;
    background-repeat: no-repeat;
    opacity: 1;
}
p > em {
    color: #000000;
}
.highlighted {
    background: #B3D3E8;
}
div.tournament-tools.active {
    background: #DFDFDF;
    box-shadow: none;
}
div.notice > div.infobox > strong > font {
    color: #007AA3;
}
div.notice > div.infobox > font {
    color: #00B2B2;
}
div.notice > b > font {
    color: #007AA3;
}
div.mainmenufooter a:link, div.mainmenufooter a:hover, div.mainmenufooter a:visited {
    color: #C0C6C9;
    text-decoration: none;
}
.broadcast-blue {
    color: #FFF;
    background-color: rgba(102, 136, 170, .9);   
    border: 1px solid #003380;
}
.broadcast-red {
    color: #FFF;
    background: rgba(170, 85, 68, .8);   
    border: 1px solid #7A1414;     
}
.broadcast-green {
    color: #FFF;
    background-color: rgba(85, 153, 85, .8);   
    border: 1px solid #003300;     
}
div.battle-controls em {
    color: #000; 
}
div.battle-log-add {
    background:url("http://i.imgur.com/2dyqgwD.png");
    border-top: 1px solid #575757;   
    border-left: 1px solid #575757;  
}
div.battle-log {
    border-left: 1px solid #575757;  
}
div.battle {
    border-bottom: 1px solid #575757;   
}
div.notice.tournament-message-end-bracket.tournament-message-end-bracket-overflowing{
    border: 1px solid #575757;  
}
div.battle-log div.inner {
      overflow: hidden;
      border-right: 1px solid #575757;
}
}
