    /*
    FLASH NOTICE
    */
    
    #flash_notice, #flash_alert, #flash_error {
      position: fixed;
      z-index: 1003;
      top: -4em;
    }
    #flash_notice.expose, #flash_alert.expose, #flash_error.expose {
      opacity: 1 !important;  
      animation: 7s ease-in-out 0s normal none 1 running expose;
    }
    #flash_notice .message {
      color: white !important;
      background-color: #08C;
      border: 0;
    }
    #flash_notice .message, #flash_alert .message, #flash_error .message {
      color: black;
      box-shadow: 0px -1000em 0px 1000em #08C;
      font-size: 1.25em;
      padding: 0.83em;
      border-radius: 0;
    }
    @Keyframes expose {
      0% {
        top: -4em;
      }
      15% {
        top: 0px;
      }
      90% {
        top: 0px;
      }
      100% {
        top: -4em;
      }
    }
