css:
/* Dark JEB by siliconworm */

@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix(http://jeb.biologists.org),
url-prefix(https://jeb.biologists.org),
domain("jeb.biologists.org") {

/* 页面背景色 */
body{background: #222 !important;}
.hw-gen-page.pagetype-content{background: #292929 !important;}
#content-block{background: #292929 !important; background-color: #292929 !important;}
#col-2 div.content-box div.cb-slug, #col-2 div.content-box div.cb-corr-slug, #col-2 div.content-box div.cb-addendum-slug{background: #292929 !important; background-color: #292929 !important;}
#pageid-home #col-3 #sidebar-global-nav ul.pub-links li {background: #292929 !important; background-color: #292929 !important;}
#col-3 {background-color: #292929 !important;}
#col-2 {background-color: #292929 !important;}
#col-2 div.cb-contents {border-left: #292929 !important; background-color:  #292929 !important;}
#col-2 div.cb-section  {background-color:  #292929 !important;}
#col-2 div.sidebar{background-color:  #292929 !important;}
#col-2 div.sidebar-qs{background-color:  #292929 !important;}
#col-2 .content-box h4 {background-color:  #292929 !important;}
#col-2 div#docked-nav{background-color:  #292929 !important;}
#col-2 {border-right: #292929 !important}
#col-3 #sidebar-global-nav ul.pub-links li{background-color:  #292929 !important;}
#col-3 div.most-links-box div.most-header{background-color:  #292929 !important;}
#col-3 div.most-links-box div.most-list {border:  #292929 !important; background-color:  #292929 !important;}
#col-3 #sidebar-global-nav ul.pub-links li {border:  #292929 !important; background-color:  #292929 !important;}
#col-3 .sidebar-icon-group ul.sidebar-icon-group-interior{border:  #292929 !important; background-color:  #292929 !important;}
#pageid-toc #content-block .cit-list li.openaccess {background: #474E53;}
.cit.openaccess {background: #474E53;}
#hovering-abs {background-color: #474E53 !important;}
#hovering-ref {background-color: #292929 !important;}
.searchresults-bracket div {background-color: #474E53 !important;}
td.proxy-archive-year, td.proxy-archive-decade, #content-block td.proxy-archive-year, #content-block td.proxy-archive-decade {background-color:  #2A384B !important;}
#footer{background: #292929 !important; background-color: #292929 !important;}
.article.fulltext-view{background: #292929 !important;}
.hw-gen-page,.main-block,.home-announce,.cover-img-wrap,.home-announce-wrapper,.home-announce-bottom.sb-div,.current-issue,.jnl-links,.in-this-issue,.coverlines,
.button-list pub-links,.button-list pub-links,.sidebar-icon-group,.sidebar-global-nav,.button-list.pub-links,.button-list.pub-links,.button-list.pub-links,.sidebar-icon-group-home,
.content-box,.cb-contents,.fig pos-float  odd
{background: #292929 !important;}
div.article .fig, div.article .table {background: #292929 !important;}
#pageid-content .article .fig .fig-inline{background: #292929 !important;}
.fig-caption, .table-caption{background: #292929 !important;}
.current-vol-issue{background: #DA4E4E !important;}
#pageid-content #content-block .article .boxed-text {background-color: rgba(59, 67, 70, 0.87)!important;}
.fig-inline, .table-inline {background-color: rgb(80, 86, 105);}
#pageid-search #content-block legend {background: #854A7B;}
.container-30 .suffix-1 {
  background-color: #333;
}
div.panel-region-sidebar-right div.pane-style-alt-content, div.panel-region-sidebar-right div.pane-cob-journals {
  background: #222222;
}
div.cluetip .cluetip-outer, div.cluetip .ui-cluetip-header {
  background-color: rgba(54, 67, 87, 0.5);
}
#mini-panel-home_page_middle .snippet > .snippet-content {
  background: rgb(36, 36, 47);
}
.highwire-article-citation.tooltip-enable.abstract-popup-hover {
  background-color: #214C5E;
}
#region-menu .nice-menu .menuparent.over > ul {
  -webkit-border-radius: 0 0 0.25em 0.25em;
  border-radius: 0 0 0.25em 0.25em;
  background-color: rgba(17, 24, 39, 0.8);
}
body #region-menu .nice-menu .menuparent.over, body #region-menu .nice-menu .menuparent.over > a, body #region-menu .nice-menu .menuparent > ul {
  background-color: #274461;
}
.highwire-search-form fieldset.collapsible.form-wrapper {
  background-color: #333;
}
#section-header .zone, #section-content .zone {
  background-color: #333;
}

/* 深色主题还需开启下面这段，把文字颜色变为浅色  */
h1,li,.main_text, 
.answer-txt,.post-detail,.cmt-content, .document,
.main-message-content,
.card-footmark, .article.fulltext-view , .hw-gen-page.pagetype-content,
.article, .article-title,
.content-txt
{color: #DDD !important;} 
.img-caption span
{color: #666 !important;}
  .cit .cit-metadata .cit-title, .cit .cit-metadata .cit-title-note {color: #B4CFB3;}  
  ul.kwd-group li.kwd{background-color:  rgba(60, 60, 60, 0) !important;}

 h1 {background: #292929; color: #DF75A5  !important;}
 h1 span, h2 span, h2 {color: #DF75A5  !important;}
 h2.pane-title {  color: #4ADAA3;}
 #content-block h1, #content-block h1 span, #pageid-logout #content-block h2 {color: #DF75A5;}
 #pageid-toc #content-block h2 {background: #292929; color: #FFF79B;}
 #col-2 .content-box h4 a, #col-2 .content-box h3 {color: #AA3A72;}
 #col-2 p.qs-instructions {color: #AA3A72;}
 #col-3 div.most-links-box h3 {color: #AA3A72;}
 h3 {color: #AA3A72;}
 h4 {color: #CDFEF4;}
 element.style{color: #DF75A5;}
 /*a {background: #292929; color: #C0C0C0;}*/
 label {background: #292929; color: #ADDBDE;}
 li {background: #292929; color: #ADDBDE;}
 strong.fl {background: #88D580; color: #292929;}
 #pageid-home #content-block .jnl-links li p {background: #292929; color: #A9B7C2;}
 em {color: #88D580;}
 #pageid-content #content-block h2 {background-color: #515461;color: #D7F170;}
 div.section .section-nav, div#intro-header .section-nav {background-color: #515461;}
 #pageid-content #content-block .kwd-group-title {background-color: #35516B;color: #90CDCF;}
 
 #header .header-ac-elements #hdr-login label {
color: #BE588B!important;
}

input {
color: #F7E248!important;
background-color: #383838!important;
}
option {
color: #8FE4E4!important;
background-color: #383838!important;
}
select {
color: #FF62E6!important;
background-color: #383838!important;
}

/*no bullshit*/
.altmetric_container{display: none !important;}
#col-3 .sidebar-icon-group{display: none !important;}



/* defaults for all links */
a:link, .link, a font[color], .mn-hd-txt, 
    a.fl:link, .fl a, .flt, a.flt, .gl a:link, a.mblink, .mblink b, ._Hg ._qA, ._Hg ._Pn, ._Hg ._Uq, #gt-feedback, #gb a  { 
        color: #7C9EDC !important; 
        transition: color .4s, text-shadow .4s !important;
    }
                    
a:visited { 
    color: #F92672 !important; 
}

/* hovers */
.menuitem:hover, a:hover, a span:hover, .actbar-btn:hover, #gt-feedback:hover, #gb a:hover {
color: #E5E5E5 !important; text-shadow: #ffffff 0px 0px 2px !important;
transition: color .4s, text-shadow .4s !important;
}

/* big link visited */
#res .g a:visited { opacity: 0.5 !important; }

/* URL text */
.a, cite, .cite, .cite:link, cite b, #resultStats { color: #999 !important; }

/* dashes between Cached and Similar links */
.gl { color: #999 !important; }

/* disable dashed outline on focused elements */
:focus { outline: none; }

/* image thumbnails */
#res .g img, #ImgContent td img {
  border: none !important;
box-shadow: 4px 4px 10px rgba(0,0,0,0.8) !important;
}

/* Main text color */
div, td, font[color="#000000"], .qbtbha .qbtbtxt.qbclr, .gt-rw-span, .gt-syn-span, .gt-card-ttl-txt, #contribute-target, #source { color: #CECECE !important; }

/* fix for google suggest */
.aAutoComplete .cAutoComplete, .google-ac-a .google-ac-c, .gac_a > .gac_c { color: #CCCCCC !important; }
.aAutoComplete .dAutoComplete, .google-ac-a .google-ac-d, .gac_a > .gac_d { color: white !important; }
.google-ac-e td, .gac_e { color: #380000 !important; }

/* make sure web forms (and google suggest) don't get their colors changed */
input *, textarea *, .aAutoComplete {
    background-color: -moz-Field !important;
    color: #999999 !important;
}


}
/* Dark JEB by siliconworm */
