css:
@-moz-document url("about:addons") {
  * {
    opacity: 1 !important; 
    text-shadow: none !important;
    box-shadow: none !important;
  }
  *:not(.radio-check) {
    border-radius: 0px !important;
  }
  *|button, menulist, colorpicker[type="button"]  {
    background-image: none !important;
  }
  *|*:root {
    background-color: #CAD8EA;
    background-image: none;
    padding: 0 0 0 0;
  }
  .list, *|*.main-content {
    background-color: white;
  }
  label#detail-version, #detail-creator, .version, .disabled-postfix {
    color: rgb(150, 150, 150);
    font-weight: 400;
  }
  .addon {
    border-top: none;
    border-bottom: 0px solid #E5E5E5;
  }
  *|*.main-content {
    border: none;
  }
  .detail-row, .detail-row-complex, setting {
    border: none;
  }
  #header {
    margin-top: 18px;
    padding-top: 0px;
    padding-right: 18px;
  }
  #categories {
    margin-right: 0;
    margin-left: 0;
  }
  richlistitem.category {
    border: none;
  }
  .addon[active="false"] {
    background-image: none !important;
  }
}
@-moz-document url-prefix("https://services.addons.mozilla.org") {
  * {
    border-radius: 0px !important;
    opacity: 1 !important; 
    border-width: 1px !important;
    text-shadow: none !important;
    font-family: Arial !important;
  }
  section, a, header, h2, .header {
     box-shadow: none !important;
  }
  #sub li {
    border-bottom: none !important;
  }
  html {
    background-color: rgb(240,240,240);
  }
  #featured-addons, .gallery-wrap, #mission, body > header:nth-child(1), #up-and-coming > h2, #featured-themes > h2:nth-child(1), #more-ways > h2:nth-child(1), .header  {
    background: #DFE8F2 !important;
  }
  a.video:nth-child(1) {
    background-color: #DFE8F2 !important;
  }
  .recs #mission, .recs #my-account {
    margin-bottom: 0 !important;
  }
  #up-and-coming {
    background:white !important;
  }
  .addons li > a {
    background: white;
  }
}
