css:
@-moz-document domain(computerbase.de) {
/* CB Logo mit orangenem B */
header .homepage-link img {
  width: 0;
  height: 0;
  padding-right: 147px;
  padding-bottom: 64px;
  background: url("data:image/svg+xml;base64,");
}

/* ads */
.adbox-skyscraper, .adbox-banner, .adbox-wrapper, .adbox-topbanner{
  z-index: 3 !important;  
  opacity: .1;
  transition: opacity .25s ease-in-out;
  -moz-transition: opacity .25s ease-in-out;
  -webkit-transition: opacity .25s ease-in-out;  
}

.adbox-skyscraper:hover, .adbox-banner:hover, .adbox-wrapper:hover, .adbox-topbanner:hover{
  opacity: 1;
}
/* END ads */

/* link colors */
/* change to blue */
.carousel-h3, .teaser__title, .slide__title,  .box h2 a, .tabs__li a, a.footer1-box p em, .download-nav__sub__a{
  color: #2c5b9c !important;
}

a{
  color: #2c5b9c;
}

/* article hover */
.article__title:hover, .ticker__body span:hover, .slide__title:hover, .teaser__title:hover, .ticker__body a:hover, .video__title a:hover, .video-hero__title a:hover, .download-cats-li2 a:hover, .download-featured__title a:hover, .download-classic__title a:hover{
  color: #e84d0e;
}  

/* article visited */
.ticker__article-link:visited, .ticker__update:visited, .article__h2 a:visited, .article__title:visited, .ticker__body a:visited{
  color: #777 !important;
}

.ticker--compact a{
  color: #000 !important;                  
}
/* END link colors */

/* buttons */
a.download-url__link, .button-link, .newcontent_textcontrol {
  background: #2c5b9c;
}
/* END buttons */

/* forum box */
.box--blue{
  background-color: #e5e5e5 !important;
  color: #222 !important;
}

.box--blue time {
	color: #777 !important;      
}
/* END forum box */
 
/* share buttons */
/* dont animate social buttons in header */
.header--1 .follow-links li{
  margin: 9px 3px
}

/* disable share buttons */
.share-link {
  display: none !important;
}

/* show rss-feed icon */
.share-link--feed{
  display: block !important;
  min-width: 3.8em;
  max-width: 3.8em;
  margin-left: 13.5em;
}
/* END share buttons */

/* news list */
.ticker li {
  padding: .125rem 0;
}

/* tags list */
.taglist li a{
  color: #fff;  
}

/* text in articles over full width */
.text-width {
  max-width: 60rem;
}
.article-view__header {
  padding: 0 0;
  width: 85%;
}
.article-view__header figure {
  width: 100% !important;
}
.article-view__content .article-view__pic11 {
  left: 825px;
}
/* END text in articles over full width */

/* article navigation */
.tocbox {
  position: fixed;
  right: 50%;
  margin-top: 13.45em;
  margin-right: -49.5em;
  z-index: 5000;
}
.toctoggle {
  display: none;
}
.toc--top {
  display: block !important;
}
.toc {
  border: 1px solid #bbb;
  box-shadow: none !important;
  border-top-left-radius: 0;
  min-width: 380px;
  max-width: 380px;
}
.gallery-next--inline{
  z-index: 5001;
  }
/* END article navigation */

/* Forum */
.postbit, .postbitlegacy, .eventbit , .L1, .threadlist-wrapper, .forumbits{
  padding: 0px !important;
}
.blockhead {
  box-shadow: none;
}
.toolsmenu {
  padding: 0px;
}
.toolsmenu-inner, .thread_controls-inner {
  background: #3572AF none;
}
.toolsmenu .popupgroup a.popupctrl, .toolsmenu .popupgroup .popupmenu a.popupctrl, .toolsmenu .popupgroup .popupmenu:hover a.popupctrl, .toolsmenu .popupgroup .popupmenu:hover .popupctrl a.popupctrl.active {
  border-left: none;
}

.pagination span a {
  border-radius: 0px !important;
}
.blockfoot .button, .navpopupmenu .popupctrl, .navpopupmenu .textcontrol {
  background: #f4f4f4 none !important;
  border: 1px solid #cedfeb !important;
  color: #2c5b9c !important;
  font-weight: 500 !important;
}

.options_block_container {
  display: none !important;
}
.navlinks {
  border: none;
  background: #fff;
}

.vb-nav {
  margin-bottom: 0em;
  padding-bottom: 0.2em;
}
.below_body {
  margin-bottom: 20px;     
}
.block--top>.wrapper>.row {
  padding-bottom: 0px;
}

.popupbody, .menupanel {
  box-shadow: none !important;
}
/* END Forum */
}
