css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("addic7ed.com") {

    body { font-size: 10px; background-color: black;}
    table { background-color: black;}
    html { margin: 0px; background-color: black ;}
    #header { background-color: black;}
    #container70 {display: none}
    .tabel { background-color: black;}
    .tablecorner {display: none}
    .tabel90 { background-color: darkgray; width: 100%;display: none}
    
    /*Hide News*/
    #containernews {display: none}
    
    /*Link Color*/
    a { color:blueviolet }
    
    /*Select TV Show */
    #qsShow {position:absolute; left:10%; top:0%;width:65% }
    #qsShow {display: none}
        
    /*Season List*/
    #sl { display: inline !important;}
    
    /*Top Banner*/
    /*body > center:nth-child(1) > table:nth-child(2) > tbody:nth-child(1) > tr:nth-child(3) { position:fixed;top:0%;bottom:10%;left:80%;right:0%}*/
    #mgidtot {display: none}
    body > center:nth-child(1) > table:nth-child(2) > tbody:nth-child(1) > tr:nth-child(3) { display: none}    
    
    /*Remove all unused spaces*/
    br { display: none}
        
    /*Choose Language*/
    body > center:nth-child(1) > table:nth-child(2) > tbody:nth-child(1) > tr:nth-child(2) > td:nth-child(1) > h1:nth-child(1) {display: none}
    body > center:nth-child(1) > table:nth-child(2) > tbody:nth-child(1) > tr:nth-child(2) > td:nth-child(1) > h1:nth-child(1) {margin-top: 20px}
    
    /*Search Bar*/
   /* .coolBoton  { position:absolute;left:0%;top:0px}*/
    
     #search { background-color: white; position:relative;left:0%;top:0%;width:90%  }
    .coolBoton  { background-color: grey; position:relative;left:100%;right:0%;top:100% }
    
    #form1 #search { position:fixed;left:10%;top:50%;width:80%  }
    #form1  .coolBoton  { position:fixed;left:30%;top:55%;width:40%}

    .tabel70 #search { position:relative;left:00%;top:00%;width:30% }
    .tabel70 .coolBoton { position:relative;left:00%;top:00%;width:30%}
    .tabel70 > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(2) > form:nth-child(1) > div:nth-child(1) > b:nth-child(4) {position:relative;left:00%;top:00px;padding-top: 10px}

    
    /*Episode List in search*/
    .tabel70 { background-color: black;color: white;width: 100%;font-size: 10px; }
     /*Lower Banner in Search*/
    body > center:nth-child(2) > center:nth-child(5)  {display: none}
    body > center:nth-child(2) > center:nth-child(20) > center:nth-child(1)  {display: none}
    /*Mid Banner in search result*/
    img:nth-child(1) { display: none}
    /*Upper Banner in search*/
    .tabel70 > center:nth-child(2) {display: none}
    
    #qssShow {display: none}
    body > center:nth-child(1)  {display: none}
    
    body > center:nth-child(2) > table:nth-child(5) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(1) > a:nth-child(1)  { position:absolute;left:50%;top:0px}
    .tabel70 > tbody:nth-child(1) > tr:nth-child(2) > td:nth-child(2) > center:nth-child(3) > form:nth-child(2) > input:nth-child(3) { display: none}
    #episodes > td:nth-child(2) {display: none}
    #episodes > form:nth-child(1) > table:nth-child(1) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(3) {display: none}
    body > center:nth-child(2) > center:nth-child(8) {display: none}
    body > center:nth-child(1) > table:nth-child(2) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(1) {display: none}
    body > center:nth-child(1) > table:nth-child(2) > tbody:nth-child(1) > tr:nth-child(4) {display: none}
    body > center:nth-child(1) > table:nth-child(2) > tbody:nth-child(1) > tr:nth-child(5) {display: none}

    body > center:nth-child(2) > center:nth-child(7) {display: none}
}
