css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("facebook.com"), regexp("https?://(www|apps|touch)\\.facebook\\.com/(?!plugins/).*") {
body {
  background-color: rgb(2, 0, 20) !important;
}
#leftCol{
  background-color: rgba(255, 255, 255, 0.8);
}
.hasCaption, .postText{
  color: white !important;
}
.fbPhotoSnowlift .rhc {
    background-color: rgba(2, 0, 20, 0.5);
}
#contentCol{
  background-color: rgb(2, 0, 20) !important;
}
._4-u2, .mbm, ._5jmm, ._5pat, ._5v3q, ._2l4l, ._x72{
  background-color: rgba(255, 255, 255, 0.8);
  box-shadow: 0px 0px 1px white;
  border-radius: 0px;
}
/* top bar */
#blueBarNAXAnchor{
  background-image: url("http://www.robertjamesrussell.com/wp-content/uploads/2013/10/Desktop-Space-HD-Wallpapers.jpg");
  background-position: right 0px top 0px;
  background-color: black;
  border-color: transparent;
  border-width: 0px;
}
/*that little blue shit that wouldn't go away*/
#blueBarNAXAnchor::after{
  background: none !important;
}
/*search field*/
  .__wu .roundedBox .smurfbarTopBorder, .__wu .roundedBox .smurfbarBottomBorder, .__wu .roundedBox .uiTypeahead {
  background: rgb(2, 0, 20) none repeat scroll 0% 0%;
  border-radius: 0px;
}
#q{
  color: white !important;
}
#q::-moz-placeholder{
  color: white !important;
}
/*chat window title*/
._5q5b .fbNubFlyoutTitlebar{
    background: repeating-linear-gradient(135deg, rgb(10, 10, 10) 10%, rgb(30, 30, 30) 12%);
  border: 1px solid black;
}
._5q5b .fbNubFlyoutTitlebar:hover{
  background: repeating-linear-gradient(135deg, rgb(0, 0, 0) 10%, rgb(30, 30, 30) 12%);
}
#leftCol{
  background-color: transparent;
  box-shadow: 0px 13px 0px rgb(2, 0, 20), -11px 12px 0px rgba(255, 255, 255, 0.8);
}
.linkWrap, .prs > div:nth-child(2) > a:nth-child(2), .fbxWelcomeBoxName{
  color:white !important;
}
/* chat names */
._5pr2 ._42fz a {
  color: white;
}
.fbChatSidebar li a:hover, .fbChatSidebar .fbFeedTicker .fbFeedTickerStory:hover {
  background-color: black;
  border-color: transparent !important;
  box-shadow: 1px 0px 2px rgba(60, 60, 60, 0.1) inset;
  background: repeating-linear-gradient(135deg, rgb(10, 10, 10) 20%, rgb(30, 30, 30) 22%);
}
._5vb_ ._55y4 ._bui ._5afe:hover, ._5vb_ ._55y4 ._bui ._5afe:focus, ._5vb_ ._55y4 ._bui ._5afe:active, ._5vb_ ._55y4 ._bui ._5afd ._5afe {
  background-color: rgba(220, 222, 227, 0.3);
  border-radius: 0px;
}
/* chat bubbles */
._1nc6 ._5w1r, ._1nc6 ._3okg::before {
  background: rgba(100, 100, 100, 0.2);
}
/* controls on top bar */
#u_0_d > span > button:nth-child(2){
  filter: brightness(10);
}
[data-click="home_icon"] > a > div > div{
  background-color: rgba(60,60,60,.6);
}
[data-click="home_icon"] > a > div > div:hover{
  background: rgba(60,60,60,.9) !important;
}
.jewelButton{
  filter: brightness(10);
}
#privacyFlyoutLabel{
  filter: brightness(10);
}
#userNavigationLabel{
  filter: brightness(10);
}
.jewelCount{
  filter: brightness(.1);
}
/* chat sidebar  hover*/
.fbChatSidebar{
  background-image: url("http://www.robertjamesrussell.com/wp-content/uploads/2013/10/Desktop-Space-HD-Wallpapers.jpg");
  background-position: right 0px top 0px;
  border-left-color: black !important;
  box-shadow: none !important;
}
.fbSidebarGripper{
  border-top: 0px !important;
}
.tickerStoryContent{
  color:white;
}
.fbChatSidebar{
  margin-right: -157px;
  transition: margin-right 1s;
  border-left: 5px black;
}
.fbChatSidebar input{
 opacity: 0;
  transition: opacity 1s;
}
.fbChatSidebar:hover{
  margin-right: initial;
}
.fbChatSidebar:hover  input{
  opacity: 1;
}
/*friend search*/
.fbChatSidebar table{
  background-color: rgb(2, 0, 20) !important;
}
/*message page */
._kv{
  background: repeating-linear-gradient(135deg, rgb(10, 10, 10) 10%, rgb(30, 30, 30) 12%);
}
._kv:hover{
  background: repeating-linear-gradient(135deg, rgb(0, 0, 0) 10%, rgb(30, 30, 30) 12%);
}
.uiSideNavEditButton a{
  filter: brightness(0.5);
}
}
