css:
@-moz-document regexp("^https?://bakabt\\.me(?!/description).*?$") {
body {
	background-image: url(data:image/jpeg;base64,);
	background-color: #FAFAFA;
	background-repeat: repeat;
}
.main {
	box-shadow: 0 0 100px #444
}
div.torrents a.title:link,
div.torrents tr:not(:hover) span.tags a:link,
#details h2 a:link[class=""] {
	color: #617168;
	text-decoration: none
}
div.torrents a.title:visited,
div.torrents span.tags a:visited,
#details h2 a:visited[class=""] {
	color: #68A382;
	text-decoration: none
}
a {
	text-decoration: none
}
a:hover {
	text-decoration: underline!important;
	text-decoration-skip: ink;
}
.alt0 {
	background-color: transparent
}
.alt1 {
	background-color: rgba(0, 0, 0, 0.04)
}
div.quote {
	background-color: #F9FCEF;
	font-size: 90%
}
}

@-moz-document regexp("https?://bakabt.me/(offer|browse).php.*") {
.main {
	width: auto;
	margin: 0 4em
}
.banner {
	background-size: cover
}
.content,
.browse_filter ul li.search,
.browse_filter ul li.searchcloud,
.browse_filter ul li.tags,
.browse_filter ul li.category {
	width: auto;
	margin: 0
}
.alt0 {
	background: transparent
}
div.torrents tr:hover {
	background-color: #EEDDFF
}
div.torrents td.name div:last-child {
	margin-left: -10ex
}
div.torrents td:first-child {
	position: relative
}
div.torrents td.name > div[style*="clear"] {
	font-size: 8pt;
	display: block;
	text-indent: -6ex;
	overflow: hidden;
	margin-left: 60%
}
div.torrents td.name .pending {
	display: none
}
div.torrents table td {
	vertical-align: top
}
div.torrents table tr.torrent_alt td {
	white-space: normal;
	max-width: inherit
}
div.torrents table th.category {
	width: 30px;
	text-overflow: ellipsis;
	overflow: hidden
}
div.torrents table th.category a {
	display: block;
	width: 30px;
	text-overflow: ellipsis;
	overflow: hidden
}
div.torrents table td.category {
	width: 30px;
	padding-top: 2px
}
div.torrents td.name {
	line-height: 1.0
}
div.torrents .category + td > * {
	display: block;
	float: left
}
#offers div.torrents table a.title,
div.torrents .category + td > *:nth-child(1) {
	max-width: calc(60% - 100px);
	white-space: normal;
	text-overflow: normal
}
div.torrents table a.title {
	overflow: visible;
	max-width: calc(60% - 100px);
	white-space: normal
}
div.torrents table .tags {
	display: block;
	float: right;
	width: 39%;
	white-space: normal;
	opacity: 0.4;
	line-height: 0.8;
	margin-top: -4px;
	margin-bottom: 4px
}
div.torrents .torrent_alt .category + td > * {
	display: inline-block;
	vertical-align: top
}
div.torrents table tr.torrent_alt .tags {
	width: calc(39% + 7px);
	float: right;
	overflow: hidden
}
div.torrents .torrent_alt .alt_title {
	line-height: 1.0
}
div.torrents span.tags a:link:hover,
div.torrents tr:hover div > a {
	color: #800090!important
}
div.torrents tr:hover span.tags {
	opacity: 1.0
}
#browse div.torrents table td:first-child:not(.name):not(.category) {
	padding: 4px 2px 0 20px
}
div.torrents table .files {
	width: 2em
}
div.torrents table th.size {
	padding-right: 8px
}
div.torrents table td.peers {
	position: relative;
	padding-right: 15px;
	right: 30px;
	text-align: right;
	width: 13ex;
	color: rgba(0, 0, 0, 0.3);
	font-size: 90%!important
}
div.torrents table td.peers > *:nth-child(1) {
	position: absolute;
	right: 4px;
	width: 25px;
	text-align: right;
	background: white
}
div.torrents table tr.alt1 td.peers > *:nth-child(1) {
	background: #f0f0f0
}
div.torrents table tr:hover td.peers > *:nth-child(1) {
	background: #EEDDFF
}
div.torrents table td.peers > *:nth-child(2) {
	position: absolute;
	right: -16px;
	width: 15px;
	text-align: right
}
div.torrents table .owner {
	text-align: left!important
}
div.torrents table tr.alt1 td.category {
	background: #fbfbfb;
	border-right: 0px
}
div.torrents table tr.alt1:hover td.category {
	background: #EEDDFF
}
div.torrents table td {
	padding: 8px 5px 0
}
div.torrents table td.category,
div.torrents table td.comments {
	text-align: right;
	padding-right: 4px!important
}
div.torrents table td.category div {
	height: auto;
	width: 100%
}
img.overlay {
	position: inherit;
	float: left;
	margin-left: 20px
}
.torrents tr > td:not(.category) img.icon {
	opacity: 0.5
}
.torrents tr:hover > td:not(.category) img.icon {
	opacity: 1.0
}
.category .icon {
	width: 20px;
	height: 20px;
	margin-right: 4px
}
.category .series { background: rgba(160, 176, 0, 0.4); border-radius: 10px }
.category .movie { background: rgba(234, 0, 0, 0.2); border-radius: 10px }
.category .ova { background: rgba(151, 0, 255, 0.15); border-radius: 10px }
.category .ost { background: rgba(255, 235, 0, 0.8); }
.category .liveaction { background: rgb(255, 235, 0); }
.category .h_ova { background: rgba(246, 0, 255, 0.15); }
.category .h_manga { background: rgba(246, 0, 255, 0.15); }
.category .manga { background: rgba(0, 191, 75, 0.25); border-radius: 10px }
.category .musicvideo { background: rgba(255, 123, 0, 0.3); }
.category .lightnovel { background: rgba(115, 152, 255, 0.3); }
.category .artbook { background: rgb(255, 235, 0); }
.category .series:after { content: "S"; color: rgb(160, 176, 0); margin-right: 1ex; margin-top: .25ex; display: block; }
.category .movie:after { content: "M"; color: rgb(234, 0, 0); margin-right: .75ex; margin-top: .25ex; display: block; }
.category .ova:after { content: "O"; color: rgb(151, 0, 255); margin-right: .75ex; margin-top: .25ex; display: block; }
.category .ost:after { content: "â™ª"; color: rgb(189, 152, 9); margin-right: .5ex; margin-top: -.3ex; display: block; font-size:14pt}
.category .liveaction:after { content: "LA"; color: rgb(234, 0, 0); margin-right: .35ex; margin-top: .25ex; display: block; }
.category .h_ova:after { content: "hO"; color: rgb(246, 0, 255); margin-right: .25ex; margin-top: .25ex; display: block; }
.category .h_manga:after { content: "hM"; color: rgb(246, 0, 255); margin-right: .25ex; margin-top: .25ex; display: block; }
.category .manga:after { content: "ma"; color: rgb(0, 191, 75); margin-right: .2ex; margin-top: .15ex; display: block; }
.category .musicvideo:after { content: "mv"; color: rgb(234, 0, 0); margin-right: .75ex; margin-top: .25ex; display: block; }
.category .lightnovel:after { content: "LN"; color: rgb(115, 152, 255); font-size: 85%; margin-right: .5ex; }
.category .artbook:after { content: "A"; color: rgb(234,0,0); margin-right: .8ex; margin-top: .25ex; display: block;}
}

@-moz-document regexp("^https?://bakabt\\.me/(torrent/\\d+/|details\\.php\\?id=).*?(\\.html$|)") {
#content {
	margin-top: 0;
	box-shadow: inset 0 20px 15px -20px black
}
#details h2 a:link {
	display: block;
	letter-spacing: 0;
	text-decoration: none;
	font-size: 80%
}
#details .comment_wrap {
	position: relative;
	margin: 0 -15px;
	border: none;
	border-bottom: 1px solid #ccc;
	width: calc(100% - 10px);
	padding: 8px 20px
}
#details .comment_wrap:nth-child(odd) {
	background: linear-gradient(to right, #e4e4e4 100px, #eaeaea 100px, #eaeaea 264px, #eeeeee 264px)
}
#details .comment_wrap:nth-child(even) {
	background: linear-gradient(to right, #f0f0f0 100px, #f4f4f4 100px, #f4f4f4 264px, #f8f8f8 264px)
}
#details .comment_wrap em {
	color: #8B9F96
}
#details .comment_avatar {
	width: 64px;
	padding-right: 20px
}
#details .comment_avatar img {
	max-width: 64px;
	max-height: 64px
}
#details img.default_avatar {
	visibility: hidden;
	height: 1px
}
#details .comment_header {
	width: 144px;
	float: left
}
#details .comment_header {
	line-height: 1.2
}
#details .comment_header h2 > span {
	display: none;
	padding-bottom: 0.5ex
}
#details .comment_body {
	width: 660px;
	text-align: left;
	font-size: 120%;
	margin-right: 16px
}
#details .comment_mod {
	right: 0.4rem;
}
#details .comment_mod a {
	border: 1px solid #ddd;
	background: #eee;
	line-height: 1.2rem;
	padding: 0 0.5rem;
	margin-bottom: 0.2rem;
	position: absolute;
	top: 0.5em;
	width: 3rem;
	text-align: center;
	font-size: 100%;
	float: none;
}
#details .comment_mod a.report_link {
	left: 0.25rem;
}
#details .comment_mod a.sublink {
	right: 0.5rem;
}
#details .comment_mod a.warning {
	right: 0.5rem;
	top: 2rem;
}
.content {
	background-color: #eee
}
.download_link,
.other-versions {
	border: none
}
.download_link {
	box-shadow: 1px 3px 10px rgba(0, 0, 0, 0.3);
	padding-top: 4px;
	padding-bottom: 4px
}
#description_iframe {
	margin: 10px -15px 0;
	border-top: 5px solid #ddd;
	width: calc(100% + 30px);
	background-color: white
}
#screenshots {
	background: transparent!important
}
.torrent_mod a:link {
	background: none
}
#tab_outer {
	margin: -5px -15px 0;
	width: calc(100% + 15px);
	padding-left: 15px;
	padding-right: 0px;
	border-top: 5px solid #ccc;
	border-bottom: 5px solid #ccc
}
#tab_content {
	background-color: transparent;
	width: 785px
}
#tab_menu {
	width: 145px;
	margin-right: 25px
}
#tab_menu ul {
	margin-top: 10px
}
#tab_outer li.selected a::after {
	content: "";
	position: absolute;
	left: 170px;
	width: 0;
	height: 0;
	border-style: solid;
	border-width: 8px 0 8px 16px;
	border-color: transparent transparent transparent red;
	z-index: 9999
}
#tab_menu ul li a {
	background: transparent
}
}

@-moz-document url-prefix("http://bakabt.me/description.php?id="), url-prefix("https://bakabt.me/description.php?id=") {
body,
#sizer {
	width: inherit;
	background-size: cover
}
.main {
	border: none
}
}
