css:
@-moz-document domain("youtube.com") {
  
/* bg color/image */  
html body {
background-image: url(http://puu.sh/gUohA/3e972b8984.png)!important;
 background-attachment: fixed;
}
  
/* main content colors */  
#watch-header, #action-panel-details, .branded-page-box, .yt-card, .yt-ui-ellipsis, .video-player-view-component .description {
  background-color: #333333 !important;
  color: #6d7393 !important;
}
  
/* remove seperators */
  #watch8-action-buttons, .yt-card .yt-uix-button-expander, .yt-card .yt-uix-tabs, #appbar-guide-menu .guide-section-separator, .branded-page-v2-primary-col .branded-page-box, .feed-header, #appbar-nav, #masthead-appbar, a.yt-uix-button-epic-nav-item:hover, #watch7-sidebar .watch-sidebar-separation-line, .search-header, .branded-page-v2-subnav-container {
    border-top: 0px solid !important;
    border-bottom: 0px solid !important;
}
  
/* first color */
#eow-title, #watch7-sidebar .video-list-item .title, #eow-description, .watch-view-count, .yt-lockup-title a, #appbar-nav .epic-nav-item-heading, .epic-nav-item-heading, .yt-uix-button-default, #watch-like:not(.yt-uix-button-toggled) .yt-uix-button-content {
  color: #6d7393 !important;
}  
  
/* secondary color */
a, li.video-list-item, .video-list .video-list-item .stat, #watch-header .yt-user-info a, .watch-time-text, #appbar-nav, .yt-uix-button-default:hover, .yt-uix-button-menu .yt-uix-button-menu-item, .yt-uix-button-group .start, .yt-uix-button-icon-wrapper + .yt-uix-button-content {
  color: #474c70 !important;
}    
  
 /* third color */
a:hover, #watch7-sidebar .video-list-item:hover .title, .yt-lockup-description {
  color: #434359 !important;
  text-decoration: none;
}  
  
/* hide buttons */  
.addto-button:before, .action-panel-trigger-share:before, #action-panel-overflow-button:before, .end {
  display: none;
}  
  
 /* video options share */
.yt-uix-form-input-checkbox-element, .yt-uix-form-input-text {
    border: 0px !important;
}  
 
 /* video options menu  */
  .yt-uix-menu-content, .yt-ui-menu-item {
    background-color: #333333 !important;
    border: 0px !important;
    color: #6d7393 !important;
  }
  
/* fonts */
body {
   font-family:  cure;
    font-weight: 450;
    font-size: 13px !important;
  }  
  
/* sub button */  
.yt-uix-button-subscribe-unbranded:hover, .yt-uix-button-subscribe-unbranded, .yt-uix-subscription-preferences-button, .yt-uix-button-subscribed-branded, .yt-uix-button-subscribed-branded[disabled], .yt-uix-button-subscribed-branded[disabled]:hover, .yt-uix-button-subscribed-branded[disabled]:active, .yt-uix-button-subscribed-branded[disabled]:focus, .yt-uix-button-subscribed-unbranded, .yt-uix-button-subscribed-unbranded[disabled], .yt-uix-button-subscribed-unbranded[disabled]:hover, .yt-uix-button-subscribed-unbranded[disabled]:active, .yt-uix-button-subscribed-unbranded[disabled]:focus, .yt-uix-button-subscribe-branded.yt-is-buffered, .yt-uix-button-subscribe-branded + .yt-uix-subscription-preferences-button + .yt-subscription-button-subscriber-count-branded-horizontal, .yt-uix-button-subscribe-branded {
    border: 1px solid #6d7393;
    background-color: #333333;
    color: #6d7393;
}
  
#watch-header {
    margin-top: 30px !important;
}  
  
/* like bar */
.video-extras-sparkbar-likes {
  background: #474c70 !important;
}  
  
#watch-like:before {
    background: url("http://i.imgur.com/KxkJKLZ.png") no-repeat scroll 0 -144px !important;
    width: 20px;
    height: 20px;
    opacity: 0.4 !important;
}  
  
.actionable #watch-like.yt-uix-button.yt-uix-button-toggled:before {
    background: url("http://i.imgur.com/KxkJKLZ.png") no-repeat scroll 0 -144px !important;
    width: 20px;
    height: 20px;
    opacity: 0.7 !important;
}    
  
/* dislike bar */  
.video-extras-sparkbar-dislikes  {
  background: #6d7393 !important;
}

#watch-dislike:before {
    background: url("http://i.imgur.com/KxkJKLZ.png") no-repeat scroll 0 -175px !important;
    width: 20px;
    height: 20px;
    opacity: 0.4 !important;
}    
  
.actionable #watch-dislike.yt-uix-button.yt-uix-button-toggled:before {
    background: url("http://i.imgur.com/KxkJKLZ.png") no-repeat scroll 0 -175px !important;
    width: 20px;
    height: 20px;
    opacity: 0.8 !important;
}      
  
/* remove theatre bg */  
.watch-stage-mode #theater-background, #theater-background {
    background-color: transparent !important;
}  
  
/* header */
#yt-masthead-container {
    background-color: #333333;
    border-bottom: 0px;
    padding-top: 7px;
    padding-bottom: 8px;
}  
  
/* search bar */
#masthead-search-terms input {
    border: 0px solid #6d7393;
    background: #333333;
}  
  
/* buttons */
#masthead-search-terms, #masthead-search .search-btn-component, .yt-uix-button-default {
  border: 1px solid #6d7393;
  background-color: #333333;
}
  
/* remove search button and region logo */  
#masthead-search .search-btn-component .yt-uix-button-content, #masthead-search .search-btn-component, #yt-masthead #logo-container span.content-region, #appbar-guide-button .yt-uix-button-arrow {
  display: none;
}  
  
/* guide */
#masthead-positioner:hover #appbar-guide-button, #guide-container, .guide-item:hover {
    border: none;
    background: #333333;
}  

#yt-masthead-user .yt-masthead-user-icon {
  border-radius: 0px !important;
}
  
#appbar-guide-button {
  margin-top: 2px;
  margin-left: 0px;
}  
  
/* custom yt logo */  
#logo, #yt-masthead #logo, #yt-masthead #logo-container.doodle img {
  background: url("http://puu.sh/gaqDP/9ca4316e05.png") no-repeat scroll !important;
  margin-top: -6px;
  margin-left: -60px;
  width: 80px;
}  
  
/* remove google a+ bell */
#yt-masthead-user #sb-button-notify {
  display: none;
}  
  
/* fix white bg of areas */
.yt-lockup-description, .yt-lockup-title a, #appbar-nav {
  background: #333333;
}

/* heading  selected  */
.epic-nav-item-heading {
    border-bottom: 0px !important;
}  
  
 /* fixes description ellipsis */
.yt-lockup-tile .yt-lockup-description {
    max-width: 229px;
}  
  
 .yt-ui-ellipsis:before {
    display: none;
} 
  
/* footer */
body #footer-container, #footer-main {
    background-color: #333333;
    border-top: 0px;
    border-bottom: 0px;
}  

#footer .yt-uix-button-group .yt-uix-button:hover, #footer .footer-history:hover, #footer #google-help:hover, .yt-uix-button-default:hover, .yt-uix-button-menu {
  background-color: #333333;
  border: 1px solid #6d7393;
}  
  
 /* slider buttons */
.yt-uix-shelfslider .yt-uix-shelfslider-next, .yt-uix-shelfslider .yt-uix-shelfslider-prev {
   background-color: #333333;
  border: 1px solid #6d7393;
}  

/* hide this video dropdown menu */  
.yt-section-hover-container .yt-uix-button-action-menu   {
  opacity: 0 !important;
}  

  .yt-section-hover-container:hover .yt-uix-button-action-menu   {
    opacity: 0.9 !important;
  }
  
/* ytc menu */
 .ytcenter-dialog-base .ytcenter-dialog-header h2, .ytcenter-settings-subcat-header-wrapper, .ltr .ytcenter-settings-panel-left, .ytcenter-settings-subcat-content-wrapper, .ytcenter-settings-subcat-header, .ytcenter-dialog-base .ytcenter-dialog-header, .yt-horizontal-rule, .ytcenter-settings-content, .ytcenter-settings-option-label, #masthead-user-button {
   background-color: #333333;
  border: 0px !important;
  color: #6d7393;
  border-bottom: 0px !important;
 }
  
 .ytcenter-settings-category-list .ytcenter-settings-category-item.ytcenter-selected {
     color: #6d7393;
     background-color: #1b1b1b;
     text-shadow: none;
} 
 
/* hides description of videos on feed */
.yt-lockup-description {
  display: none;
}  
  
/* autohides header */  
#masthead-positioner {
 /* margin-top: -95px !important;*/
  margin-top:  10px !important;
  /*padding-bottom: 80px !important;*/
  background: transparent !important;
}  
  
/*#masthead-positioner:hover {
  margin-top: 10px !important;
  background: transparent !important;
  padding-bottom: 10px !important;
}     */
  
.branded-page-v2-col-container {
   margin-top:  0px !important;
}  
  
#yt-masthead-container {
  height: 33px !important;
  width: 1005px !important;
  margin: 0 auto; 
}  
  
#appbar-nav {
  width: 1065px !important;
  margin: 0 auto; 
}  
  
#appbar-content { 
  margin-top: 5px !important;
}
  
/* removes ellipsis */
 .yt-ui-ellipsis:before {
  display: none;
}  
  
#masthead-appbar {
  background: transparent !important;
}  
  
/* custom app logo */  
.yt-uix-button-icon-appbar-guide, .yt-uix-button-icon-appbar-guide:hover, .show-guide .yt-uix-button-icon-appbar-guide {
    background: url("http://puu.sh/gaqFN/c82846240a.png") no-repeat scroll;
    width: 16px;
    height: 16px;
   margin-top: -5px;
   margin-left: -10px;
}    
  
.watch.watch-non-stage-mode #player.content-alignment {
  margin-top: 15px !important;  
}  
  
/* player size in theatre mode */  
.watch-stage-mode #player-api {
 /* width: 1050px;
  height: 589px;*/
  margin-top: 20px !important;
  margin:0 auto; 
}

/* remove this if not using YTC */
#watch7-sidebar-contents {
  margin-top: -10px;
}
  
#watch7-content {
  margin-top: -20px;
}  
/* ~~~~~ */
  
/* fade header icons */  
.guide-item .thumb, #yt-masthead-user .yt-masthead-user-icon, #masthead-user-button {
  opacity: 0.4 !important;
}  
 
/* removes new author link shit wtf yt why */
.feed-item-container .branded-page-module-title .branded-page-module-title-link, .ytcenter-gridview .ytcenter-grid-subscriptions-username	{
   display: none !important;
}	 
  
/* hide upload button */  
#appbar-onebar-upload-group #upload-btn {
  border: 1px solid #333333;
  background-color: #333333;
   color: #333333 !important;
} 
#appbar-onebar-upload-group #upload-btn:hover {
  border: 1px solid #6d7393;
  background-color: #333333;
   color: #6d7393 !important;
}  

/* changes color of time on videos */  
.video-time {
    margin-left: 30px !important;
    font-size: 11px;
    background-color: #000 !important;
    color: #6d7393 !important;
    opacity: 0.95;
}    
  
/* comment section (ie. yt why so many selectors?) */
}
@-moz-document regexp("https://apis.google.com/u/[0-9]/(.*)/_/widget/render/comments\\?usegapi=1&first_party_property=YOUTUBE&href=(.*)"), regexp("https://plus.googleapis.com/(.*)/widget/render/comments\\?usegapi=1&first_party_property=YOUTUBE&href=(.*)") {
.yJa, .BJa, .mj, .dn, .Jj, .ve.oba .o-U-s, .Aq, .vy, .bmd, .xN .d-y-r-c-ha, .DJa, .lohp-medium-shelves-container .lohp-video-link {
  background: #333333 !important;
  color: #6d7393 !important;
  font-family:  cure;
    font-weight: 450;
    font-size: 13px !important;
}
.r3 .r0 {
  background: #333333 !important;
  border: 1px solid #6d7393 !important;
  color: #474c70 !important;
  font-family: cure;
    font-weight: 450;
    font-size: 13px !important;
}  
/* link color */  
.gna, .ve.oba .d-s, .via .TD, .Sea .K5, .g9 .Xa.va .ot-hashtag, .proflink, .ot-hashtag, .Xa .ot-anchor {
  color: #474c70 !important;
  font-family: cure;
    font-weight: 450;
    font-size: 13px !important;
}  
/* top comments dropdown */
.xN.d-y-r-c, .E5 {
  background: #333333 !important;
  color: #6d7393 !important;
  border: 1px solid #6d7393 !important;
}  
.xN .d-y-r-c-Qa, .Mga, .Jea {
  display: none;
}  
/* like/dislike selected */
.MJa.JMc {
background: no-repeat url(http://i.imgur.com/G39kb8k.png) 0 -186px !important;
}
.LJa.Zld {
background: no-repeat url(http://i.imgur.com/G39kb8k.png) 0 -204px !important;
}
/* like/dislike not selected */
.MJa {
background: no-repeat url(http://i.imgur.com/KxkJKLZ.png) 0 -144px !important;
}
.LJa {
background: no-repeat url(http://i.imgur.com/KxkJKLZ.png) 0 -175px !important;
}     
}
