@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("tagged.com") {

/* 2014-02-03 */
/* style by Kaligula */
/*
I try to update it as soon as they change Facebook.
Because it's meant for me and syncing with my brother so I also want this to be up to date.
You want sth – shout. :)
_______________________________________
I:
- use Chrome
- have 1280 wide screen
and do not know if other browsers/resolutions will look the same. If I had time…
*/

* {
background-color: #000 !important;
color: #b1b1b1 !important;
border-color: #333 !important;
}
#pagelet_group_, #pagelet_group_ *,
#contentArea, #contentArea *,
#content, #content *,
#photos_snowlift * {
  background-color: transparent !important;
}

a, a * {
color: #555 !important;
}

#pageLogo, #pageLogo * {
 background-color: rgba(0,0,0,0) !important;
}

#pageLogo a i,
#pageLogo a img {
 display: none !important;
}

div[id^="blueBar"],
div[id^="blueBar"]:after {
 background-image: none !important;
}

#headNav, #headNav * {
 background-color: rgba(0,0,0,0) !important;
}
 #fbRequestsFlyout, #fbMessagesFlyout, #fbNotificationsFlyout {
  background-color: black !important;
 } 
#headNav #navSearch .uiTypeahead {
 background-color: white !important;
}

/*
| photos and maps
\_______________________________________________*/

#photos_snowlift > div,
#photos_snowlift > div > div,
.fbPhotoSnowliftContainer,
/*.fbPhotoSnowliftOuter,
.fbPhotoSnowliftInner,
*/.fbPhotoSnowliftPopup,
.fbPhotoSnowlift .rhc,
.stageWrapper, .stage, .videoStage,
.stage *,
.stage ~ * {
 background: transparent !important;
}
#photos_snowlift {
 background-color: rgba(0,0,0,0.9) !important;
}

.fbPhotoSnowlift.pagingActivated .snowliftPager,
.fullScreenSwitch {
opacity: .25;
}

.fbPhotoSnowlift .snowliftFixedPagers .snowliftPager:hover, .fbPhotoSnowlift.pagingActivated .snowliftPager.hilightPager,
DIV#fbxPhotoContentContainer SPAN.like {
opacity: 0.6 !important;
}

DIV#fbxPhotoContentContainer SPAN.like:hover {
opacity: 1 !important;
}

.uiScrollableArea.nofade .uiScrollableAreaWrap, .uiScrollableArea.fade .uiScrollableAreaWrap {
 padding-right: 35px; /* original 30px */
}

#photos_snowlift .tagContainer {
 border: none;
}
.fbPhotoTagger, .fbPhotoTagger * {
background-color: transparent !important;
}
.fbPhotoTagger .typeaheadBackdrop, .fbPhotoTagger .uiTypeaheadView {
background-color: black !important;
}

.fbPhotoSnowlift.pagingActivated .snowliftOverlayBar, .fbPhotoSnowlift.taggingMode .snowliftOverlayBar {
opacity: .6;
}

DIV.fbPlaceFlyoutMapArrow,
DIV.fbPlaceFlyoutMapArrow *,
DIV.tagPointer * {
  background-color: transparent !important;}
DIV#fbPhotoSnowliftTagBoxes DIV.tagPointer DIV.tagName {
  background-color: rgba(0,0,0,0.5) !important;}

/* thumbnails in albums fix */
._6ia *,
._53s * {
background-color: transparent !important;
color: white !important;
}

/* photo in a conversation */
.generic_dialog_modal,
.generic_dialog_fixed_overflow {
  background-color: rgba(0, 0, 0, .9) !important;}
DIV.pop_container_advanced,
DIV.pop_content,
DIV.dialog_content,
DIV.dialog_body,
DIV.dialog_body > DIV {
  border: none !important;
  background: transparent !important;}

/* fixed adding photos to message when sending from recipient's Wall */
.mal._3mf, .mal._3mf * {
background-color: transparent !important
}


/*
| fixing the window showing who shared the post
\_______________________________________________*/

BODY > DIV.uiOverlayPageWrapper.shareOverlay,
body > DIV.uiLayer[role="dialog"] {
background-color: rgba(0,0,0,.7) !important;
}

BODY > DIV.uiOverlayPageWrapper.shareOverlay > .uiOverlayPageInner,
BODY > DIV.uiOverlayPageWrapper.shareOverlay > DIV.uiOverlayPageInner > DIV.uiOverlay {
background-color: transparent !important;
}


/*
| fixing unread msg layout
\_______________________________________________*/

.uiScrollableArea li.jewelItemNew a.messagesContent,
.uiScrollableArea li.jewelItemNew a.messagesContent *,
.threadRow.unread *,
._4af,
._4af *,
#pagelet_web_messenger ._kx,
#pagelet_web_messenger ._kx * {
background-color: #222 !important;
}

/* button to insert img to chat */
.fbNub form, .fbNub form * {
background-color: transparent !important;
}

/* chat msgs no bg */
._1nc7 ._5w1r, ._1nc7 ._5w1r:before, ._1nc7 ._5w1r:after, ._1nc6 ._5w1r, ._1nc6 ._5w1r:before, ._1nc6 ._5w1r:after {
background-image: none !important;
background-color: black !important;
}
._1nc7 ._5w1r:before, ._1nc7 ._5w1r:after, ._1nc6 ._5w1r:before, ._1nc6 ._5w1r:after {
width: 0 !important;
}
/* bg of avatars in chat */
._5ys_:after {
background-image: none !important;
}

/* background of popup images sent in conversation */
body > ._n8 {
 background-color: rgba(0,0,0,0.8) !important;
}
body > ._n8 * {
 background-color: rgba(0,0,0,0) !important;
}
body > ._n8  ._n3 {
 background-color: black !important;
}

/* searchbox search icon bg */
#pagelet_web_messenger span.uiSearchInput button {
 background-color: #333 !important;
}

#ChatTabsPagelet .conversation .fbChatConvItem .profileLink:after {
  background-image: none;
  display: none;
}

.fbNubButton, .fbNubButton:after, .fbNubButton:before,
.fbNubFlyoutTitlebar {
 background-image: none !important;
}

/*
| notifications color  #111
\_______________________________________________*/

DIV#fbRequestsFlyout DIV.jewelFooter:hover,
DIV#fbRequestsFlyout DIV.uiStickyArea:hover,
DIV#fbRequestsFlyout DIV.fbRequestList:hover,
DIV#fbRequestsFlyout DIV.jewelFooter:hover *,
DIV#fbRequestsFlyout DIV.uiStickyArea:hover *,
DIV#fbRequestsFlyout DIV.fbRequestList:hover *,
DIV#fbMessagesFlyout > LI:hover,
DIV#fbMessagesFlyout > LI:hover *,
DIV#fbMessagesFlyout > LI.selected *,
DIV#fbMessagesFlyout DIV.jewelFooter:hover,
DIV#fbMessagesFlyout DIV.jewelFooter:hover *,
.notifNegativeBase #fbMessagesFlyout .jewelContent a.messagesContent:hover,
.notifNegativeBase #fbMessagesFlyout .jewelContent a.messagesContent:active,
.notifNegativeBase #fbMessagesFlyout .jewelContent a.messagesContent:focus,
DIV#fbNotificationsFlyout UL LI:hover,
DIV#fbNotificationsFlyout UL LI:hover *,
DIV#fbNotificationsFlyout UL LI.selected *,
DIV#fbNotificationsFlyout DIV.jewelFooter:hover,
DIV#fbNotificationsFlyout DIV.jewelFooter:hover * * {
background-color: #222 !important;
}

UL#fbMessagesItemList,
UL#fbNotificationsList {
padding: 0 !important;
}


/*
| buttons' background
\_______________________________________________*/


.uiButton,
.uiButtonSuppressed:active,
.uiButtonSuppressed:focus,
.uiButtonSuppressed:hover,
.uiSelectorNormal .uiButtonOverlay,
.uiSelectorNormal .uiButtonOverlay:active,
.uiSelectorNormal .uiButtonOverlay:focus,
.uiSelectorNormal .uiButtonOverlay:hover,
.uiSelectorNormal .uiSelectorButton:active,
.uiSelectorNormal .uiButtonSuppressed,
.uiButtonConfirm,
._42me, ._42me:hover
 {background-image: none !important;}

#globalContainer .uiButtonGroup {
 background-color: black !important;
}

.comment_like_button
 {padding: 4px !important;}

/*.uiSearchInput button
 {
 background-image: url(https://s-static.ak.fbcdn.net/rsrc.php/v2/yw/x/l6qufrBtvhv.png) !important;
 background-position-x: 0px !important;
 }*/

#contentArea div[role="menu"] ul li {
 background-color: black !important;
}


/*
| timeline
\_______________________________________________*/

DIV#fbProfileCover > DIV.cover A > * {
background-color: transparent !important;
}

DIV#pagelet_timeline_nav * {
background-color: transparent !important;
}

.fbTimelineCapsule .bottomBorder,
.fbTimelineCapsule .topBorder {
background-image: none !important;
background-color: #333 !important;
}

/* few friends list on timeline */
.fbTimelineFriendsBoxLarge .friendName {
background:-webkit-linear-gradient(top, transparent, rgba(0, 0, 0, .8)) !important;}
.fbTimelineFriendsBoxLarge .friendName .mas.name {
background: transparent !important;}

/* profile popup if sb has timeline */
.HovercardStage *
  {background-image: none !important;}

.coverOverlaidText,
.coverOverlaidText *,
.HovercardContent,
.HovercardContent *,
._7lt * {
  background-color: transparent !important;}

.CoverAreaGradient
  {background:url(https://s-static.ak.fbcdn.net/rsrc.php/v2/yG/r/8RHv8nDQAHL.png) repeat-x left top !important;}


/*
| OTHER
\_______________________________________________*/

/* popup after hover on profile pic
and group photos over the group page */
.uiOverlay.uiContextualDialog.uiOverlayArrowTop,
.groupsCoverPhoto.fbTimelineSection, .groupsCoverPhoto.fbTimelineSection * {
background: transparent !important;
}


/* sidebar fbChat (TURNED OFF)
#pagelet_sidebar > .fbChatSidebar > * {
border-color: #333 !important;
}*/


/* poll */
.pollResultsBar .label {
line-height: 15px !important;
}

.pollResultsBar .fbQuestionsPollClickTarget {
background: transparent !important;
}

.commentActions.fsm.fwn.fcg * {
color: #333 !important;
}

/* fix: if sb uses my both fb styles (black and without garbage) */
.storyContent {
margin-top: 1px !important;
}

/* calendar */
#contentArea .fbCalendarGrid .fbCalendarGridToday {
background-color: #fdfad3 !important;
}

.fbCalendarGrid .fbCalendarGridToday .fbCalendarItemDay::after,
.fbCalendarGrid .fbCalendarItemDay::after {
background-image: none !important;
}

/* msgbox */
#pagelet_web_messenger, #pagelet_web_messenger * {
background-color: transparent !important;
}

/* rightCol fix */
.rightCol {
    border: none !important;
}
.groupsSideMargin, .groupsSideMargin ~ *,
#pagelet_rhc_footer, .groupsFileBox {
    display: none !important;
}

/* new post (image) image upload bar fill fix */
#contentArea .uiProgressBar .fill {
    background-color: #6d84b4 !important;
}

/* .UFIAddCommentWithPhotoAttacher background FIX */
.UFIAddCommentWithPhotoAttacher .innerWrap {
    padding-right: 0 !important;
}
.UFIAddCommentWithPhotoAttacher:hover textarea,
.UFIAddCommentWithPhotoAttacher textarea:focus,
#content .UFIAddCommentWithPhotoAttacher:hover textarea,
#content .UFIAddCommentWithPhotoAttacher textarea:focus,
#contentArea .UFIAddCommentWithPhotoAttacher:hover textarea,
#contentArea .UFIAddCommentWithPhotoAttacher textarea:focus {
    background-color: #444 !important;
}
.UFIPhotoAttachLinkWrapper:hover .UFICommentPhotoIcon,
#content .UFIPhotoAttachLinkWrapper:hover .UFICommentPhotoIcon,
#contentArea .UFIPhotoAttachLinkWrapper:hover .UFICommentPhotoIcon {
    background-color: #FFF !important;
}

}
