css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("imdb.com")

{

/*text&background*/

html, body, tbody, thead, th, tr, td, blockquote, h1, h2, h3, h4, h5, h6, p, footer, small, #pagecontent, .article, .aux-content-widget-2, .aux-content-widget-3, .news_item, .list-preview, #footer, #bottom_ad_wrapper, .list_item, .active, .listo ul.list_tabs li.active a, .showtimes, .tv_nav_container, .tv_bucket, .tv_grid, .tv_bucket2, .discuss .threads .odd, .discuss .threads .even, .discuss textarea.post, div.showtime, div.faq div, #navbar #nb_search #navbar-suggestionsearch a, .amazon-affiliate-site-desc, div.infobar, .list_meta, .star-box-rating-label, .mellow, .star-box-details, #overview-top div.txt-block span, table.cast_list td.character div, #navbar-query

{
color: white !important;
background: black !important;
border: black !important;
}

button, .action-box .disabled, .action-box .linkasbutton-primary, .action-box button.secondary, .action-box .linkasbutton-secondary, #navbar ul.main_nav li a, .swiki .history, div#meta, div#photo-caption, .odd, .even, .head, #navbar, div.rec-jaw-upper, div.rec-jaw-lower, div.rec-jaw-teeth, div#nb20, div.quicksearch_dropdown_wrapper

{
background: black !important;
}

div, span, a

{
background-color: black !important;
}

select, input, span, code, textarea, pre, label, div, dd, option

{
color: white !important;
}

/*links*/

a

{
color: #00FF00 !important;
}

a:visited

{
color: #009900 !important;
}

/*highlight*/

textarea, pre, .btn, .listo ul.list_tabs a, .newsWidget ul.newsTabs a, .social_networking, #navbar #nb_search #navbar-suggestionsearch a:hover

{
background: #333333 !important;
}

#navbar select:hover

{
background: url("http://www.imdb.com/images/navbar/navbar_sprite.png") no-repeat scroll -25px -47px transparent !important;
}

/*imgbgreset*/

.tv_logo img

{
background: white !important;
}

}
