 #FBC, rgba(85,85,85,.7) 5%, rgba(85,85,85,.7)95%, #222)!important}



OL.dict.vk_c.vk_bk,DIV#cwmcwd.vk_c,#rhs .vk_rhsc{box-shadow: none!important;}



