css:
@-moz-document url("https://www.google.com/webhp?sourceid=chrome-instant&ion=1&espv=2&es_th=1&ie=UTF-8#es_th=1&q=result"), url-prefix("http://google.com/webhp"), url-prefix("https://google.com/webhp"), domain("www.google.com") {
/* Sets the text color of an unclicked link */
a:link, a.f1 a {
 color: #3FCCE5 !important;
}

/* Sets the text color of a link you have already visited */
a:visited, .sbpqs_a {
 color: #336CD4 !important; 
}

/* Sets the text color of the URL in each search result, as well as the source (i.e. USA Today) */
cite, ._Rm { 
 color: #24DA5F !important; 
}

/* Sets the text color of the description of each search result */
span.st {
 color: #ffffff !important; 
}

/* Sets the color of matched words from your search */
.st em {
 color: #FFD531 !important; 
}

/* Sets various parts of the screen to have a black background */  
._Fmb, ._Fmb:hover, ._Fmb.selected, ._Fmb.selected:hover, #cnt, #topabar, #hdtbSum, div.sfbgg {
  background-color: black !important;
}

/* Sets various parts of the screen to have a black background */  
#fbar, div#viewport, #hdtbSum {
 background: #000000 !important;    
}

/* Sets the text color of the page # you are on in the G O O O O O O G L E section at the bottom */
.cur {
 color: white !important;
}
  
.f, .f a:link {
  color: #6FBDB4 !important;
}

/* Sets each search result to have a black background and a default white font */    
li {
 background: #000000 !important;
 font-color: white !important;
}

/* This should remove any Google ads from the page */
.ads-ad {
 display: none !important;
}

/* Sets the color of the search dropdown text, among others */
body {
  color: #30D2BF !important;
}
}
