css:
@-moz-document regexp("https://www\\.eldarya\\.(com|fr|de|es|hu|it|pl|ru|com.br).*") {
/*Ezarel en familier*/  
 
  /*Images - pictures*/
 #pet-headerPortrait-base {
   background: url("https://nsm07.casimages.com/img/2017/12/12//17121210331510339315411617.png") -45px 5px no-repeat !important;
  background-size : auto 145px !important ;
  }

  #pet-portrait {
    background : url("https://nsm07.casimages.com/img/2017/12/12//17121210331510339315411617.png") center no-repeat !important ;
    background-size : auto 450px !important ;
    height : 450px !important ;
  }

  #pet-portrait > img[src*="/static/img/pet/icon/"] {display : none !important ;}

    /*Infos*/

  #pet-info-name:before {
    content : "Ezarel" !important ;
    font-size : 20px !important ;
  }
  
  #pet-info-name {
    font-size : 0 !important ;
  }  
  
  #pet-info-evolve > .on {font-size : 0 !important ;}
  
  .pet-info-evolve-icon {font-size : 13px !important ;}
  
  .pet-info-evolve-icon:after {
    content : " Ezarel est au maximum de sa servitude. N'espère pas plus !" ;
  }
  
  
  #pet-info-racegender:before {
    content : "Elfe " !important ;
    font-size : 16px !important ;
    line-height : 20px !important ;
  } 
  
  #pet-info-racegender {
    font-size : 0 !important ;
    margin-top : 5px ;
  }  
  
  img[src*="/static/img/f.png"] {
    background : url("http://www.eldarya.fr/static/img/m.png") !important ;
    padding-left : 14px !important ;
    padding-top : 15px !important ;
    width : 0 !important ;
    height : 0 !important ; 
    margin-bottom : 5px !important ;
  }
 
 /*Nourriture - food*/
  
 #pet-info-food > img {
  background : url("http://nsm08.casimages.com/img/2015/04/02//15040204041710339313132918.png") !important ;
  background-size : 75px 75px !important ;
  margin-top : -5px !important ;
  padding-left : 75px !important ;
  padding-top : 75px !important ;
  width : 0 !important ;
  height : 0 !important ;
} 
  
  #pet-info-food > div {font-size : 0 !important ;}
  
  #pet-info-food > div:before {
    content : "Gaufres au miel " !important ;
    font-size : 16px !important ;
  }
  
  #pet-info-food > div > span {font-size : 16px !important ;}

  /*Ezarel sur la page du profil*/

  #player-display-pet {
    background : url("http://img4.hostingpics.net/pics/164775Ezarelmaid.png") no-repeat ;
    background-size : auto 100% ;
    height : 250px !important ;
    width : 195px !important ;
  }

  #player-display-pet > img {display : none !important ;}
}
