css:
/* it begins */

@-moz-document url-prefix("http://www.myth-weavers.com") {

@media (max-width:360px){

	html{
		width:320px!important;
		height:100%;
	}

	body{
		background:#A95;
		font-size:1em;
		width:100%!important;
		margin: 0 auto;
	}

	body a {
		font: 14px "Times New Roman",Times,serif;
	}

	.lastpostinfo a,
	.lastpostinfo,
	.forumdescription {
		font-size: 15px!important;
	}

	.lastpostinfo {
		margin: 10px 0px 0px 35px!important;
	}

	#headerContainer {
		background:#530;
		height: auto;
		margin-bottom: 0!important;
		z-index: 50;
	}

	#headerShadow,
	#headerInner  {
		width: 100%!important;
		background: none!important;
		height: auto!important;
		margin:0!important;
	}


	#headerInner #searchform {
		float:none!important;
	}    

	#headerInner #logo img,
	#headerInner #logo {
		clear: both;
		margin:0 auto;
		display:block;
		top:0;
		left:0;
		position: relative!important;
	}

	#headerInner #social{
		float:none!important;
	}

	#headerInner #navbar {
		position: relative!important;
		height: auto!important;
		width: 100%;
	}

	ul#menu_bar {
		display: block;
		height: auto;
		margin: 0;
		vertical-align: top;
		width: 100%;
	}

	ul#menu_bar li:hover,
	ul#menu_bar li {
		color: #faecae;
		float: none !important;
		line-height: 25px;
		text-align: center;
		width: 100%!important;
		background: none!important;
		margin:0 auto!important;
		padding:0!important;
	}

	ul#menu_bar li a,
	ul#menu_bar li a:hover {
		color: #faecae;
		display: block!important;;
		font: 1em tahoma,verdana,geneva,lucida,"lucida grande",arial,helvetica,sans-serif!important;
		padding: 0;
		width: 100%!important;
		margin:0 auto;
		background:none!important;
	}

	#menu_bar ul li:hover,
	#menu_bar ul {
		background: none;
		padding: 0px 0px 20px;
		margin-left: 0;
		width: 100%;
		position: relative!important;
		overflow: visible;
		top:unset!important;
		background:#750;
	}


	#menu_bar ul li:hover {
		display: block;
	}


	ul#menu_bar li {
		line-height: 26px;
		color: #FAECAE;
		float: none!important;
		text-align: center;
	}

	ul#menu_bar li#timenow,
	ul#menu_bar li#notification_area {
		text-align: center!important;
		margin: 0!important;
		padding: 0!important;
	}

	.page div .last ul li {
		margin: 10px 0!important
	}

	#threadslist div {
		text-align: left;
		vertical-align: middle;
		width: 100%!important;
		display: block!important;
		position: relative!important;
		margin-left: 0px!important;
		right:unset!important;
		font-size:15px!important;
	}

	ul#threadslist li {
		border-bottom: 1px solid #DED8C6;
		padding: 30px 5px!important;
		position: relative;
		clear: both;
	}

	.smallfont a[href*="showthread"], 
	.smallfont a[href^="showthread"] {
		font-size:18px!important;
	}

	#threadslist span {
		display: inline-block;
		vertical-align: middle;
		text-align: left;
		font: bold 18px times,"Times New Roman",serif;
		color: #555;
		width: 100%!important;
		float: none!important;
		margin: 0!important;
		padding: 10px 0!important;
	}

	#threadslist div.smallfont a,
	#threadslist div.smallfont {
		font:13px tahoma,verdana,geneva,lucida,"lucida grande",arial,helvetica,sans-serif
	}

	#threadslist div.smallfont span{
		display: inline!important;
		width: auto!important;
	}

	a[id^="thread_title_"],
	a[id^="thread_title_"] {
		font-size:24px!important;
	}

	div[id^="td_threadstatusicon_"], 
	div[id*="td_threadstatusicon_"],
	div[id^="td_threadstatusicon_"] img, 
	div[id*="td_threadstatusicon_"] img {
		display:inline!important;
		width:auto!important;
	}

	#menu_bar ul li {
		width:100%!important;
	}

	ul#menu_bar li#login {
		margin: 20px 0;
	}

	ul#menu_bar li#login a {
		display: block !important;
		width: auto!important;
		float: none!important;
	}

	.bigtitle {
		background-size: contain!important;
	}

	.span-4 ins {
		overflow: hidden!important;
		width:280px!important;
	}

	.page {
		padding: 0 5%!important;
		width:90%!important;
	}

	form{
		overflow:hidden;
	}

	.tcat {
		height:110px!important;
		background:#530!important;
		padding: 0 5px!important;
	}

	.postHeader{
		padding-bottom:50px!important;
	}

	.postContent {
		padding: 12px 0;
	}
	    
	    .postContent img {width:100%!important}

	.userMenu .dropdown {
		padding:0!important;
		margin:0!important;
	}
	.userMenu .dropdown > li {
		padding:5!important;
		margin:0!important;
	}

	.postFooter {
		position: relative;
		margin-left: 0;
	}
	.postControlsTop {
		position: absolute;
		bottom: 0px;
		right: 0px;
		height: 26px;
		overflow: hidden;
	}

	#qrform table div div,
	#qrform table div[style] {
		width:100%!important;
		max-width: 100%!important;;
	}

	#vB_Editor_QR_controls{
		width: 300px;
	}
	#vB_Editor_QR_textarea {
		height: 300px!important;
		width: 300px!important;
	}

	ins {
		overflow: hidden!important;
		width: 280px!important;
	}

	div.controlbar textarea{
		height: 300px;
	}
	.postFooterRule, .signature {
		margin-right: 167px;
		font-size: 10px;
	}

	textarea, .bginput {
		font: 14pt tahoma,verdana,geneva,lucida,"lucida grande",arial,helvetica,sans-serif;
	}

	.postControlsBottom {
		right: 0px;
		position: absolute;
		top: -15px;
		font-size: 14px;
	}

	.page last a img{
		width:100%!important;
		padding: 0;
		margin: 20px 0!important;
	}    

	.append-2 {
		padding-right: 0px!important;
	}
	.span-5,
	.span-4,
	.span-3,
	.span-2,
	.span-1 {
		width: 100%!important;
	}

	.aswift_1_anchor,
	.aswift_1_expand {
		width:100%!important;
	}

	#collapseobj_forumhome_activeusers .alt1 {
		width:230px!important;
	}

	#collapseobj_forumhome_activeusers .alt1 .smallfont div {
		width:200px!important;
		white-space: normal!important;
	}

	.thead {
		height:auto!important;
		background: #530!important;
		color:wheat!important;
		padding: 5px!important;
	}

	.thead div {
		position: relative!important;
		color: wheat;
		left: unset!important;
	}

	li div.smallfont.lastpostinfo a {
		white-space: unset!important;
	}
	.buttonbar {
	height:35px!important;
	}
	.postTime a,
	.postTime {
		font-size: 10px!important;
		line-height:10px!important;
	}
	.postTime{
		top:2px!important;
		right: 2px!important;
	}
	}

}
