css:
@-moz-document domain("blockland.us") {
/* Normal, standard links. */
a:link, a:visited
{
	color: #005177;
	background-color: transparent;
	text-decoration: none;
}
a:hover
{
	color: #cc3333;
	background-color: transparent;
	text-decoration: none;
}

/* Navigation links - for the link tree. */
.nav, .nav:link, .nav:visited
{
	color: #000000;
	background-color: transparent;
	text-decoration: none;
}
a.nav:hover
{
	font-weight: bold;
	color: #cc3333;
	background-color: transparent;
	text-decoration: underline;
}

/* Tables should show empty cells too. */
table
{
	empty-cells: show;
}

/* By default (td, body..) use Verdana in black. */
body, td, th
{
	color: #000000;
	font-size: small;
	font-family: verdana, arial, helvetica, sans-serif;
}

body
{
	background: #FFFFFF;
}

/* Input boxes - just a bit smaller than normal so they align well. */
input, textarea
{
	font-size: 9pt;
	color: #000000;
	font-family: verdana, arial, helvetica, sans-serif;
	background-color: #afc6db;
}

/* Checkboxes shouldn't have a background color. */
input.check
{
	background-color: transparent;
}

/* Selects are a bit smaller, because it makes them look even better 8). */
select
{
	font-size: 8pt;
	font-weight: normal;
	color: #000000;
	font-family: verdana, arial, helvetica, sans-serif;
	background-color: #afc6db;
}

/* Standard horizontal rule.. */
hr
{
	color: #6394bd;
	background-color: transparent;
}
/* A more colorful hr.. */
.hrcolor
{
	height: 1px;
	border: 0;
	color: #6394bd;
	background-color: #6394bd;
}

/* A quote, perhaps from another post. */
.quote
{
	color: #000000;
	background-color: #dee7ef;
	border: 1px solid black;
	margin: 1px;
	padding: 1px;
	font-size: x-small;
}

/* A code block - maybe even PHP ;). */
.code
{
	color: #000000;
	background-color: #cccccc;
	border: 1px solid black;
	margin: 1px;
	padding: 1px;
	font-size: x-small;
	line-height: 1.3em;
}

/* The "Quote:" and "Code:" header parts... */
.quoteheader, .codeheader
{
	color: #000000;
	text-decoration: none;
	font-style: normal;
	font-weight: bold;
	font-size: x-small;
	line-height: 1.2em;
}

/* Generally, those [?] icons. */
.help
{
	cursor: help;
	background-color: transparent;
}

/* /me uses this a lot. */
.meaction
{
	color: red;
	background-color: transparent;
}

/* The main post box - this makes it as wide as possible. */
.editor
{
	width: 100%;
}

/* Highlighted text - such as search results. */
.highlight
{
	background-color: yellow;
	font-weight: bold;
	color: black;
}

/* Alternating backgrounds... */
.windowbg
{
	color: #000000;
	background-color: #afc6db; 
	vertical-align: middle;
}
.windowbg2
{
	border-style: none;
	color: #000000;
	background-color: #ffffff;
	vertical-align: middle;
}

/* Titles - such as table headers. */
.titlebg, tr.titlebg th, tr.titlebg td, .titlebg a:link, .titlebg a:visited, .titlebg2, tr.titlebg2 th, tr.titlebg2 td, .titlebg2 a:link, .titlebg2 a:visited
{
	font-weight: bold;
	font-style: normal;
	color: #ffffff;
	background-color: #6e94b7;
}
.titlebg a:hover
{
	color: #ffffff;
	text-decoration: underline;
}

/* The category headers, page indexes, and such things. */
.catbg
{
	font-weight: bold;
	background-color: #afc6db;
	background-image: url(http://forum.blockland.us/Themes/Blockland/images/catbg.gif);
	color: #FFF;
}

.catbg a:visited, .catbg a:link
{
	color: #FFF;
	text-decoration: none;
}
.catbg a:hover
{
	color: #CCF;
	text-decoration: underline;
}
.catbg td
{
	color: #FFF;
}

.catbg3
{
	font-weight: bold;
	background-color: #afc6db;
	color: #FFF;
}


/* The borders around things. */
.bordercolor
{
	background-color: #000; 
}
.tborder
{
	border: 1px solid  #002C72;
}

/* Default font sizes. */
.smalltext
{
	font-size: x-small;
}
.normaltext
{
	font-size: small;
}
.largetext
{
	font-size: large;
}
/* No image should have a border when linked */
a img{
	border: 0;
}

/* The AJAX notifier */
#ajax_in_progress
{
	background: #32CD32;
	color: white;
	text-align: center;
	font-weight: bold;
	font-size: 18pt;
	padding: 3px;
	width: 100%;
	position: fixed;
	top: 0;
	left: 0;
}



.logoLink {
	border:0px;
}

a.logoLink:hover 
{
	background-color:#F00;

	filter:alpha(opacity=50);   /* Internet Explorer       */
    -moz-opacity:0.5;           /* Mozilla 1.6 and below   */
    opacity: 0.5;               /* newer Mozilla and CSS-3 */
}
A:hover .logoLink, A:active .logoLink
{
	filter:alpha(opacity=50);   /* Internet Explorer       */
    -moz-opacity:0.5;           /* Mozilla 1.6 and below   */
    opacity: 0.5;               /* newer Mozilla and CSS-3 */
}
.navPages
{
  	color: gray;
}
.signature
{
  	display:none
}
}
