css:
@-moz-document 

domain(gamefaqs.gamespot.com),
domain(gamefaqs.com) {

#content, .body, .msg, .question, .status, 
input, select, textarea, .contrib td, .slide, .body li,
.pod_topgames li .count,
.user_subnav, .user_submenu,
.pod_poll .fields,
.search_results_title .sr_product:nth-child(2n)
{
background: #000000 !important;
}
#mantle_skin, .wrapper , .board_poll
{
background: #111111 !important;
}
.pod_filter, .searchtopics
{
background: #28398A !important;
}
tr:nth-child(2n+1), tr:nth-child(2n+1) td
{
background-color: #111111 !important;
}
.forum_anon
{
color: #FFFFFF !important;
background-color: #FF0000 !important;
border: none !important;
border-radius:0 !important;
}
.pod_filter
{
border-radius:0 !important;
}
.pod_filter
{
box-shadow: none !important;
}
.msg_infobox, .tinynav, .tinynav .crumb,
.content_subnav, .ui-dialog, .paginate > li > a
{
background: #28398A !important;
}
#content, table
{
border:4px #28398A solid !important;
}
input, .pod_poll .fields
{
border:1px #28398A solid !important;
}
.board.message .msg_infobox:not(.deleted)
{
border-top:1px #28398A solid !important;
}
.board.message .msg_infobox:not(.deleted),
th, td, .pod_topgames li,
.board_poll .row:not(:last-child)
{
border-bottom:1px #28398A solid !important;
}
.ab_alternating-thumbnails-c_abp-mode,
.ab_alternating-thumbnails-b_abp-mode,
textarea
{
border-color: #28398A !important;
}
.content_nav, .board_poll
{
border-radius:1px !important;
box-shadow:none !important;
}
blockquote
{
border-top:1px #28398A solid !important;
border-left:4px #28398A solid !important;
border-right:1px #28398A solid !important;
border-bottom:1px #28398A solid !important;
}
cite
{
border:none !important;
}
#content
{
border-radius:15px !important;
}
.body, .title, .page-title a
{
padding:10px !important;
}
.crumbs
{
padding-left:10px !important;
}
a, b, p, li, h1, h2, h3, 
input, select, textarea, label,
.name, .post_time, .msg_body, .newbeta,
.title, .title_nocap, .paginate, .user,
.topic, .author, .count, .lastpost, .body,
.sig_text, .video-label, .video-title,
.edited, .search_results_title .sr_as
{
color: #FFFFFF !important;
}
a:hover
{
text-decoration: underline !important;
}
}
