css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("shortmarks.com") { 

@font-face{font-family:ottomat;src:url('https://raw.githubusercontent.com/ms-89/static/master/ottomat.ttf')}

  body {
    background: url('https://raw.githubusercontent.com/ms-89/static/master/tech_light_gray.jpg') no-repeat center center fixed;
    background-size: cover;
    color: #444!important;
  }

  .footer, .left-header, #searchForm, .toolbar {
    display: none!important;
  }

  #bookmarks, .button {
    visibility: hidden!important;
  }
  
  #marksTable, #tmarks, #tbundles, #defaultSearch, .bold, #bundlesTable, .small {
    visibility: visible!important;
  }
  
  .small {
    text-shadow: none;
  }
  
  #bookmarks .bold {
    position: fixed;
    right: 2px;
    bottom: 4px;
  }
  
  input, select {
    color: #444;
    border: none;
    box-shadow: inset 1px 1px 2px 0px rgba(0,0,0,0.4);
    padding: 4px 8px;
    background-color: rgba(255,255,255,0.7)!important;
  }
  
  input { 
    border-radius: 5px;
  }

  select {
    border-radius: 5px 0 0 5px;
  }

  .right-header, .right-header a {
    color: #888!important;
    font-family: verdana!important;
    font-size: 11px!important;
    text-decoration: none;
    margin-top: -6px;
  }
  
  table {
    background-color: transparent!important;
    border-radius: 10px;
    border-spacing: 0;
    box-shadow: 0 0 5px 0 black;
    font-family: ottomat!important;
    font-size: 12px!important;
    padding: 10px;
  }

  b {
    font-weight: normal!important;
  }
  
  table td {
    color: #fff;
    text-shadow:
      0 0 1px #333,
      0 0 2px #555,
      0 0 3px #777;
  }

  table td a {
    color: #bbb;
    text-decoration: none;
    transition: all 0.3s ease-in-out;
  }

  table td a:hover, .key:hover {
    color: #000;
    text-shadow:
      0 0 1px rgba(155,195,255,1.0),
      0 0 3px rgba(155,195,255,1.0),
      0 0 5px rgba(155,195,255,1.0),
      0 0 7px rgba(155,195,255,1.0),
      0 0 9px rgba(155,195,255,1.0);
    text-decoration: none;
    transition: all 0.3s ease-in-out;
  }

  #marksTable, #bundlesTable {
    position: fixed;
    top: 25px!important;
    left: 50%;
    border: none!important;
  }

  #bundlesTable *, #marksTable * {
    border: none!important;
    background-color: transparent!important;
  }

  #marksTable {
    margin-left: -360px!important;
    width: 350px!important;
    background:
      linear-gradient(to left,   rgba(0,0,5,0.5) 20%, rgba(0,0,5,0.7)),
      linear-gradient(to bottom, rgba(0,0,5,0.5),     rgba(0,0,5,0.7));
  }

  #bundlesTable {
    width: 350px!important;
    margin-left: 10px!important;
    background:
      linear-gradient(to right,  rgba(0,3,0,0.5) 20%, rgba(0,3,0,0.7)),
      linear-gradient(to bottom, rgba(0,3,0,0.5),     rgba(0,3,0,0.7));
  }

  #bundlesTable th, #marksTable th {
    color: transparent;
    text-indent: -9999px;
    line-height: 0;
    text-shadow: none;
    background-image: none!important;
  }

  #bundlesTable th:before, #marksTable th:before {
    padding-bottom: 10px;
    margin-top: -3px;
    text-indent: 0;
    display: block;
    line-height: initial;
    color: rgba(255,255,255,0.9);
    text-shadow:
      0 0 1px rgba(255,255,255,0.2),
      0 0 2px rgba(255,255,255,0.2),
      0 0 3px rgba(255,255,255,0.2),
      0 0 5px rgba(255,255,255,0.2),
      0 0 7px rgba(255,255,255,0.2);
  }

  #bundlesTable th:nth-child(1):before { content: "Keyword"; }
  #bundlesTable th:nth-child(2):before { content: "Bundle"; }
  #bundlesTable th:nth-child(3):before { content: "Sites"; }
  #marksTable   th:nth-child(1):before { content: "Keyword"; }
  #marksTable   th:nth-child(2):before { content: "Site"; }
  #marksTable   th:nth-child(3):before { content: "Query link"; }
  #marksTable   th:nth-child(4):before { content: "Post Data"; }

  #marksTable   th:nth-child(3) { display: none; }
  #marksTable   td:nth-child(3) { display: none; }

  #bundlesTable th, #bundlesTable td, span, #bundlesTable th {
    text-align: center;
    border: none!important;
  }

  #tmarks {
    margin-top: 10px!important;
    background-color: rgba(230,230,230,0.8)!important;
  }
  
  #bookmarks {
    margin-top: -50px;
  }
  
  #tbundles {
    margin-top: -90px;
  }

  #marksTable td:nth-child(1),
  #marksTable th:nth-child(1),
  #bundlesTable td:nth-child(1),
  #bundlesTable th:nth-child(1) {
    text-align: right;
  }

  #marksTable td:nth-child(2),
  #marksTable th:nth-child(2),
  #bundlesTable td:nth-child(2),
  #bundlesTable th:nth-child(2) {
    text-align: left;
    padding-left: 15px;
  }
  
  #bundlesTable td:nth-child(3) {
    text-align: left;
  }

  #marksTable   th:nth-child(1), #marksTable   td:nth-child(1) { width: 20%!important; }
  #marksTable   th:nth-child(2), #marksTable   td:nth-child(2) { width: 60%!important; }
  #marksTable   th:nth-child(3), #marksTable   td:nth-child(3) { width:  0%!important; }
  #marksTable   th:nth-child(4), #marksTable   td:nth-child(4) { width: 20%!important; }

  #bundlesTable th:nth-child(1), #bundlesTable td:nth-child(1) { width: 20%!important; }
  #bundlesTable th:nth-child(2), #bundlesTable td:nth-child(2) { width: 40%!important; }
  #bundlesTable th:nth-child(3), #bundlesTable td:nth-child(3) { width: 40%!important; }

}
