css:
@-moz-document domain("forum.paradoxplaza.com") {  
a.avatar img, .avatarHolder img, a.avatar span.img.m, a.avatar {
  border: 1px transparent !important; 
  background-color: transparent !important; 
  box-shadow: none !important}
.messageUserBlock .avatarHolder {padding-top: 2px !important; padding-bottom: 0 !important}
.posterAvatar .avatarContainer {min-height: 52px !important}
.onlineMarker {display: none}
.messageUserBlock.online a.username {color: #6EAE05}
img[src*="default/xenforo/avatars"], span.img.s[style*="default/xenforo/avatars"] {display: none !important}
.messageUserBlock img[src*="obsolete"] {max-width: 50px !important; max-height: 50px !important}
.messageUserBlock img[src*="v2"] {max-width: 80px !important; max-height: 80px !important}
.messageUserBlock img[src*="eu3"] {max-width: 90px !important; max-height: 90px !important}
.messageUserBlock img[src*="Sengoku"] {max-width: 90px !important; max-height: 90px !important}
.messageUserBlock img[src*="new"] {max-width: 80px !important; max-height: 80px !important}
.messageUserBlock img[src*="EUIV"] {max-width: 90px !important; max-height: 90px !important}
.messageUserBlock img[src*="hoi3"] {max-width: 80px !important; max-height: 80px !important}
.messageUserBlock img[src*="generals"] {max-width: 50px !important; max-height: 50px !important}
.messageUserBlock img[src*="dev"] {max-width: 90px !important; max-height: 90px !important}
.messageUserBlock img[src*="in_nomine"] {max-width: 70px !important; max-height: 70px !important}
.messageUserBlock img[src*="new_avatars"] {max-width: 50px !important; max-height: 50px !important}
.messageUserBlock img[src*="rome"] {max-width: 60px !important; max-height: 60px !important}
.messageUserBlock img[src*="ftg"] {max-width: 80px !important; max-height: 80px !important}
}
