css:
@-moz-document url-prefix("https://www.facebook.com/"), url-prefix("https://apps.facebook.com/") {
body
{
  background-color: #070707 !important;
  color: #ddd !important;
  font-family: Consolas !important;
}

._5afe
{
  color: #eee !important;
}

.conversation
{
  font-family: Consolas !important;
  background-color: #070707 !important;
}

div::before
{
  display: none !important;
}

div::after
{
  display: none !important;
}

#contentCol
{
  background-color: #333 !important;
}

.mbm
{
  color: #eee !important;
  background-color: #000 !important;
}

.fbChatSidebar
{
  background-color: #111 !important;
  color: #eee !important;
  font-family: Consolas !important;
  border: 1px solid #0f0 !important;
}

.uiGrid
{
  background-color: black !important;
}

a.UFICommentActorName, input[type="button"]
{
  font-weight: bold !important;
  font-family: Consolas !important;
  color: #0f0 !important;
}

a
{
  font-family: Consolas !important;
  color: #0f0 !important;
}

.fbFeedTickerStory
{
  font-family: Consolas !important;
  border: 1px solid #0f0 !important;
}

.UFIList, .UFIRow, .pinnedUfi, .UFIReplyList, ._ohe
{
  background-color: #222 !important;
}

.UFIRow
{
  border-top: 1px solid #444 !important;
  border-bottom: 1px solid #444 !important;
}

input, textarea, .tickerFeedMessage, .UFICommentBody
{
  font-family: Consolas !important;
  color: #eee !important;
}

input[type="text"], textarea, .fbNubFlyoutFooter, .smurfbarBottomBorder, .smurfbarTopBorder, .uiTypeahead
{
  background-color: #222 !important;
}

.sentFromMobile
{
  font-family: Consolas !important;
  background-color: #070707 !important;
}

.vBot
{
  background-color: #070707 !important;
}

._5wdf
{
  max-width: 150px !important;
  border: 0px !important;
  color: #ccc !important;
  -webkit-box-shadow: none !important;
}

._5w1r
{
  background-image: -webkit-linear-gradient(bottom, #010101, #222) !important;
}

.titlebar
{
  background: #222 !important;
  border: 1px solid #777 !important;
}

._6m7
{
  font-family: Consolas !important;
}

._5vb_, ._5qdv, ._4-u2
{
  border-color: #777 !important;
}

._4f7n
{
  background-image: none !important;
  background-color: #333;
  border-bottom: 1px solid black !important;
}

.cardRightCol, ._4-u2
{
  background-color: #333 !important;
}

._g3j, ._g3i, ._50f7
{
  background-color: #222 !important;
  color: #fff !important;
}

.fbRemindersTitle
{
  font-weight: bold !important;
  color: #0f0 !important;
}
}
