css:
@-moz-document domain("computerbase.de") {
body {
  /*font-family: Verdana;
  /*font-size: 14px;*/
}

.block1, .header, .subheader, .fullwidth-block {
  padding: 0;
  }
  
#content {
  	 background: #AEBCCF;
}

.header:not(.header--2) {
    border: none;
    }
    
.header {
  border-bottom: 3px solid #e84d0e;
      }
      
.follow-links {
      display: none;
        }          
      
.usermenu-avatar__wrap, .usermenu-avatar__wrap--logged-in {
        border-radius: 0;
        -moz-border-radius: 0;
          }
        
.subheader {
  border-bottom: 3px solid #e84d0e;
      }  
        
.subheader:before {
          display: none;
            }

.block1--ads, #legacy-adbox-banner, #legacy-adbox-skyscraper, .adbox-wrapper, 
.adbox-wrapper--rectangle {
    display: none;
  }

.block1--highlight {
    display: none;
  }

.block1 {
  background: #AEBCCF; 
}
    
  
.comment {
    display: none;
      }
    
.box--blue {
        background: #e5e5e5;
      }
      
.box--blue time {
        color: #777!important;
      }
     
.box--blue h2 a{
        color: #e84d0e!important
            }
        
.box--blue a {
        color: #333!important;
      } 
          
.tabs__li a[aria-selected=true] {
        background: #2c5b9c; 
        color: #fff!important;
        border-radius: 0px;            
          }
          
.tabs__li a[aria-selected=false] {
        border-radius: 0px;
            }      

.ticker__article-title {
    font-weight: 500;
  }

.ticker {
	letter-spacing: 0.0em;
  }

.article__update, .ticker__update {
   border-radius: 0;
   -moz-border-radius: 0;
  
   /*background: #e84d0e;
   color: #fff;*/
  }
}

@-moz-document regexp("http://www.computerbase.de/") {
#content {
     padding-top: 300px; 
}

#content div:nth-of-type(6) {
 	position: absolute; 
  	width: 100%;
    top: 25px;    
}

#content div:nth-of-type(4) {
 	display: none;   
}

#content div:nth-of-type(5) .col-aside {
    margin-top: -550px;
  }
}
