css:
@-moz-document url-prefix("http://pikabu.ru/") {
	
	#branding_ad_header, #branding_ad_footer, #right_menu #branding_ad_comment, /*баннеры*/
	div[id^='yandex_ad'], .adv_wrap, .right_menu_ads, /*рекламные блоки*/
	div.stories > .story[data-story-id='_'], /* реклама в профиле */
	#right_menu > table, /* социальные виджеты в сайдбаре */
	.stories_prev, /* закрепленный пост */
	.b-navigator /* зафиксированная панелька в ленте */
	{
		display: none !important;
		position: absolute !important;
		width: 0 !important;		
		height: 0 !important;
		left: -9000% !important;
		z-index: -9000 !important;
	}

	#wrap {
		padding-top: 10px;
	}
	
	#wrap * {
		transition: .3s !important;
		transition-property: color, text-decoration-color, background-color, border-color, box-shadow, opacity !important;
	}
	#wrap *:hover, #wrap *:active, #wrap *:focus,
	#wrap *:hover > *, #wrap *:active > *, #wrap *:focus > * {
		transition: none !important;
	}
	
	#wrap blockquote {
		background: rgba(150,150,0,.1) !important;
	}
	
	.tooltipster-base {
		box-shadow: none !important;
	}
	
	.popup-box {
        z-index: 10001 !important;
		border-radius: 3px !important;
		box-shadow: 0 1px 2px rgba(0,0,0,.1),
		            0 5px 10px rgba(0,0,0,.1),
		            0 10px 30px rgba(0,0,0,.15);
	}
	
	
	/* Ссылки
	******************************************/
	a[href] {
		text-decoration-color: rgba(81,144,184,.3);
	}

	#wrap .info a[href]:not([href*='javascript']),
	#wrap .story__header-additional-wrapper a[href]:not([href*='javascript']),
	.header_links a,
	#wrap .story__authors,
	.awards_wrap > div a,
	.footer_links a
	{
		color: #777;
		text-decoration: underline;
		text-decoration-color: rgba(0,0,0,.2);
	}

	a[href]:hover:not([href*='javascript']):not([href^='#']):not(.no_ch):not(.b-button):not(.ss-active),
	.b-user-menu__header > a:hover,
	.b-comment__user > a:hover span,
  	.b-community-line:hover .b-community-line__name
	{
		color: #dc5620 !important;
		text-decoration: underline;
		text-decoration-color: rgba(220,86,32,.3) !important;
		border-bottom-color: rgba(220,86,32,.3) !important;
	}

	/* псевдоссылки */
	a[href='#'],
	#wrap a.dotted,
	a[href*='javascript']:not(.detailDate):not(.story-search-reset):not(.b-button),
	a.tb-add-btn, /* кнопка добавления тега в Поиске */
	a.tb-more-btn, /* «показать все теги» в выдаче Поиска */
	a.story-result-user-more /* «показать всех пользователей» */
	{
		text-decoration: none !important;
		border-width: 0 !important;
		border-color: rgba(0,30,60,.6) !important;
		color: rgba(0,30,60,.5) !important;
		background-image: linear-gradient(to right, rgba(0,30,60,.6), rgba(0,30,60,.6) 50%, transparent 50%);
		background-size: 2px 1px;
		background-repeat: repeat-x !important;
		background-position: 0 calc(100% - 1px);
		background-clip: content-box !important;
	}
	a[href='#']:hover,
	#wrap a.dotted:hover,
	a[href*='javascript']:not(.detailDate):not(.b-button):hover,
	a.tb-add-btn:hover,
	a.tb-more-btn:hover,
	a.story-result-user-more:hover,
	.search-result-msg > a
	{
		text-decoration: none !important;
		border-width: 0 !important;
		border-color: #2a80b1 !important;
		color: #2a80b1 !important;
		background-image: linear-gradient(to right, #2a80b1, #2a80b1 50%, transparent 50%);
		background-size: 2px 1px;
		background-repeat: repeat-x !important;
		background-position: 0 calc(100% - 1px);
		background-clip: content-box !important;
	}
	
	#wrap .a-selected > a {
		color: #5190b8 !important;
	}
	
	
	/* Инпуты
	******************************************/
	input,
	.b-comments-reply__editable,
	.selectize-input, /* поле добавления тегов в Билдере */
	.tb-tag /* поле добавления тега в Поиске */
	{
		color: rgba(0,0,0,.7) !important;
		line-height: normal !important;
		background: rgba(255,255,255,.7) !important;
		background-clip: padding-box !important;
		border: 1px solid rgba(0,0,0,.2) !important;
		border-radius: 3px !important;
		box-shadow: none !important;
	}
	
	input:hover,
	.b-comments-reply__editable:hover,
	.selectize-input:hover,
	.tb-tag:hover
	{
		background-color: white !important;
		border-color: rgba(0,0,0,.3) !important;
	}
	
	#wrap input:focus,
	.b-comments-reply__editable:focus,
	.selectize-input.focus,
	.tb-tag.tb-selected
	{
		background-color: white !important;
		border-color: #3296c8 !important;
		box-shadow: 0 1px 2px rgba(0,0,0,.05),
		            0 5px 10px rgba(0,10,20,.05) !important;	
	}
	
	
	/* Кнопки
	******************************************/
	.b-button,
	select.b-select,
	.tb-tag-complete > table /* тег-выпадайка в Поиске */
	{
		color: rgba(0,0,0,.7);
		background: none;
		border: 1px solid rgba(0,0,0,.2);
		border-radius: 3px !important;
		box-shadow: none;
		text-shadow: none !important;
		text-decoration: none !important;
		cursor: pointer;
		-webkit-appearance: none;
		-moz-appearance: none;
	}
	
	.b-button:hover:not(.b-button_loading_yes),
	.b-button:focus:not(.b-button_loading_yes),
	select.b-select:hover,
	.tb-tag-complete > table:hover
	{
		background-color: white;
		box-shadow: 0 1px 2px rgba(0,0,0,.07);
		border-color: rgba(0,0,0,.3);
	}
	
	.b-button:active:not(.b-button_loading_yes),
	.tb-tag-complete > table:active
	{
		background-color: rgba(0,30,50,.05);
		border-color: rgba(0,0,0,.3);
		box-shadow: inset 0 1px 3px rgba(0,0,0,.1);
	}
	
	/* текстокнопка */
	.b-button.b-comment__control,
	.b-button.b-comments-reply__attach-files,
	#wrap .b-gtpost-builder__preview
	{
		color: #5190b8 !important;
		background: none !important;
		box-shadow: none !important;
		border: 0 !important;
	}
	.b-button.b-comment__control:hover,
	.b-button.b-comments-reply__attach-files:hover,
	#wrap .b-gtpost-builder__preview:hover
	{
		color: #75a7c7 !important;
	}
	.b-button.b-comment__control:active,
	.b-button.b-comments-reply__attach-files:active,
	#wrap .b-gtpost-builder__preview:active 
	{
		color: #47738f !important;
	}
	
	/* зеленая кнопка */
	.b-button_type_green,
	.b-gtpost-builder__submit /* кнопка публикации поста */
	{
		color: white;
		background: #37ae45 !important;
		border-color: transparent !important;
		box-shadow: none !important;
	}
	
	.b-button_type_green:hover,
	.b-gtpost-builder__submit:hover, .b-gtpost-builder__submit:focus
	{
		background-color: #3dbf4c !important;
	}
	
	.b-button_type_green:active,
	.b-gtpost-builder__submit:active
	{
		background-color: #07a33e !important;
	}
	
	/* неактивная кнопка */
	#wrap button[disabled]:not(.b-button_type_preview),
	#wrap .b-button_type_green_disabled {
		background-color: rgba(0,0,0,.1) !important;
		box-shadow: none !important;
		cursor: not-allowed !important;
	}
	#wrap .b-button_loading_yes {
		color: white !important;
		background-color: #2b6fad !important;
		background-image: linear-gradient(-45deg, rgba(255,255,255,.5) 25%,transparent 25%,transparent 50%, rgba(255,255,255,.5) 50%, rgba(255,255,255,.5) 75%,transparent 75%,transparent) !important;
		background-size: 50px 50px !important;
		box-shadow: none !important;
		cursor: wait !important;
		opacity: .7 !important;
	}
	
	/* выпадайка */
	#wrap select.b-select {
		background-color: transparent;
		background: calc(100% - 7px) center no-repeat;
		background-image: url('data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAkAAAAFCAYAAACXU8ZrAAAANElEQVQImWNgYGCYycDAIMkABf///4djqPhMZIYksiJ0cWwCGAqwKURVADUal4kMDAwMDAC62RjHEsgmhQAAAABJRU5ErkJggg==');			
	}
	select.b-select:focus {
		border-color: #3296c8;
		box-shadow: none !important;
	}
	select option {
		background: white;
	}
	select option:first-child {
		margin-top: 2px;
	}
	select option:last-child {
		margin-bottom: 2px;
	}
	
	
	/* Теги
	******************************************/
	#wrap .tag,
	#wrap .story__tag,
	#wrap .b-tag-search-result > div > a, /* в Тегах */
	#wrap .search-result-tags table, /* в Поиске */
	#wrap .b-gtpost-builder__tags .item /* в Билдере */
	{
		padding: 1px 4px 2px !important;
		text-decoration: none !important;
		text-shadow: none !important;
		background: rgba(0,0,0,.025);
		border: 1px solid rgba(0,0,0,.125);
		border-radius: 3px !important;
		box-shadow: none !important;
	}
	a.tag:hover, a:hover > span.tag,
	#wrap .story__tag:hover,
	#wrap .b-tag-search-result > div > a:hover,
	#wrap .search-result-tags table:hover,
	.pager ul li:hover a /* кнопки пагинации */
	{
		color: #dc5620;
		background: rgba(255,200,64,.15) !important;
		border-color: rgba(220,86,32,.3) !important;
	}
	
	
	
	/***********************************************************
	 ХЕДЕР
	***********************************************************/
	div.header-cont, div[style*="header_bg13.gif"] {
		background: #F0F1EB linear-gradient(to right, #F0F1EB, #F5F0E6) !important;
		border-bottom: 1px solid rgba(0,0,0,.05);
	}
	#header_t, #header_t .header-nav > table {
		height: 64px !important;
	}
	
	
	/* Лого
	******************************************/
	.header-logo {
		width: 200px !important;
	}
	.b-logo {
		width: 148px !important;
		height: 39px !important;
		margin: auto !important;
		padding-top: 14px;
		background: url(http://i.imgur.com/XE6faTw.png);
		background-image: var(--sprite);
		background-repeat: no-repeat;
		background-origin: content-box;
	}
	.b-logo:hover:not(:active) {
		-webkit-filter: brightness(1.03) saturate(103%);
		filter: brightness(1.03) saturate(103%);
	}
	.b-logo::after {
		display: none !important;
	}
	
	
	/* Навигация
	******************************************/
	#header_t .header-nav-bottom * {
		position: relative !important;
	}
	#header_t .menu {
		height: 35px !important;
	}
	#header_t .menu > li {
		width: auto;
		height: auto;
		margin: 0 !important;
	}
	#header_t .menu > li > a {
		width: auto !important;
		height: 32px !important;
		margin: 0 10px !important;
		padding: 0 5px !important;
		color: #536d8d !important;
		border: 0 !important;
		border-bottom: 4px solid #536d8d !important;
		border-radius: 0 !important;
		font-size: 18px !important;
		text-shadow: none !important;
		text-transform: lowercase;
		background: none !important;
		transition: .5s !important;
	}
	#header_t .menu > li > a:not([href]) {
		cursor: default !important;
	}
	#header_t .menu > li.noactive > a {
		border-bottom-color: transparent !important;
	}
	#header_t .menu > li.noactive > a:hover {
		color: #dc5620 !important;
		transition: none !important;
	}
	
	
	/* Ссылки над поисковой строкой
	******************************************/
	.header_links {
		height: 25px !important;
		margin-top: -74px !important;
	}
	.header_links ul {
		margin-left: 1px;
	}
	.header_links li {
		margin-top: 6px;
	}
	.header_links a:not(:hover) {
		color: #888 !important;
	}
	.header_links a:hover {
		text-decoration: underline !important;
	}
	
	
	/* Поисковая строка
	******************************************/
	#serach_block {
		width: 300px !important;
		margin-left: 1px !important;
		padding-bottom: 10px !important;
	}
	#search_menu {
		box-sizing: border-box !important;
		height: 30px !important;
		padding-right: 40px !important;
		background-image: url(data:image/png;base64,) !important;
		background-position: calc(100% - 10px) 50% !important;
		background-clip: padding-box !important;
		background-repeat: no-repeat !important;
		border-color: rgba(0,0,0,.1) !important;
	}
	#search_menu:hover {
		border-color: rgba(0,0,0,.2) !important;
	}
	#search_menu::-moz-placeholder {
		color: rgba(0,0,0,.7);
	}
	#serach_block .search_btn {
		position: absolute !important;
		width: 40px !important;
		height: 30px !important;
		margin-left: -40px !important;
		opacity: 0;
	}
	
	
	
	/***********************************************************
	 БОКОВАЯ ПАНЕЛЬ
	***********************************************************/
	.menu-block,
	.b-user-menu__main {
		border-color: #dbdbdb !important;
	}
	#search_menu,
	.b-user-menu,
	.b-sign {
		width: 302px !important;
	}
	.menu-block-title {
		padding: 8px 12px 5px 15px !important;
		background: none !important;
		border-bottom: 1px dashed #dbdbdb !important;
		text-shadow: none !important;
	}
	#right_menu a:not(:hover) {
		text-decoration: none;
	}
	
	
	/* Меню пользователя
	******************************************/
	.b-user-menu {
		position: relative !important;
		margin-top: 21px !important;
	}
	.b-user-menu__main {
		width: 100% !important;
		border-radius: 3px !important;
	}
	
	.b-user-menu__header {
		margin: 0 !important;
		padding: 8px 16px 5px 14px;
	}
	.b-user-menu__header > a {
		color: #5190B8;
		font-size: 14px !important;
	}
	.b-user-menu__logout {
		width: 19px !important;
		height: 17px !important;
		background: url(http://i.imgur.com/XE6faTw.png) -1px -40px no-repeat !important;
		background-image: var(--sprite) !important;
	}
	
	/* ссылки */
	.b-user-menu-list {
		margin: 8px 0 12px !important;
	}
	.b-user-menu-list__icon {
		display: none !important;
	}
	.b-user-menu-list > li {
		display: table;
		padding-left: 15px;
	}
	.b-user-menu-list > li:nth-child(2) ~ li {
		cursor: pointer;
	}
	.b-user-menu-list > li:nth-child(2) ~ li:hover a {
		color: #dc5620;
	}
	.b-user-menu-list > li:nth-child(2) ~ li:hover a.b-user-menu-list__count {
		background: rgba(250,150,50,.2);
		transition: none;
	}
	.b-user-menu-list > li a:not(.b-user-menu-list__count) {
		display: inline-block !important;
		margin-left: -15px;
		padding-left: 12px;
		border-left: 3px solid transparent;
		text-decoration: none !important;
	}
	.b-user-menu-list > li a[href^='http://pikabu.ru/profile'] {
		display: none !important;
	}
	
	/* юзерпик */
	.b-user-menu__logo {
		position: absolute !important;
		width: auto !important;
		top: 47px;
		right: 15px;
	}
	.b-user-menu__logo::before, .b-user-menu__logo::after {
		display: none !important;
	}
	.b-user-menu__avatar {
		border-radius: 0 !important;
		border: none !important;
	}
	.b-user-menu__avatar img {
		width: 100px !important;
		height: 100px !important;
		border-radius: 3px;
	}
	
	/* переключатель */
	.b-user-menu .switchery {
		margin-top: 5px;
	}

	
	/* Кнопка «Добавить пост»
	******************************************/
	.menu-add-btn {
		margin-top: 17px !important;
	}
	.menu-add-btn > a {
		text-decoration: none !important;
	}
	.menu-add-btn .b-button {
		border: 0 !important;
	}
	@keyframes motorback {
		0% {transform: rotate(180deg)}
	}
	
	
	/* Комментарий дня
	******************************************/
	.menu-best-comm {
		background: none !important;
	}
	.menu-best-comm > .menu-story-link {
		line-height: 1 !important;
		margin-bottom: 10px !important;
	}
	.menu-best-comm .menu-comm-head > h6 {
		margin-top: 1px !important;
	}
	
	
	/* Обсуждения
	******************************************/
	.menu-block tr:first-child + tr.menu-discussed-item {
		display: table;
		width: 100%;
		margin-top: 10px;
	}
	.menu-discussed-item .most_comm_post_show {
		padding-top: 3px;
	}
	
	
	/* Подписки
	******************************************/
	.menu-subs-tabs {
		font-size: 0;
	}
	.menu-subs-tabs > span {
		position: relative;
		display: inline-block;
		padding: 0 !important;
		font-size: 12px;
		border: 1px solid #ccc;
		border-radius: 3px;
	}
	.menu-subs-tabs > span:not(:first-child) {
		margin-left: -1px !important;
		border-radius: 0 3px 3px 0 !important;
	}
	.menu-subs-tabs > span:not(:last-child) {
		border-radius: 3px 0 0 3px !important;
	}
	.menu-subs-tabs > span:hover {
		border-color: #8daebd;
		z-index: 2;
	}
	.menu-subs-tabs > span.subs_list_switch_active {
		background: rgba(0,20,30,.07) !important;
		border-color: #bebebe !important;
		box-shadow: inset 0 1px 3px rgba(0,0,0,.07) !important;
		z-index: 1;
	}
	.menu-subs-tabs > span a {
		display: inline-block;
		padding: 2px 7px !important;
		line-height: 16px !important;
		color: #888 !important;
	}
	.menu-subs-tabs > span.subs_list_switch_active a {
		color: #686969 !important;
		cursor: default !important;
	}
	
	/* список подписок */
	.subs_list_item {
		display: table;
		margin: 0 !important;
	}
	.subs_list_item:last-child {
		margin-bottom: 5px !important;
	}
	.subs_list_item > a {
		display: inline-table;
		padding: 1px .9em 3px 0 !important;
	}
	.subs_list_item > .unsub_item_wrap:not(:hover) {
		opacity: .6;
	}
	
	
	/* Сообщества
	******************************************/
	#right_menu .b-community-line > a {
		text-decoration: none !important;
	}
	
	
	/* Популярные теги
	******************************************/
	.menu-top-tags-cont {
		padding-top: 0 !important;
	}
	.tag_count {
		margin-left: 0 !important;
	}
	.allMenuTags > a:hover > .tag_count {
		color: rgba(220,86,32,.75);
	}
	#wrap .menu-top-tags-links a {
		color: rgba(0,30,60,.5);
		text-decoration: none !important;
		background: none !important;
		border-bottom: 1px solid rgba(0,30,60,.25);
	}
	#wrap .menu-top-tags-links a.dotted {
		border-bottom: 1px dashed !important;
	}
	
	
	
	/***********************************************************
	 ФУТЕР
	***********************************************************/
	.footer_links {
		margin-top: 150px !important;
		padding: 12px 28px !important;
		border-top: 1px solid rgba(0,0,0,.1)
	}
	.footer_links * {
		font-size: 0 !important;
	}
	.footer_links a {
		font-size: 12px !important;
	}
	.footer_links2,
	.footer-table-bottom .counter {
		display: none !important;
	}
	
	/* контакты */
	.footer-table-bottom {
		padding: 0 !important;
	}
	.footer-table-bottom * {
		color: #999 !important;
	}
	.footer-table-bottom .copy {
		box-sizing: border-box !important;
		position: absolute;
		width: 100% !important;
		margin-top: -140px;
	}
	.footer-table-bottom a[href] {
		color: #217cb6 !important;
		text-decoration: none;
	}
	.footer-social a {
		vertical-align: middle !important;
	}
	
	/* печенька */
	.footer-pechenka {
		width: 50% !important;
		right: 0 !important;
		height: 100px;
	}
	#brovdiv {
		right: 12% !important;
		bottom: -1px !important;
	}
	
	
	
	/***********************************************************
	 ЛЕНТА
	***********************************************************/
	.b-feed-panel:not([data-mode^='community']) {
		display: table;
		margin-bottom: -20px !important;
		padding: 5px 12px 12px 1px !important;
		color: rgba(0,0,0,.7);
		background: none !important;
	}
	.b-feed-panel:not([data-mode^='community']) .b-feed-panel__toolbar > div:first-child {
		padding-right: 20px !important;
	}
	.b-feed-panel__toolbar a {
		background-position: 0 100% !important;
	}
	
	
	/* Пост
	******************************************/
	.story {
		margin-bottom: 20px;
	}
	.story__main,
	.story__header {
		min-height: 60px !important;
	}
	.story__header {
		box-sizing: border-box !important;
