css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.chatwork.com") {
    body, pre, input, select, textarea, button {
        font-family: Verdana,'Segoe UI Semibold','メイリオ',Meiryo,'ヒラギノ角ゴ Pro W3','Hiragino Kaku Gothic Pro',sans-serif !important;
    }

    /*
    .promotionArea {
        display: none !important;
    }
    */

    .chatRoomListArea {
        height: 100% !important;
    }

    .dateContent {
        font-size: 150% !important;
        font-weight: 800 !important;
        top: -16px !important;
        left: 42% !important;
    }

    .dateContent .icoFontClock {
        font-size: 18px !important;
        margin-top: 3px !important;
    }

    .dateHead {
        border-top: 3px solid #5686C6 !important;
        margin-top: 20px !important;
    }

    .chatTimeLineMessage .chatTimeLineMessageInner {
        padding: 19px 15px !important;
    }

    .chatTimeLineItemHeader {
        font-size: 115% !important;
    }

    .timeStamp {
        font-family: Verdana,'メイリオ',Meiryo,'ヒラギノ角ゴ Pro W3','Hiragino Kaku Gothic Pro',sans-serif !important;
        font-size: 12px !important;
        font-weight: 500 !important;
    }

    .actionArea {
        bottom: -9px !important;
        right: 60px !important;
    }

    .chatQuote {
        background-color: #D8E8F8 !important;
        opacity: 0.7 !important;
        border-radius: 5px !important;
    }

    .chatQuote .chatQuote {
        background-color: #C0D0E0 !important;
        border-radius: 5px !important;
    }

    .chatQuote .title {
        color: #000000 !important;
    }
    
    .chatTimeLineMessageMention {
        /* background: none repeat scroll 0% 0% #DAEFFA !important; */
        font-weight: 500 !important;
        text-shadow: 2px 1px 2px #708090 !important;
    }
}
