css:
@-moz-document domain(irccloud.com) {
    

/* COLORS:
 * -MAIN DARK: rgb(45,44,42)
 * -LIGHTER DARK: rgb(85,84,82)
 * -BABY_BLUE(Action): rgb(0,128,255);
 * -SUPER_PINK(Quit): rgb(255,128,192);
 * -DEEP_INDIGO(Join): rgb(116,103,142);
*/

html {
	font-family: "Consolas";
	font-size: 9pt;
}
body {
	background: rgb(45,44,42);
	font-family: "Consolas";
	font-size: 9pt;
}

#sidebar #limits a{
	display: none;
}

td.statuscell {
	color: whitesmoke;
}

#statusActions b, #statusActions strong {
	color: rgb(255,128,192);
}

/* MAIN EDITBOX */
.bufferinputcell {
	background: rgb(45,44,42);
}

/* MAIN MESSAGE TEXT COLOR */
div.log div.chat, div.log div.error, div.log div.groupedLines, div.log div.notice, div.log div.status, div.log div.table, div.log div.warning {
	color: whitesmoke;
	font-family: "Consolas";
	font-size: 9pt;
	color: rgb(192,192,192);
}

/* MY OWN TEXT */
div.log div.self {
	 background-color: rgb(45,44,42); 
	color: whitesmoke;
}

/* MY OWN NICKNAME COLOR */
div.log div.row span a.author {
	color: lightgray;
}

/* ACTION MSG COLOR */
div.log div.me, div.type_buffer_me_msg {
	color: rgb(0,128,255);
}

/* ALL HYPERTEXT LINKS */
div.log div.chat a {
	color: rgb(255,128,192);
}

/* USERNAME LINKS */
div.log div.row span a.user.hover {
	color: rgb(255,128,192);
}

/* Main CHAT Background */

div.scroll {
	background: rgb(45,44,42);
}

/* Main Channel Status */

.bufferHead {
	background: rgb(45,44,42);
}


/* ARROW PREFIXES */

div.log span.prefix {
	color: whitesmoke;
}

/* CHANNEL NAME IN STATUS */

td.statuscell div.status h2.bufferHeading span.label {
	color: whitesmoke;
}


/* QUIT */

div.type_quit {
	color: rgb(255,128,192);
}


/* PART CHAN */

div.type_parted_channel {
	color: rgb(255,128,192);
}

/* JOIN CHAN */

div.type_joined_channel {
	color: rgb(116,103,142);
}

/* NIPPED (JOIN + PART) */

div.groupedJoinPart {
	color: rgb(116,103,142);
}

/* MESSAGE HIGHLIGHT */
div.log div.row.highlight {
	background-color: rgb(85,84,82);
}
}
