css:
@-moz-document domain("facebook.com") {
/* Wall Background */

body, body > * {
	background: url("http://www.meh.ro/wp-content/uploads/2016/03/meh.ro12359.jpg") no-repeat;
	background-attachment: fixed;
	background-size: 100% auto;
}

/* Master head blue bar */
#pagelet_bluebar > blueBarDOMInspector > dom {
    position: fixed;
    top:0;
}
/* Profile page background */
#pagelet_timeline_recent {
        background: rgba(0,0,0,0) !important;
}

/* Profile Recent list background */

#u_jsonp_5_2i {
        background: rgba(0,0,0,0) !important;
}

/* Left Column to Hide (With Groups) */

._5vb_.hasLeftCol #leftCol {
	display: none;
}

/* Main Center Content Column Wrapper */

html ._5vb_.hasLeftCol #contentCol {
	padding: 0;
	margin-left: 0% !important;
	background: rgba(0,0,0,0);
	
}


/* Compose Status Box */

#pagelet_composer {
position: fixed;
left: 805px;
top: 55px;
width: 30%;
}

/* Compose Status Box Profile Picture (MAY 2015/MAY2016) */

div#u_0_18 div div a div._mp3 {
    display: none !important;
}

/* Compose Status Box Write/Picture li */

._5t_y ._1dsl li {
	margin-left: 0;
	padding:0;
	
}

/* Compose Status Box Photo Album li To Hide */
._4oki div {
	display: none;
}


/* Container for all columns */

._5vb_.hasLeftCol #mainContainer {
	width: 795px;
	margin-top: 10px;
	margin-left: 5px
}

/* Right Column with Birthdays - Hide Ads */

#pagelet_trending_tags_and_topics {
	display:none;
}

#pagelet_ego_pane {
	display:none;
}

#globalContainer {
	margin: 0;
}

/* Right Column with Birthdays max height */

div#u_0_0{
    max-height: 79px !important;
}

/* The very footer with the Copyright */
#pagelet_rhc_footer{
	display: none;
}

/* Semi-transparent single pages */
.UIStandardFrame_Content {
	background: lightgray;
}


/* Mini Chat... */
/* Float status to left */
._5pr2 ._5bon {
	float: left;
}

/* Hide Name */
._55lr, ._55ls {
	display: none;
}

/* Correct Width */
._5vb_ ._5pr2.fbChatSidebar, .fbCalendar ._5pr2.fbChatSidebar, ._1t4w ._5pr2.fbChatSidebar, .timelineLayout ._5pr2.fbChatSidebar {
	width: 98px;
}

/* Push Chat Window back to the sidebar */
.sidebarMode ._48gf {
	right: 100px;
}

/* Sticker window */
.emoticonsPanel div._5r8f.panelFlyout {
	right: -112px;
}

/* First Chat Window Goes Right Next to the Sidebar */

.sidebarMode ._48gf 
{
  right: 85px;
}

/* Chat textbox float left */
div._552h {
    float: left;
    bottom: 5px;
    z-index: 999;
    width: 50%;
}

/* Textbox icons drop down a little */
._5q5b ._552n {
    top: 2px;
}

/* Textbox icons float right insted of bottom line */
._552n > * {
    float: right;
}

/* Big thumb hide */

._5g2o, span._5ep3 {
    display: none;
}

/* Show photo uploads */

div._552n > div._6a._552o:first-child {
    display: block;
}

/* Hide file uploads */

div._552n > div._6a._552o {
     display: none;
}

/* Fix Compose Box Status/Picture buttons to sit in one line */

div#feedx_container div div div ul li:last-child {
    width: 100px !important;
}

/* Recommended Games Remove */
#pagelet_games_rhc {
    display: none;
}

/* Main Content Colump */
div#contentCol.homeWiderContent div#contentArea{
    max-width: 470px !important;
}

div#contentArea div._3x-2  *{
    max-width: 440px !important;
}
}
