css:
/* Dark UofT Library by siliconworm */
@-moz-document 
domain("onesearch.library.utoronto.ca"),
domain("query.library.utoronto.ca"),
url-prefix(http://getit.library.utoronto.ca)
{

body { 
background: #181818 !important;color: #C0C0C0;
}

#page, #container {
background: #293134!important;color: #CACACA!important;
}

#full_top #mini-panel-front_page_search .row .panel-pane {
background: #1E202C!important;
}

#simple-search-box {
background: #1E242C!important;
color: #3F0!important;
}

.sortOptions {
color: #6EEBEB !important;
}

.breadcrumb {
background-color: #375985!important;
}

#navigation_container {
background-color: #1F323A!important;
}

.search-wrapper {
background: rgb(27, 38, 53)!important;
}

.page-onesearch #block-panels-mini-all-results-page-search-panel .search-all {
background: #1E202C!important;
}

.front #content-inner .front-block-item {
background: -webkit-gradient(linear,0% 0,0% 100%,from(#292F38),to(#364555))!important;
}

#advanced_search_form > div {
background: -webkit-gradient(linear,0% 0,0% 100%,from(#292F38),to(#364555))!important;
}

#left_column {
background: -webkit-gradient(linear,0% 0,0% 100%,from(#292F38),to(#364555))!important;
}

input[type='text'] {
background: #1A162B!important;
color: lime!important;
}

select {
background: #1A162B!important;
color: lime!important;
}

#results_container {
background: #1E2327!important;
}

.page-onesearch .main-container .col-md-4 {
background: -webkit-gradient(linear,0% 0,0% 100%,from(#292F38),to(#364555))!important;
}

.page-onesearch .main-container header a {
background: transparent!important;
}

.page-onesearch .main-container header a {
color: #F5FF00 !important;
}

.page-onesearch .main-container h1:hover, .page-onesearch .main-container h2:hover, .page-onesearch .main-container h3:hover, .page-onesearch .main-container h4:hover, .page-onesearch .main-container h5:hover, .page-onesearch .main-container h2 a:hover {
background: none;
color: #59D0FF !important;
transition: color .2s;
}

#center_column {
background: rgb(38, 57, 77)!important;
}

.page-onesearch .box {
background-image: linear-gradient(to bottom,#343A39,#4F5D61 75px,#313A4D 75px,#424953 100%)!important;
}

.page-onesearch .selected-box {
background-image: linear-gradient(to bottom,#5A2C27,#AC6666 75px,#2D323F 75px,#3D5574 100%) !important;
}

.page-onesearch .results-header {
background: #213650 !important;
}

.pane-list-of-libraries, .pane-endeca-covers, .donate {
background: #272A38!important;
}

.front .main-container {
background: #1C1F29!important;
}

.not-front .main-container {
background: rgb(31, 31, 43)!important;
}

.search input[type='text'], .searchButton, .s-lg-form-control, .s-lg-btn, #la_qformbutton, .s-lg-btn-api-drop {
background: #333!important;
color: #70FF64!important;
}

#hitlist_container div:last-child {
color: #C9D9E7!important;
}

.hitlist_description {
color: #999!important;
}

.navigation_container, .nav_save_all, .nav_saved_items {
background-color: #424C5A;
}

#saved_items_container {
background-color: rgba(34, 35, 36, 0.92);
}

#other_results_container {background-color: #292929;}

.hitlist_link_bar {
background-color: transparent;
}

#portal-header {
background: -webkit-gradient(linear,0% 0,0% 100%,from(#30393D),to(#425161));
}
#visualWrapper {
background-color: #D4D5CE;
}

.full-text-wrapper {
background: -webkit-gradient(linear,0% 0,0% 100%,from(#30393D),to(#425161));
}
.permitted-uses-wrapper {
background: #50575C!important;color: #CACACA!important;
}

.citation {
background: #425159!important;color: #CACACA!important;
}
.citation-title {
background: -webkit-gradient(linear,0% 0,0% 100%,from(#425159),to(#425161));
}

#block-views-utl-news-block, #block-views-workshops-block , #block-views-featured-item-block-1 {
background: #24282C;
}



element.style {
	color: #C0C0C0!important;
}


h1 {color: #81A5F8  !important;}
h2 {color: #9BC893  !important;}
h3 {color: #C4ECEE!important;}
p {color: #C0C0C0!important;}
strong, b {color: #CACACA!important;}
.tag {color: #A0A9B1 !important;}
#onesearch_form label {color: #6BFF6B;}

/*new updates*/
.stream_new_entry, #left_stream_alerts .stream_item.stream_new_entry:hover {
background-color: #386080;
}

/* defaults for all links */
a:link, .link, a font[color], .mn-hd-txt, 
    a.fl:link, .fl a, .flt, a.flt, .gl a:link, a.mblink, .mblink b, ._Hg ._qA, ._Hg ._Pn, ._Hg ._Uq, #gt-feedback, #gb a  { 
        color: #7C9EDC !important; 
        transition: color .4s, text-shadow .4s !important;
    }
                    
a:visited { 
    color: #F92672 !important; 
}

/* hovers */
.menuitem:hover, a:hover, a span:hover, .actbar-btn:hover, #gt-feedback:hover, #gb a:hover {
color: #E5E5E5 !important; text-shadow: #ffffff 0px 0px 2px !important;
transition: color .4s, text-shadow .4s !important;
}

/* big link visited */
#res .g a:visited { opacity: 0.5 !important; }

/* URL text */
.a, cite, .cite, .cite:link, cite b, #resultStats { color: #999 !important; }

/* dashes between Cached and Similar links */
.gl { color: #999 !important; }

/* disable dashed outline on focused elements */
:focus { outline: none; }

/* image thumbnails */
#res .g img, #ImgContent td img {
  border: none !important;
box-shadow: 4px 4px 10px rgba(0,0,0,0.8) !important;
}

/* Main text color */
div, td, font[color="#000000"], .qbtbha .qbtbtxt.qbclr, .gt-rw-span, .gt-syn-span, .gt-card-ttl-txt, #contribute-target, #source { color: #C0C0C0 !important; }

/* fix for google suggest */
.aAutoComplete .cAutoComplete, .google-ac-a .google-ac-c, .gac_a > .gac_c { color: #CCCCCC !important; }
.aAutoComplete .dAutoComplete, .google-ac-a .google-ac-d, .gac_a > .gac_d { color: white !important; }
.google-ac-e td, .gac_e { color: #380000 !important; }

/* make sure web forms (and google suggest) don't get their colors changed */
input *, textarea *, .aAutoComplete {
    background: -moz-Field !important;
    color: #999999 !important;
}

}
/* Dark UofT Library by siliconworm */
