@-moz-document domain("framapic.org") {
  /* 
  FRAMASPHERE 
  */

  #framanav .frama {
    color: #08C;
  }

  #framanav .soft {
    color: #222222;
    font-weight: 600;
  }

  /* NAVBAR */
  /*
  #framanav.navbar {
  background: #222222 !important;;
