  #framanav.navbar {

    border-top: 0px solid transparent;
    border-bottom: 1px solid transparent;
    position: fixed;
    width: 100%;
  }

  hr {
    border-color: #111111;
    opacity: 0.9;
  }


  /* FOOTER */

  #framafooter {
    background: #f2f2f2;
  }
  /*
  SERVICE NAME
  */
  .frama {
    color: white;
    font-weight: 400;
  }

  .services {
    color: #08C;
  }

  .lead {
    font-size: 15px;
    color: #f2f2f2;
  }

  /* BOTTOM BLOCS */

  #tuto-faq, #le-logiciel, #jardin {
    font-size: 0.8em;
    box-shadow: none;
    border: 1px solid transparent;
    min-height: 285px;
    width: 32%;
    background: #111111;
    color: #f2f2f2;
  }


  #tuto-faq h2, #le-logiciel h2, #jardin h2 {
    font-size: 1.6em;
    text-align: center;
    color: #f2f2f2;
  }

  #tuto-faq, #le-logiciel {
    margin-right: 10px;
  }


  /*
  GLOBAL
  */

  body {
    background: #222222;
    font-family: "Helvetica";
    font-size: 14px;
  }

  body > .container {
    color: white;
  }

  .container {

  }

  /* FORMS */

  input, button, select, textarea {
    font-family: inherit;
    font-size: inherit;
    line-height: inherit;
    background: #111111;
    border: 1px solid #111111;
    padding: 2px;
  }

  .form-control {
    background: #111111;
    border: 1px solid black;
  }

  option:hover {
    background: #08C;
  }

  /* UPLOADER */

  .uploader.well {
    background: #111111;
    color: white;
    border: 1px solid transparent;
    border-radius: none !important;;
    box-shadow: none;
  }

  .uploader .btn label {
    font-weight: normal;
    padding-top: 7px;
    padding-bottom: 0px;
    font-size: 1.5em;
    content: "Parcourir" !important;
    border-radius: 28px;
  }
  
  .uploader .btn label input {
    padding: 5px;
  }

  /* LIENS */

  a {
    color: #08C;
    text-decoration: underline dotted;
  }

  a:hover {
    color: #222222;
  }

  .container.ombre {
    box-shadow: none !important;
    background: none;
  }

  /* TITRES */
  h1, h2, h3, h4, h5, h6 {
    font-family: "Helvetica";
    font-weight: bold;
    color: #222222;
  }

  .trait {
    height: 1px;
    background-color: #111111;
  }

  main > .row > .col-md-12 > h2:first-child {
    display: none;
  }

  /*
  NAVS
  */

  .nav-tabs {
    border-bottom: 1px solid transparent;
  }

  .nav-tabs > li.active > a, .nav-tabs > li.active > a:hover, .nav-tabs > li.active > a:focus {
    border-color: white;
    border-radius: 38px;
  }

  .nav-tabs > li > a {
    border-radius: 28px;
  }

  .nav > li > a:hover, .nav > li > a:focus {
    text-decoration: none;
    background-color: white;
    border-bottom: 1px solid transparent;
  }

  /* ALERTS */

  .alert a {
    text-decoration: none;
  }

  .alert-danger {
    background: #111111;
    border: none;
  }

  .alert a:hover {
    text-decoration: underline !important;
  }

  .alert-success a:not(.btn), .alert-success a:hover:not(.btn), .alert-success {
    color: white;
    background: #08C;
    border-color: transparent;
    border-radius: 0px;
  }

  button.close {
    color: white !important;
  }


  /* HELP BLOCK */
  .help-block {

  }

  /* ENVOI */

  #expiration, #burnafterreading {
    border: none;
    box-shadow: none;
    font-size: 0.9em;
    background: transparent !important;
    color: #f2f2f2;
  }

  #expiration:hover, #burnafterreading:hover {
    background-color: white;
  }

  /* BOUTONS */

  .btn-primary {
    color: #FFF;
    text-shadow: 0px 0px 3px rgba(0, 0, 0, 0.7);
    background-color: #08C;
    border-color: white;
  }

  .btn-primary:hover {
    background: #222222;
    border-color: white;
  }

  .btn-info, .dropdown-toggle.btn-info {
    color: #FFF;
    background-color: #08C;
    border-color: #0C5B7A;
  }

  .btn-info:hover, .btn-info:focus, .btn-info:active, .btn-info.active, .open > .dropdown-toggle.btn-info {
    color: #FFF;
    background-color: #222222;
    border-color: #0C5B7A;
  }
}
