css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("coyotus.com") {

  *{
    font-family: monospace !important;font-size: 13px !important;
  }
  
  div#brdtitle.inbox, .postsignature {
    display: none!important;
  }
  
  .inbox, .postright {
    background: #151515 !important;
    color: #959595 !important;
  }
  
  .pun .blockpost .postbody, .pun .blockpost .postfoot {
    background: none repeat scroll 0 0 #151515;
    border-color: #121212;
  }
  
  .pun .punwrap {
    background: none repeat scroll 0 0 #151515;
    border: 8px solid #202020;
  }
  
  .pun .punwrap {
    /*box-shadow: 3px 3px 3px gray /*#998B54;*/
    border-color: rgba(153, 139, 84, 0.2 )!important;
    border: 1px solid rgba(153, 139, 84, 0.2 )!important;
    margin: 10px auto;
    padding: 10px;
    width: 70% !important;
  }
  
  .pun a, .pun a:link, .pun a:visited {
    color: rgba(153, 139, 84, 0.6 )!important;
  }
    
  #brdmenu a:link, #brdmenu a:visited {
    color: gray;
  }
  
  .pun .postmsg, #punhelp code, #punhelp samp {
    color: #757575;
  }
  
  .pun .icon {
    visibility: hidden !important;
  }
  
  .pun .blocktable th {
    background: none repeat scroll 0 0 #141414;
    border-color: rgba(153, 139, 84, 0.2 )!important;
    color: white;
  }
  
  .pun .blocktable .box {
    background: none repeat scroll 0 0 #151515;
    border-color: rgba(153, 139, 84, 0.2 )!important;
  }
   
  
  tr.roweven {
    background: none repeat scroll 0 0 #151515;
  }
  
  tr.rowodd {
    background: none repeat scroll 0 0 #131313;
  }
  
  #brdmenu ul li.isactive {
    background: none repeat scroll 0 0 #101010!important;
    border-bottom: 1px solid #222;
    border-top: 1px solid #000;
    border-right: 1px solid #000;
    border-left: 1px solid #000;
  }
  
  .pun .quotebox, .pun .codebox, #punhelp .codebox code {
    background: none repeat scroll 0 0 #111;
    border-color: #222;
    color: rgba(178, 223, 231, 0.4) ;
  }

  .pun .quotebox cite {
    color:#333!important;
  }
  
  .pun .blockpost h2 {
    background: none repeat scroll 0 0 #101010;
    border-color: #202020;
    color: #3e3e3e;
  }
  
  .pun .postsignature, .pun .postmsg .postedit {
    color: #3e3e3e;
  }

  .pun label, .pun legend, #adminconsole fieldset th , .pun .blocktable .tcl, .pun .blocktable .tc2, .pun .blocktable .tc3, .pun .blocktable .tcr {
    color: #3e3e3e !important;
  }
  
  .pun h2 {
    color: gray;
  }
  
  html, body, .pun {
    background: #141414!important;
    color: #bebebe;
  }
  
  .blockpost {
    border:0px solid #fff!important;
  }
  
  .pun label input, .pun label select, .pun label textarea {
    background: #101010!important;
  }
 
  input[type=text], input[type=password], select, textarea {
    color: rgba(153, 139, 84, 0.6 );
    border-color: #202020;
  }

  input[type="text"]:focus, input[type="password"]:focus, select:focus, textarea:focus {
    color: rgba(153, 139, 84, 0.6 );
    border-color: #303030;
  }

  .pun input {
    background-color: #222!important;
    border: 1px solid #444!important;
    color: #777!important;
  }
  

}
