css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("wuggee.com") {

  body {
    font: 12px/2 "Open sans",sans-serif;
    color: #1D4F73;
    background-color: #181D20;
}
  div.movie{
    border: 2px solid;
    border-radius: 25px;	
	box-shadow: 5px 5px 5px #000;
        background-color: #181D20;
    height: 451px !IMPORTANT;

  }
  .movie-image{
    border: 2px inset solid;
	box-shadow: 5px 5px 5px #000;
    margin-top: 35px;
    
  }
.group {
    border-radius: 25px;
}
  div.movie-actions {
    z-index: 9999 !IMPORTANT;
  }
  .wuggee-rating{
        border: 2px solid;
    border-radius: 25px;	
	box-shadow: 5px 5px 5px #000;
         
  }
div#filters {
    background-color: #343C45;
COLOR: black;
}
  
  div#filters a#filter-movies {
    background-color: #252C33;
}
  div.which-buttons a {
    background: #252C33 url("http://wuggee.com/images/icon-arrow-right.png") no-repeat scroll right 12px center;
}
  .basic-info {
    display: block;
    z-index: 9999;
    position: relative;
    background-color: rgb(20, 23, 26);
    border-top: 1px solid;
    border-left: 1px solid;
    border-right: 1px solid;
    padding: 5px;
    margin-right: 30px;
      width: 240px !IMPORTANT;

}
a.movie-info-toggle{    
z-index: 9999;
    position: relative;
  }
  
  .showing{
    top: -380px;
  }
  ul.movie-ratings{
    z-index: 9999 !IMPORTANT;
    position: relative;
        color: #1D4F73;
    background-color: #181D20;
    border-right: 1px solid;
    border-left: 1px solid;
    border-bottom: 1px solid;
      width: 230px !IMPORTANT;
padding: 10px;
  }
  div.top-overlay {
        border: 1px solid;
    border-radius: 25px 25px 0 0;	
	box-shadow: 5px 5px 5px #000;
height: auto !IMPORTANT;
  }

  .movie-title{
            color: #0371B7;
        background-color: #181D20;
            border: 1px solid;
        border: 2px ridge ;

    padding-top: 5px;
    padding-left: 5px;
  }
div.movie div.details {
    padding: 0;
}
  
  div.friends_hated {
    margin-right: 20px;
         opacity: 1.0 !IMPORTANT;
  }
   div.wuggee-rating{
    border-radius: 25px;	
    margin: 10px 12px;
 
  }
  div.wuggee-rating div.wuggee-rating-inner span{
    border-radius: 25px;	
 
  }
  div.movie div.group {
    background:#181D20;
    background-color: none !IMPORTANT;

  }
  h2 {
    margin-top: 0em;
        color: #1D4F73;

}
  
  div.movie:nth-child(4n+4) {
    margin-right: 45px;
}
  
  .footer {
    width: 100%;
    text-align: center;
    font-size: x-small;
    margin: 13px auto 50px;
}
  
}
