css:
@-moz-document domain("alldomain.com") {

/*
Name: 全局广告过滤
Version: 1.01
Author: ezibo
Description: This style sheet hide elements that you want.
*/

/*通配符广告过滤，杀伤力很大，慎用*/
/*
div[id*="banner"],[id*="ads"],[id*="Adflash"]{display: none !important}
*/

/* Classes */
.ad, .Ad, .ad-banner, .Ad-banner, .Ad-Banner, .ad_banner, .Ad_banner,
.Ad_Banner, .adbanner, .Adbanner, .adbar, .Adbar, .advert, .Advert,
.advertisement, .Advertisement, .ad-block, .Ad-block, .Ad-Block, .ads,
.Ads, .banner-ad, .Banner-ad, .Banner-Ad, .banner_ad, .Banner_ad,
.Banner_Ad, .dropin, .Dropin, .DropIn, .dwindow, .Dwindow, .DWindow,
.banner1, .Banner1, .banner2, .Banner2, .banner3, .Banner3, .banner4,
.Banner4, .banner5, .Banner5, .banner6, .Banner6, .sponsor, .Sponsor,
.sponsors, .Sponsors, .reclame, .Reclame, .werbung, .Werbung
{ display: none !important }

/* 隐藏文字 */
span[style="font-size: 0pt"],span[style="font-size: 0px"] {display: none !important;}
font[style="font-size: 0pt"],font[style="font-size: 0px"] {display: none !important;}
font[style="font-size: 0px; COLOR: #ffffff"] {display: none !important;}
div[style="font-size: 0pt"],div[style="font-size: 0px"] {display: none !important;}

}
