css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.computerbase.de") {
/* Farbe für Headlines, Verknüpfungen ändern  */
.article__title, .teaser__title, a, .vb-nav a, .geizhals__price, .footer1-box em {
	color: #18355F;
}
/* Farbe für Verknüpfungen im Fliesstext ändern */
p>a {
	color: #222;
}
/* Kennzeichnung für Verknüpfungen im Fliesstext */
p>a::before {
	content: "➦ ";
	font-size: 0.8em;
	color: #666;
}
/* Artikel Headlines verkleinern und erleichtern */
.article-view__title {
	font-size: 1.5625em;
	font-weight: lighter;
}
.article__title, .teaser__title, .slide__title {
	font-size: 1.1em;
	font-weight: lighter;
}
/* alle Social Media Buttons eliminieren */
.follow-links, .share-links, .article-view__share-links-top {
	display: none;
}
/* Header absolut anstatt fix positionieren; kein Cookie mehr nötig */
.header {
	height: 56px;
	position: absolute;
}
.subheader {
	box-shadow: none;
}
.header--1 .homepage-link {
	transform: none;
}
.header--1 .mainmenu-nav {
	transform: none;
}
}
