css:
@-moz-document url-prefix("http://craftsite.pl/forum/") {
.unread {
background: -webkit-gradient(linear, left top, left bottom, color-stop(60%,rgba(31,33,34,1)), color-stop(100%,rgba(29, 31, 32, 1)));
}
.post_block {
  position: relative;
  background-color: rgb(32, 32, 32);
}
.ipsSideBlock h3 {
    border-radius: 14px 0px 0 0;
}
.ipsSideBlock {
	border-radius: 14px 0px 14px 0;
    border: black 1px solid;
  	box-shadow: 0 1px 5px black;
}      
.block_list>li {
  border-bottom: 1px solid #131313;
}
::selection {
background-color: rgb(83, 130, 140);
color: white;
}
.ipsVerticalTabbed_tabs li a:hover {
    background: #5D7D81;
    color: #FFFFFF;
    transition: 0.5;
}
.ipsUserPhoto {
  padding: 1px;
  border: 1px solid #000000;
  background: #FFFFFF;
}
#inbox_link.menu_active {
    background-position: bottom;
    background-color: #6CB2B2}
    ul.post_controls {
    margin: 0px;
}
#user_link_menucontent #links li {
    width: 50%;
    float: left;
    margin: 0px;
    text-shadow: 0px 1px 0 rgba(0, 0, 0, 1);
    white-space: nowrap;
}
.ipsHeaderMenu {
    background: #FFF;
    background: -moz-linear-gradient(top, #fff 0%, #f6f6f6 70%, #ededed 100%);
    background: rgb(26, 26, 26);
}
.post_block.feature_box {
    background-color: #293223;
    border: 1px dotted #333;
    padding: 6px;
    min-height: 60px;
    word-wrap: break-word;
}
    .userpopup h3, .userpopup .side+div {
    padding-left: 110px;
    background-color: rgb(31, 31, 31);
}
    .ipsLayout_smallleft.ipsLayout_withleft {
    padding-left: 150px;
    background-color: rgb(26, 26, 26);
}
    #community_app_menu>li>a:hover, #community_app_menu>li>a.menu_active {
    background-color: rgb(18, 69, 66);
    background-position-y: 1px;
    background-position-x: 0;
    color: #FFF;
    background-image: none;
}
.ipsVerticalTabbed_tabs li {
    background: #2C2C2C;
    color: #808080;
    border-bottom: 1px solid #545454;
    font-size: 13px;
}
.ipsVerticalTabbed_tabs li.active a {
    width: 132px;
    position: relative;
    z-index: 8000;
    background: #263638;
    color: #E7E7E7;
    font-weight: bold;
    border: none;
}
#profile_background {
	background: rgb(26, 26, 26) !important;
    background-image: none !important;
}
p.citation {
    font-size: 12px;
    padding: 8px 10px;
    border-left: 2px solid #000000;
    background: rgb(20, 26, 27);
    border-top: 2px solid #000000;
    border-right: 2px solid #000000;
    -moz-border-radius: 5px 5px 0 0;
    -webkit-border-radius: 5px 5px 0 0;
    border-radius: 5px 5px 0 0;
    font-weight: bold;
    overflow-x: auto;}
.post_body .post {
    color: #BABABA;
}
.ipsList_inline>li:first-child {
	margin-left: 10px;
}
.post_block h3 {
    background: rgb(27, 28, 29);
    padding: 0 10px;
    height: 36px;
    line-height: 36px;
    font-weight: normal;
    font-size: 16px;
}
blockquote.ipsBlockquote {
    font-size: 12px;
    padding: 10px;
    border: 2px solid #000000;
    border-left: 2px solid #000000;
    -moz-border-radius: 5px;
    -webkit-border-radius: 5px;
    border-radius: 5px;
    background: #303030;
    margin: 0 0;
    overflow-x: auto;
}
.ipsVerticalTabbed_tabs>ul {
  width: 149px !important;
  margin-top: 10px;
  border-top: 1px solid #545454;
  border-left: 1px solid #545454;
}
.general_box h3 {
    font: normal 14px helvetica, arial, sans-serif;
    padding: 8px 10px;
	background: #222222 !important;
    color: #CBCBCB;
}
.general_box {
    background: #2F2F2F;
    margin-bottom: 10px;
}
#content {
    background: rgba(34, 34, 34, 0.95) !important;
 	box-shadow: 0 5px 5px rgba(0, 0, 0, 1);
    -moz-box-shadow: 0 5px 9px white;
	box-shadow: 0 5px 9px rgba(0,0,0,0.1);
    max-width: 87%;
    min-width: 980px;
    margin-left: auto;
    margin-right: auto;
}
.maintitle {
    background-color: rgba(0, 0, 0, 0.66);
  	background-image: none;
    background-repeat-x: repeat;
    background-repeat-y: no-repeat;
    background-position: left top;
    color: #fff;
    font-size: 16px;
    font-weight: bold;
    -moz-border-radius: 6px 6px 0 0;
    -webkit-border-top-left-radius: 4px;
    -webkit-border-top-right-radius: 4px;
    border-radius: 4px 4px 4px 4px;
    -webkit-box-shadow: none;
    -moz-box-shadow: none;
    box-shadow: none;
    border-bottom: rgb(30, 32, 33) 2px solid;
  	font-family: tahoma;
}
.ipsBox {
  background: rgba(26, 26, 26, 1) !important;
  border: black 1px solid;
  -webkit-box-shadow: inset 1px 17px 36px -3px rgba(0,0,0,0.6);
}
.ipsType_pagetitle, .ipsType_subtitle {
	font: 300 26px/1.3 Helvetica,Arial,sans-serif;
	color: #D0D0D0;
}
.post_wrap {
	top: 0px;
	background-color: rgb(32, 32, 32);
    margin-bottom: -1px;
}
.statistics {
    margin: 20px 0 0 0;
    padding: 10px 0;
    border-top: 3px solid #626262;
    line-height: 1.3;
    overflow: hidden;
}
.ipsSideBlock h3 {
  font: normal 14px helvetica, arial, sans-serif;
  color: #FFF;
  padding: 5px 15px;
  background-color: rgba(26, 26, 26, 1);
  text-shadow: 1px 1px 1px rgb(0,0,0);
  background-image: none;
  height: 20px;
  border-bottom: rgb(79, 79, 79) 2px solid;
}
#primary_nav {
    background-color: rgba(0, 0, 0, 0.91);
    background-image: none;
    background-repeat: no-repeat, no-repeat, repeat-x;
    background-repeat-y: no-repeat;
    background-position: right top, left top, left top;
    font-size: 13px;
    height: 51px;
    width: 87%;
    min-width: 960px;
    margin: 30px auto 0 auto;
    padding: 0 10px;
    position: relative;
    left: auto;
    right: auto;
}
#user_navigation ul.ipsList_inline {
    background-color: rgba(0, 0, 0, 0.8);
    background-image: none;
    background-repeat: no-repeat, no-repeat, repeat-x;
    background-position: top left, top right, top left;
    box-shadow: 2px 4px 7px -4px #000;
    padding: 0 25px 0 20px;
	border-radius: 0 0 5px 5px;
}
#admin_bar{
    background-color: rgba(0, 0, 0, 0.8);
    background-image: none;
    background-repeat: no-repeat, no-repeat, repeat-x;
    background-position: top left, top right, top left;
    box-shadow: 2px 4px 7px -4px #000;
    padding: 0 25px 0 20px;
	border-radius: 0 0 5px 5px;
}
#li.active a {
	color: #FF0000;
 	font-weight: 300;
}
.a:hover {
  	background-color: #0FABB2;;
  	background-image: none;
    transition: 0.5s;
}
.post_block.row1 {
	background-color: #000;
    }
.row1, .post_block.row1 {
	background-color: #1A1A1A;
}
.ipsBox {
	background: rgba(0, 0, 0, 0.73);
}
.ipsBox_container {
	background: #1A1A1A;
    border: 1px solid #2D2D2D;
}
.row2 td {
    border-bottom: 1px solid #202020;
}
.row2, .post_block.row2 {
    background-color: #282828;
    border: 2px solid rgb(53, 53, 53);
}
a:hover {
    color: #0FABB2;
    transition: 0.5s
}
a {
  color: #B3B3B3;
  transition: 0.5s;
}
#category_shoutbox .input_text {
    width: calc(100% - 335px);
    box-sizing: border-box;
    padding: 4px;
    border-width: 1px;
    border-style: solid;
    border-color: black;
    background: #848484;
    -moz-border-radius: 2px;
    -webkit-border-radius: 2px;
    border-radius: 2px;
}
body {
    background-position: center top;
    background-repeat: no-repeat;
    background-attachment: fixed;
    background-size: cover;
    color: #ECECEC;
    margin: 0;
}
.ipsSideBlock {
    background: #2C2C2C;
    padding: 10px;
    margin-bottom: 10px;
}
#shouts-global-resizer, #shouts-resizer {
    background-color: #202020;
    cursor: n-resize;
    height: 6px;
}
table.ipb_table td {
    padding: 10px;
  	border-bottom: 1px solid #2D2D2D;
    color: rgb(124, 124, 124);
}
.post_block.row1 {
  	background-color: #000;
}
.post_block.row2 {
 	background-color: #272727;
}
.row2, .post_block.row2 {
  	background-color: #282828;
}
#shoutbox-shouts-table .row2 td { 
  	border-bottom: 1px solid #4C4C4C;
  	vertical-align: middle;
}
.userpopup h3 {
  background: rgb(24, 24, 24);
  border-bottom: rgb(226, 226, 226) 2px solid;
}
.ipsType_pagetitle, .ipsType_subtitle {
    font: 300 26px/1.3 Helvetica,Arial,sans-serif;
    color: #D2D2D2;
}
body#ipboard_body fieldset.submit, body#ipboard_body p.submit {
   background-color: #1A1A1A;
}
div.bbc_spoiler_wrapper {
    border: 1px inset #777;
    padding: 4px;
    background-color: rgb(42, 42, 42);
}
.poll_question ol {
  background-color: #191E24;
}
.poll_question {
  padding: 10px;
  margin: 10px 10px 10px 10px;
}
.poll_question h4 {
  background-color: #344659;
  margin: 0 -7px;
  padding: 5px;
  border: rgb(25, 30, 36) 5px solid;
}
.general_box h3 {
      background: #263638;
}
.progress_bar.topic_poll {
  border: 1px solid #35587B;
}
.userpopup .user_status {
  border-radius: 5px 5px 5px 5px;
}
.moderated, .moderated a {
  color: #6f3642;
}
.ipsSideMenu ul li.active a {
  background: #2F4262;
}
.ipsSideMenu ul li {
  border-bottom: 1px solid #494949;
}
.ipsSideMenu h4 {
  color: #C3C3C3;
}
.header {
  background: #2A394C;
  color: #7E9BBD;
}
.ipsComment {
  border-bottom: 1px solid #424242;
}
.ipb_table .header a, body .topic_options a {
  color: #617FA1;
}
.ipsType_textblock {
  color: #C3C3C3;
}
.message {
  background: #233523;
  border: 1px solid #233523;
  color: #C5C5C5;
}
.mini_cal td {
  border: 1px solid #444444;
}
.moderated .row2 {
  background-color: #773A3A;
}
.moderated, .moderated a {
  color: #D37F7F;
}
.ipsList_withminiphoto > li .list_content {
  margin-left: 40px;
  color: rgb(182, 182, 182);
}
.ipsLikeBar_info {
  background: #535B91;
}
.ipsBadge_green {
  background: #4955A8;
}
.reputation.positive, .members li.positive {
  background: #343B71;
}
.bar {
  background: #2C2C2C;
}
.ipsButton_secondary {
  background: #f6f6f6;
  background: -moz-linear-gradient(top, #f6f6f6 0%, #e5e5e5 100%);
  background: rgb(44, 44, 44);
  border: none;
  -webkit-box-shadow: 0px 1px 0px rgba(255,255,255,1) inset, 0px 1px 0px rgba(0,0,0,0.3);
  box-shadow: 0px 1px 0px rgba(55, 55, 55, 1) inset, 0px 1px 0px rgba(0,0,0,0.3);
  -moz-border-radius: 3px;
  color: #DFDFDF;
}
.ipsButton_secondary:hover {
   background-color: rgb(83, 130, 140);
   color: white;
}
.user_controls li a {
  background: #f6f6f6;
  background: -moz-linear-gradient(top, #f6f6f6 0%, #e5e5e5 100%);
  background: rgb(61, 61, 61);
  border: none;
  -moz-box-shadow: 0px 1px 0px rgba(255,255,255,1) inset, 0px 1px 0px rgba(0,0,0,0.3);
  -webkit-box-shadow: 0px 1px 0px rgba(255,255,255,1) inset, 0px 1px 0px rgba(0,0,0,0.3);
  box-shadow: 0px 1px 0px rgba(61, 61, 61, 1) inset, 0px 1px 0px rgba(0,0,0,0.3);
}
#user_latest_status>div {
  background-color: #233523;
  color: #EAEAEA;
}
#user_latest_status {
  background: none;
}
.ipsButton_secondary a {
  color: #DFDFDF;
}
.post_block.no_sidebar {
  background-image: none;
  margin-bottom: -1px;
}
.userpopup dl {
  border-bottom: 1px solid #444444;
}
#search_options {
  background: #949494;
}
#search_wrap {
  background: #3A3A3A;
  border: rgb(102, 81, 64) 2px solid;
}
#search_options_menucontent {
  background-color: rgb(56, 56, 56);
}
.unread .altrow, .unread.altrow {
  background-color: #2D2D2D;
}
table.ipb_table {
  background-color: rgb(39, 39, 39);
}
.input_submit.alt {
  background: #282828;
  border-color: #282828;
  color: #464646;
  -moz-box-shadow: inset 0 1px 0 0 #eff3f8, 0px 2px 3px rgba(0,0,0,0.2);
  -webkit-box-shadow: inset 0 1px 0 0 #282828, 0px 2px 3px rgba(0,0,0,0.2);
  box-shadow: inset 0 1px 0 0 #282828, 0px 2px 3px rgba(0,0,0,0.2);
  color: white;
    transition: 0.5s
}
.input_submit {
  background: none;
  color: #B0B0B0;
  -moz-border-radius: 3px;
  -webkit-border-radius: 3px;
  border-radius: 3px;
  -moz-box-shadow: inset 0 1px 0 0 #5c5c5c, 0px 2px 3px rgba(0,0,0,0.2);
  -webkit-box-shadow: inset 0 1px 0 0 #5c5c5c, 0px 2px 3px rgba(0,0,0,0.2);
  box-shadow: none;
  border-color: #273441;
  background-color: rgb(39, 52, 65);
  transition: 0.5s;
}
.input_submit.alt:hover {
  background-color: #454545;
  color: white;
  transition: 0.5s
}
.input_submit:hover {
   background-color: rgb(59, 89, 119);
}  
#search .submit_input {
  background: #665140 url(http://craftsite.pl/forum/public/style_images/master/search_icon.png) no-repeat 50%;
  text-indent: -3000em;
  padding: 0;
  border: 1px solid #665140;
  display: block;
  width: 26px;
  height: 26px;
  position: absolute;
  right: 0;
  top: 0;
  bottom: 0;
  -moz-border-radius: 0 3px 3px 0;
  -webkit-border-top-right-radius: 3px;
  -webkit-border-bottom-right-radius: 3px;
  border-radius: 0 3px 3px 0;
  -webkit-box-shadow: inset 0px 1px 0 rgba(102, 81, 64, 1);
  -moz-box-shadow: inset 0px 1px 0 rgba(255,255,255,0.2);
  box-shadow: inset 0px 1px 0 rgba(102, 81, 64, 1);
}
#search .submit_input {
  border-radius: 0 3px 3px 0;
  -webkit-box-shadow: inset 0px 1px 0 rgba(102, 81, 64, 1) !important; 
  -moz-box-shadow: inset 0px 1px 0 rgba(255,255,255,0.2) !important;
  box-shadow: inset 0px 1px 0 rgba(102, 81, 64, 1) !important;
}
}
