css:
@-moz-document domain("play.pokemonshowdown.com") {
  body {
    background: url("http://pldh.net/media/gallery/rarities/alphaomega.png") no-repeat !important;
    background-color: #220B0B !important;
  }
  
  .ps-room.ps-room-light.scrollable {
    background: url("http://i.imgur.com/RR1c0H2.png?1") no-repeat right top;
    background-color: rgba(0, 0, 0, 0.3);
    color: #FFF;
    text-shadow: 2px 2px 1px #000;
  }
  
  .ps-room.ps-room-light {
    border-left: 1px solid #000;
    background: rgba(0, 0, 0, 0);
  }
  
  .header {
    background: rgba(0, 0, 0, 0.3);
  }
  
  .userbar .username {
    text-shadow: 1px 1px 0px #000, 1px -1px 0px #000, -1px 1px 0px #000, -1px -1px 0px #000;
  }

  .mainmenufooter small:after{
    content:"Made by: Charles Paroissien"; 
    font-size:9pt; 
    color:#cccccc; 
    text-shadow:0 1px black; 
    position:absolute; 
    size:6px; 
    left:230px; 
    top:21px; 
    width: 300px;
  }
  
  .closebutton, .minimizebutton {
    color: #DEDEDE;
  }
  
  .closebutton:hover {
    color: #C9223B;
  }
  
  .minimizebutton:hover {
    color: #2D337C;
  }
  
  a {
    color: #2D327A;
    text-shadow: 1px 1px 1px #000;
  }
  
  a:hover {
    color: #282746;
  }
  
  .chat-log {
    background: rgba(0, 0, 0, 0.7);
    color: #F3F3F3;
    border-bottom: 1px solid #000;
    border-left: 1px solid #000;
  }
  
  .userlist {
    border-right: 1px solid #19191A;
    background: rgba(0, 0, 0, 0.4);
    color: #FFF;
  }
  
  .userlist li {
    border-bottom: 1px solid #000;
  }
  
  .userlist li button:hover {
    background: rgba(0, 0, 0, 0.5);
  }
  
  .chat-log-add {
    border-top: none;
    background: rgba(0, 0, 0, 0.4);
  }
  
  .username {
    text-shadow: 1px 1px 1px #000;
  }
  
  .chat small {
    color: #E1E1E1;
  }
  
  .battleform label {
    background: url("http://i.imgur.com/yFWeHx0.png") no-repeat scroll left center transparent !important;
    padding: 4px;
    color: #FFF;
  }
  
  .menugroup {
    background:rgba(255, 255, 255, 0);
  }
  
  .roomlist a.ilink {
    font-size:14px;
    font-family: Tahoma, Verdana, Segoe, sans-serif;
    font-weight:bold;
    opacity:0.9;
    color:#fbfbfb;  
    text-transform: uppercase;
    text-align:left;
    text-shadow: 1px 1px 1px #4d4d4d,
    1px 0px 1px #4d4d4d;
    background: url(http://i.imgur.com/DqmewkB.png);
    box-shadow:none; 
    border-radius:0px; 
    border-width:0; 
    width:642px;
    padding-left:20px;
    padding-top:10px;
    padding-bottom:12px;
  }
  
  .roomlist a.ilink:hover {
    font-size:14px;
    opacity:0.9;
    font-family: Tahoma, Verdana, Segoe, sans-serif;
    font-weight:bold;
    color:#fbfbfb;  
    text-transform: uppercase;
    text-shadow: 1px 1px 1px #4d4d4d,
    1px 0px 1px #4d4d4d;
    background: url(http://i.imgur.com/UoUnGNL.png);
    box-shadow:none; 
    border-radius:0px; 
    border-width:0; 
    width:642px;
    padding-left:20px;
    padding-top:10px;
    padding-bottom:12px;
  }
  
  .highlighted {
    background: rgba(0, 0, 0, 0.9);
  }
  
  .ps-overlay {
    background: rgba(0, 0, 0, 0.4);
  }
  
  .battleform .roomlist a.ilink {
    width: 205px;
    border: 1px solid #000;
    background: rgba(0, 0, 0, 0.8);
    color: #FFF;
    text-shadow: 2px 2px 1px #000;
    width: 230px;
  }

  .battleform .roomlist a.ilink:hover {
    border: 1px solid #FFF;
    background: rgba(0, 0, 0, 0.8);
    color: #FFF;
    text-shadow: 2px 2px 1px #000;
  }

.mainmenu .menugroup:nth-child(1) .button, .mainmenu .menugroup:nth-child(2) p:nth-child(1) .button, .mainmenu .menugroup:nth-child(2) p:nth-child(2) .button, .mainmenu .menugroup:nth-child(2) p:nth-child(3) .button, .mainmenu .menugroup:nth-child(3) p:nth-child(1) .button, .mainmenu .menugroup:nth-child(3) p:nth-child(2) .button {
    border: 1px solid #000;
    background: rgba(0, 0, 0, 0.8);
    color: #FFF;
    text-shadow: 2px 2px 1px #000;
    width: 230px;
  }
  
  .mainmenu .menugroup:nth-child(1) .button:hover, .mainmenu .menugroup:nth-child(2) p:nth-child(1) .button:hover, .mainmenu .menugroup:nth-child(2) p:nth-child(2) .button:hover, .mainmenu .menugroup:nth-child(2) p:nth-child(3) .button:hover, .mainmenu .menugroup:nth-child(3) p:nth-child(1) .button:hover, .mainmenu .menugroup:nth-child(3) p:nth-child(2) .button:hover {
    border: 1px solid #FFF;
    background: rgba(0, 0, 0, 0.8);
    color: #FFF;
    text-shadow: 2px 2px 1px #000;
  }
  
  .mainmenu .menugroup:nth-child(1) .button {
    width: 270px;
    margin-left: -20px;
  }
  
  .mainmenufooter a, .mainmenufooter {
    color: #FFF;
  }
  
  .mainmenufooter a:hover {
    color: #2C3177;
  }
  
  .message small {
    color: #D4D4D4 !important;
  }
  
  .ps-popup {
    background: rgba(0, 0, 0, 0.95);
    color: #F3F3F3;
  }
  
  .popupmenu h3 {
    color: #2C3176;
  }
  
  .popupmenu button {
    color: #F3F3F3;
  }
  
  .popupmenu button:hover {
    color: #F3F3F3;
    background: rgba(0, 0, 0, 0.7);
    border: 1px solid #FFF;
  }
  
  .maintabbarbottom {
    background: #121212;
    border: 1px solid #000;
  }
  
  .tabbar a.button.notifying, .tabbar a.button.notifying:hover  {
    border-color: #FFF;
    background: #000;
  }
  
  .tabbar a.button.cur, .tabbar a.button.cur:hover {
    background: #121212;
    color: #FFF;
    text-shadow: 2px 2px 1px #000;
    border: 1px solid #000;
  }
  
  .tabbar a.button {
    background: #121212;
    color: #FFF;
    text-shadow: 2px 2px 1px #000;
    border: 1px solid #000;
  }
  
  .tabbar a.button:hover {
    background: #121212;
    color: #FFF;
    text-shadow: 2px 2px 1px #000;
    border: 1px solid #000;
  }
  
  .pm-log-add, .pm-window.focused .pm-log-add {
    background: rgba(0, 0, 0, 0.5);
    border: 1px solid #000;
    border-top: none;
  }
  
  .pm-log, .pm-window.focused .pm-log {
    background: rgba(0, 0, 0, 0.5);
    border: 1px solid #000;
    color: #F3F3F3;
    border-top: none;
    border-bottom: none;
  }
  
  .pm-window h3 {
    background: rgba(0, 0, 0, 0.5);
    border: 1px solid #000;
    border-bottom: none;
  }
  
  .pm-window h3:hover, .pm-window.focused h3, .pm-window.focused h3:hover {
    background: rgba(0, 0, 0, 0.5);
    border: 1px solid #000 !important;
    border-bottom: none;
    color: #FFF;
  }
  
  .pm-window h3 small {
    color:#9E9E9E;
  }
  
  .select {
    border: 1px solid #000;
    background: rgba(0, 0, 0, 0.8);
    color: #FFF;
    box-shadow: none;
    border-top-left-radius: 0px;
  }
  
  .select:hover {
    border: 1px solid #FFF;
    background: rgba(0, 0, 0, 0.8);
    color: #FFF;
  }
  
  .select:disabled, .select:disabled:hover {
    border: 1px solid #000;
    background: rgba(0, 0, 0, 0.3);
    color: #FFF;
  }
  
  .select.preselected, .select.preselected:disabled {
    color: #FFF;
  } 
  
  .challenge {
    margin-top: -1px;
    background: rgba(0, 0, 0, 0.5);
    border: 1px solid #000;
    border-bottom: none;
    border-top: none;
    color: #FFF;
    padding: 1px 5px;
  }
  
  .message-pm {
    color: #C0243E;
  }
  
  .infobox {
    border: 1px solid #000;
    background: rgba(0, 0, 0, 0.2);
    color: #000;
    text-shadow: none;
  }
  
  
  .broadcast-red {
      background: #D57575;
      border: 1px solid #985555;
      color: #FFD7D7;
      padding: 5px 1px 3px 8px;
      text-shadow: 1px 1px 1px #985555;
      font-family: helvetica, arial, sans-serif;
      text-align: center;
  }

  .broadcast-green {
      background: #578A54;
      border: 1px solid #375735;
      padding: 5px 1px 3px 8px;
      color: #BEFFB9;
      text-shadow: 1px 1px 1px #375735;
      font-family: helvetica, arial, sans-serif;
      text-align: center;
  }

  .broadcast-blue {
      background: #677992;
      border: 1px solid #414D5E;
      padding: 5px 1px 3px 8px;
      color: #C1DAFF;
      text-shadow: 1px 1px 1px #414D5E;
      font-family: helvetica, arial, sans-serif;
      text-align: center;
  }

  .broadcast-green a {
    color: #BEFFB9;
  }

  .broadcast-green a:hover {
    color: #8EFF86;
  }

  .broadcast-blue a {
    color: #C1DAFF;
  }

  .broadcast-blue a:hover {
    color: #91BDFF;
  }

  .broadcast-red a {
    color: #FFD7D7;
  }

  .broadcast-red a:hover {
    color: #FFB5B5;
  }

  .broadcast-red button {
    border: 1px solid #985555;
    background: #C36C6C;
    color: #FFD7D7;
    margin-bottom: 2px;
  }

  .broadcast-red button:hover {
    background: #D57575;
  }

  .broadcast-green button {
    border: 1px solid #375735;
    background: #4A7648;
    color: #BEFFB9;
    margin-bottom: 2px;
  }

  .broadcast-green button:hover {
    background: #578A54;
  }

  .broadcast-blue button {
    border: 1px solid #414D5E;
    background: #566477;
    color: #C1DAFF;
    margin-bottom: 2px;
  }

  .broadcast-blue button:hover {
    background: #677992;
  }

  
  /******************
  * Teambuilder CSS *
  ******************/
  
  
  .setmenu button {
    color: #FFF;
    text-shadow: 1px 1px 1px #000;
    border: 1px solid #202020;
    border-bottom: none;
    background: rgba(0, 0, 0, 0.1);
  }
  
  .setmenu button:hover {
    color: #FFF;
    text-shadow: 1px 1px 1px #000;
    border: 1px solid #000;
    border-bottom: none;
    background: rgba(0, 0, 0, 0.5);
  }
  
  .setchart, .setchart-nickname {
    background: rgba(0, 0, 0, 0.7);
    border: 1px solid #000;
    box-shadow: 1px 1px 0px rgba(255, 255, 255, 0.1) inset;
  }
  
  .teambuilder-results .result a:hover, .teambuilder-results .firstresult a {
    background: rgba(0, 0, 0, 0.5);
    border: 1px solid #000;
  }
  
  .teambuilder-results .firstresult a:hover {
    background: rgba(0, 0, 0, 0.7);
    border: 1px solid #000;
  }
  
  .utilichart b {
    color: #FFF;
  }
  
  .utilichart .pokemonnamecol {
    color: #FFF;
  }
  
  .utilichart .col {
    color: #E4E4E4;
  }
  
  .utilichart .labelcol em, .utilichart .widelabelcol em, .utilichart .pplabelcol em, .utilichart .statcol em, .utilichart .bstcol em {
    color: #D0D0D0;
  }
  
  .teambuilder-results h3, .teambuilder-results .utilichart h3 {
    background: rgba(0, 0, 0, 0.7);
    border: 1px solid #000;
    text-shadow: 0px 0px 1px #000;
    box-shadow: none;
  }
  
  .setchart label, .setchart-nickname label {
    color: #FFF;
    text-shadow: 1px 1px 1px #000;
  }
  
  .setchart input, .setchart-nickname input, .statform input.inputform, .textbox {
    border: 1px solid #000;
    background: rgba(0, 0, 0, 0.8);
    color: #F3F3F3;
    box-shadow: 0px 1px 2px #404040 inset, 1px 1px 0px rgba(10, 10, 10, 0.6);
  }
  
  .setchart input:hover, .setchart-nickname input:hover, .statform input.inputform:hover, .textbox:hover {
    border: 1px solid #000;
    background: rgba(0, 0, 0, 1);
    color: #F3F3F3;
    box-shadow: 0px 1px 2px #252525 inset, 1px 1px 0px rgba(0, 0, 0, 0.6);
  }
  
  .setchart input:focus, .setchart-nickname input:focus, .statform input.inputform:focus, .textbox:focus {
    border: 1px solid #000;
    background: rgba(0, 0, 0, 1);
    color: #F3F3F3;
    box-shadow: 0px 1px 2px #252525 inset, 1px 1px 0px rgba(0, 0, 0, 0.6);
  }
  
  .setdetails, .setstats {
    border: 1px solid #000;
    background: rgba(0, 0, 0, 0.8);
    color: #F3F3F3;
  }
  
  .setdetails:hover, .setstats:hover {
    border: 1px solid #000;
    background: rgba(0, 0, 0, 1);
    color: #F3F3F3;
  }
  
  .setdetails:focus, .setstats:focus {
    border: 1px solid #000;
    background: rgba(0, 0, 0, 1);
    color: #F3F3F3;
  }
  
  .setstats .statrow-head em {
    color: #E1E1E1;
  }
  
  .teambar button, .teambar button:disabled, .teambar button:disabled:hover {
    background: rgba(0, 0, 0, 0.5);
    color: #F3F3F3;
    border: 1px solid #000;
  }
  
  .teambar button:hover {
    background: rgba(0, 0, 0, 0.7);
    color: #F3F3F3;
    border: 1px solid #000;
  }
  
  .teamchartbox {
    border-top: 1px solid #000;
  }
  
  .majorbutton, .savebutton {
    background: rgba(0, 0, 0, 0.7);
    border: 1px solid #000;
    color: #FFF;
    text-shadow: 2px 2px 1px #000;
  }
  
  .majorbutton:hover, .savebutton:hover {
    background: rgba(0, 0, 0, 0.9);
    border: 1px solid #000;
    color: #FFF;
    text-shadow: 2px 2px 1px #000;
  }
  
  .teamchart li.format-select select {
    background: rgba(0, 0, 0, 0.7);
    border: 1px solid #000;
    color: #FFF;
    text-shadow: 2px 2px 1px #000;
  }
  
  
  /*************
  * Ladder CSS *
  *************/
  
  
  .ladder th {
    background: rgba(0, 0, 0, 0.7);
  }
  
  .ladder table, .ladder td, .ladder th {
    border: 1px solid #000;
    background: rgba(0, 0, 0, 0.4);
  }
  
  
  /**************
  * Credits CSS *
  **************/
  
  
  .credits ul li p, a.subtle {
    color: #FFF;
  }
  
  a.subtle:hover {
    color: #FFF;
  }
  
  .credits li small {
    color: #C0C0C0;
  }
  
  .credits h2 {
    color: #C5253E;
  }
  
  .autofocus {
    background: rgba(10, 10, 10, 0.7);
    border: 1px solid #FFF;
    color: #FFF;
    text-shadow: 2px 2px 1px #000;
  }
  
  
  /*************
  * Battle CSS *
  *************/
  
  
  .battle-log-add, .battle-log h2  {
    background: rgba(0, 0, 0, 0.6);
    border: 1px solid #000;
  }
  
  .ps-room.ps-room-opaque {
    background: rgba(0, 0, 0, 0.3);
  }
  
  .battle-log {
    background: rgba(0, 0, 0, 0.6);
    color: #FFF;
    text-shadow: 2px 2px 1px #000;
    border: 1px solid #000;
  }
  
  .battle-log .chat em {
    color: #E1E1E1;
  }
  
  .rightbar, .leftbar {
    background: rgba(0, 0, 0, 0.7);
  }
  
  .trainer strong {
    color: #FFF;
  }
  
  .battle-log .chat strong {
    color: #2C3172;
  }
  
  .message-throttle-notice, .message-error {
    color: #BD263E;
  }
  
  .battle {
    border: 1px solid #000;
  }
  
  .battle-log .rated strong {
    background: #2C3172;
  }
  
  .ps-room .battle-controls {
    background: transparent !important;
  }

}
